import * as TGEvent from 'owp/TGEvent';

class DialogReweighing extends Component {
    constructor(props) {
        super(props);
        this.owpDialog = React.createRef();
        this.clickUpdate = this.clickUpdate.bind(this);
        this.onUpdate = this.onUpdate.bind(this);

        this.state = {};
    }

    handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
    };

    handleChange3 = (event) => {
        const { name, value } = event.target;
        this.setState({
            [name]: value,
        }, () => {
            if (this.state.USERPASSWORD === this.state.USERPASSWORD_Chk) {
                this.setState({
                    passValid: true,
                });
            } else {
                this.setState({
                    passValid: false,
                });
            }
        });
    };

    // 수정 화면 열기
    updateDialogOpen = () => {
        if (this.props.UserSEQ === null || this.props.UserSEQ === undefined || this.props.UserSEQ === '') {
            OwpMessage({
                message: '사용자를 선택해 주세요.',
                variant: 'warning',
            });
            this.updateDialogClose();
        } else {
            this.onLoad();
            this.setState({ updateDialogOpen: true });
        }
    };

    // 수정 화면 닫기
    updateDialogClose = () => {
        this.setState({ updateDialogOpen: false });
        this.owpDialog.handleClose();
        this.clearState();
    };

    // 수정 확인 화면 열기
    updateCheckOpen = () => {

        this.setState({ updateCheckOpen: true });
        this.setState({ popMessage: '저장하시겠습니까?' });
    };

    // 수정 확인 화면 닫기
    updateCheckClose = () => {
        this.setState({ updateCheckOpen: false });
    };

    clearState = () => {
        this.setState({
            USERNAME: '',
            USERID: '',
            USERPASSWORD: '',
            USERPASSWORD_Chk: '',
            DEPTID: '',
            RANKID: '',
            TEL: '',
            EMAIL: '',
            FLAG: '',
            PERMISSIONID: '',
            REMARK: '',
            INIT_ID: '',
            INIT_ID_CODENM: '',
        });
    };

    onLoad = () => {
        query({
            url: '/loadIpxUser/' + this.props.UserSEQ,
        })
        .then((res) => {
            this.setState({
                USERNAME: res['IPX_USER.USERNAME'], // 성명
                USERID: res['IPX_USER.USERID'], // 사용자 ID
                USERPASSWORD: res["IPX_USER.USERPASSWORD"], // 비밀번호
                USERPASSWORD_Chk: res["IPX_USER.USERPASSWORD"], // 비밀번호
                DEPTID: res['IPX_USER.DEPTID'], // 부서명
                RANKID: res['IPX_USER.RANKID'], // 직급
                TEL: res['IPX_USER.TEL'], // 연락처
                EMAIL: res['IPX_USER.EMAIL'], // 이메일
                FLAG: res['IPX_USER.FLAG'], // 사용여부
                PERMISSIONID: res['IPX_USER.PERMISSIONID'], // 권한그룹
                REMARK: res['IPX_USER.REMARK'], // 비고
                INIT_ID: res['IPX_USER.INIT_ID'], //초기화면ID
                INIT_ID_CODENM: res['IPX_USER.INIT_ID_CODENM'], //초기화면명
                passValid: true,
            });
            
        })
        .catch((error) => console.error('error...', error));
    };

    onUpdate = async (type) => {
        const data = {
            cudtype: 'update',
            // 'IPX_USER.WUSERSEQ': OwpSession('USERSEQ') + '', // : 로그인사용자SEQ
            'IPX_USER.WUSERSEQ': '0', // : 로그인사용자SEQ
            'IPX_USER.USERSEQ': this.props.UserSEQ + '', // USERSEQ
            'IPX_USER.USERNAME': document.getElementById('USERNAME').value, // 성명
            'IPX_USER.USERID': document.getElementById('USERID').value, // 사용자 ID
            'IPX_USER.USERPASSWORD': document.getElementById('USERPASSWORD').value, // 비밀번호
            'IPX_USER.DEPTID': this.props.CodeID, // 부서 ID
            'IPX_USER.RANKID': this.state.RANKID, // 직급
            'IPX_USER.TEL': document.getElementById('TEL').value, // 연락처
            'IPX_USER.EMAIL': document.getElementById('EMAIL').value, // 이메일
            'IPX_USER.FLAG': this.state.FLAG, // 사용여부
            'IPX_USER.PERMISSIONID': this.state.PERMISSIONID, // 권한 그룹
            'IPX_USER.REMARK': document.getElementById('REMARK').value, // 비고
        };

        const result = await mutate({
            url: '/updateIpxUser',
            data,
        })
            .then((res) => {
                const G = TGEvent.getGridByID('C801002');
                G.ReloadBody();
                this.updateDialogClose();
                this.updateCheckClose();
                this.props.userClearProps();
            })
            .catch((error) => {
                console.error(error);
                this.updateCheckClose();
            });
    };

    clickUpdate() {
        if (this.state.passValid === true) {
            this.onUpdate('START');
        } else {
            OwpMessage({
                message: '비밀번호를 확인해주세요.',
                variant: 'warning',
            });
            this.updateCheckClose();
        }
    }

    render() {
        return (
            <OwpDialog
                title="사용자 정보 수정"
                dialogButton="수정"
                callbackFunction={this.updateDialogOpen}
                dialogActions={
                    <React.Fragment>
                        <div className="w-full center">
                            <OwpButton color="secondary" variant="contained" onClick={this.updateCheckOpen}>
                                저장
                            </OwpButton>
                            <OwpButton color="primary" variant="contained" onClick={this.updateDialogClose}>
                                취소
                            </OwpButton>
                        </div>
                    </React.Fragment>
                }
                innerRef={(ref) => (this.owpDialog = ref)}
                width="auto"
                height="auto"
            >
                <React.Fragment>
                    <OwpDialog // 저장버튼을 누르면 나오는 다이얼로그.
                        onOpen={this.state.updateCheckOpen}
                        dialogActions={
                            <React.Fragment>
                                <OwpButton color="secondary" variant="contained" onClick={this.clickUpdate}>
                                    예
                                </OwpButton>
                                <OwpButton color="primary" variant="contained" onClick={this.updateCheckClose}>
                                    아니오
                                </OwpButton>
                            </React.Fragment>
                        }
                        width="211px"
                        height="100%"
                    >
                        <React.Fragment>{this.state.popMessage}</React.Fragment>
                    </OwpDialog>

                    <Formsy>
                        <table className="table table-bordered table-advance table-hover tlw7">
                            <tbody>
                            <tr>
                                <th className="left">
                                    성명
                                </th>
                                <td>
                                    <OwpTextField
                                        id="USERNAME"
                                        name="USERNAME"
                                        value={this.state.USERNAME}
                                    />
                                </td>
                                <th className="left">
                                    사용자ID
                                </th>
                                <td>
                                    <OwpTextField
                                        id="USERID"
                                        name="USERID"
                                        value={this.state.USERID}
                                        disabled
                                    />
                                </td>
                            </tr>
                            <tr>
                                <th className="left">
                                    비밀번호<span className="c04">*</span>
                                </th>
                                <td>
                                    <OwpTextField
                                        id="USERPASSWORD"
                                        name="USERPASSWORD"
                                        type="password"
                                        onChange={this.handleChange3}
                                        value={this.state.USERPASSWORD}
                                    />
                                </td>
                                <th className="left">
                                    비밀번호 확인<span className="c04">*</span>
                                </th>
                                <td>
                                    <OwpTextField
                                        id="USERPASSWORD_Chk"
                                        name="USERPASSWORD_Chk"
                                        type="password"
                                        value={this.state.USERPASSWORD_Chk}
                                        onChange={this.handleChange3}
                                    />
                                </td>
                            </tr>
                            <tr>
                                <th className="left">부서명</th>
                                <td>
                                    <OwpTextField
                                        id="DEPTID"
                                        name="DEPTID"
                                        value={this.props.CodeNM}
                                        disabled={true}
                                    />
                                </td>
                                <th className="left">
                                    직급<span className="c04">*</span>
                                </th>
                                <td>
                                    {this.state.RANKID && (
                                        <OwpSelectField
                                            className="w-full left"
                                            id="RANKID"
                                            name="RANKID"
                                            groupId={'D001000'}
                                            onChange={this.handleChange}
                                            value={this.state.RANKID}
                                            isall={false}
                                        />
                                    )}
                                </td>
                            </tr>
                            <tr>
                                <th className="left">휴대폰번호</th>
                                <td>
                                    <OwpTextField
                                        id="TEL"
                                        name="TEL"
                                        onChange={this.handleChange}
                                        value={this.state.TEL}
                                    />
                                </td>
                                <th className="left">이메일</th>
                                <td>
                                    <OwpTextField
                                        id="EMAIL"
                                        name="EMAIL"
                                        onChange={this.handleChange}
                                        value={this.state.EMAIL}

                                    />
                                </td>
                            </tr>
                            <tr>
                                <th className="left" style={{ height: '54px' }}>
                                    사용여부<span className="c04">*</span>
                                </th>
                                <td>
                                    {this.state.FLAG && (
                                        <OwpSelectField
                                            className="w-full left"
                                            id="FLAG"
                                            name="FLAG"
                                            onChange={this.handleChange}
                                            value={this.state.FLAG}
                                            options={[
                                                { label: '사용', value: 'Y' },
                                                { label: '미사용', value: 'N' },
                                            ]}
                                            useAll={false}
                                        />
                                    )}
                                </td>
                                <th className="left">
                                    권한 그룹<span className="c04">*</span>
                                </th>
                                <td>
                                    {this.state.PERMISSIONID && (
                                        <OwpSelectField
                                            className="w-full left"
                                            id="PERMISSIONID"
                                            name="PERMISSIONID"
                                            onChange={this.handleChange}
                                            value={this.state.PERMISSIONID}
                                            groupId={'D002000'}
                                            useAll={true}
                                        />
                                    )}
                                </td>
                            </tr>
                            <tr>
                                <th className="left">비고</th>
                                <td colSpan={3}>
                                    <OwpTextField
                                        style={{ width: '500px' }}
                                        id="REMARK"
                                        name="REMARK"
                                        onChange={this.handleChange}
                                        value={this.state.REMARK}
                                    />
                                </td>
                            </tr>
                            </tbody>
                        </table>
                    </Formsy>
                </React.Fragment>
            </OwpDialog>
        );
    }
}

export default DialogReweighing;
