import React, { Component } from 'react';
import { OwpButton, OwpDialog, OwpMessage, OwpSession } from 'owp/wrapper';
import { mutate, query } from 'owp/api';
import { getGridByID } from 'owp/TGEvent';
import * as TGEvent from 'owp/TGEvent';
import axios from 'axios';
import Formsy from 'formsy-react';

const TEMPLATE_FILEPATH =
    process.env.REACT_APP_REST_API_URL +
    '/downloadNFile/template/template_facility_management.xlsx';

class DialogReweighing extends Component {
    constructor(props) {
        super(props);
        this.inputFileRef = React.createRef();
        this.state = {
            stateDialog: false,
            isDone: false,
        };
    }

    componentWillMount() {}

    clickUpload = () => {
        this.onInsert('START');
    };

    handleOnChange = (event) => {
        let files = event.target.files[0];
        if (files) {
            if (this.checkFileExtension(files)) {
                this.uploadFile(files);
            } else {
                OwpMessage({
                    message: '파일을 다시 확인해주세요.',
                    variant: 'warning',
                });
            }
        }
    };

    fileUploadAction = () => {
        this.inputFileRef.current.click();
    };

    closeDialogReweighing = () => {
        this.setState({
            stateDialog: false,
            isDone: false,
            FACILITY_EXCEL: null,
        });
        this.owpDialog.handleClose();
    };

    checkFileExtension = (file) => {
        let allowExts = ['hwp', 'doc', 'xls', 'xlsx', 'ppt', 'pptx', 'pdf'];
        if (!!file) {
            let ext = file.name.substr(file.name.lastIndexOf('.')).replace('.', '');

            return allowExts.find((allowExt) => allowExt == ext) ? true : false;
        } else {
            return false;
        }
    };

    uploadFile = async (file) => {
        let formData = new FormData();
        formData.append('file', file);
        try {
            await axios({
                method: 'post',
                url: '/uploadFileRestService',
                config: {
                    headers: {
                        'Content-Type': 'multipart/form-data',
                    },
                },
                data: formData,
            })
                .then((res) => {
                    if (res.data.resultData[0]) {
                        let fileData = {
                            fileName: res.data.resultData[0].FileName,
                            fileSeq: res.data.resultData[0]['IPX_FILE.FILESEQ'],
                        };
                        console.log(res.data.resultData[0]);
                        console.log(fileData);

                        this.setState((state) => {
                            return { FACILITY_EXCEL: fileData, isDone: true };
                        });
                    }
                })
                .catch((error) => console.error(error));
        } catch (error) {
            console.error(error);
        }
    };

    onInsert = async (type) => {
        //TODO:convert data
        const data = {
            cudtype: 'create',
            'OWP_FACILITIES.FACILITY_EXCEL': this.state.FACILITY_EXCEL,
        };

        console.log(data);

        const result = await mutate({
            url: '/importOwpFacilitiesFromExcel',
            data,
        }).then((res) => {
            const G = TGEvent.getGridByID('C403000');
            G.ReloadBody();
            this.closeDialogCheck();
            this.closeDialog();
        });
    };

    render() {
        return (
            <OwpDialog
                title={'첨부파일'}
                onOpen={this.state.stateDialog}
                dialogButton="Excel 업로드"
                dialogActions={
                    <React.Fragment>
                        <OwpButton onClick={this.clickUpload} variant="contained" color="primary">
                            저장
                        </OwpButton>
                        <OwpButton
                            onClick={this.closeDialogReweighing}
                            variant="contained"
                            color="primary"
                        >
                            닫기
                        </OwpButton>
                    </React.Fragment>
                }
                innerRef={(ref) => (this.owpDialog = ref)}
                width="400px"
            >
                <React.Fragment>
                    <Formsy>
                        <div className="w-full center">
                            <div>
                                <a href={TEMPLATE_FILEPATH}>Template Excel file.</a>
                            </div>
                            {this.state.isDone && (
                                <div>
                                    <span>{this.state.FACILITY_EXCEL.fileName}</span>
                                </div>
                            )}

                            <div className="inline">
                                <input
                                    ref={this.inputFileRef}
                                    id="file"
                                    type="file"
                                    style={{ display: 'none' }}
                                    accept=".hwp,.doc,.xls,.xlsx,.ppt,.pptx"
                                    onChange={this.handleOnChange}
                                />
                                <OwpButton
                                    color="secondary"
                                    variant="contained"
                                    onClick={this.fileUploadAction}
                                >
                                    업로드
                                </OwpButton>
                            </div>
                        </div>
                    </Formsy>
                </React.Fragment>
            </OwpDialog>
        );
    }
}

export default DialogReweighing;
