import { OwpButton, OwpDialog, OwpMessage, OwpSelectField, OwpSession, OwpTextField } from 'owp/wrapper';
import React, { Component } from 'react';
import Formsy from 'formsy-react';
import { mutate, query } from 'owp/api';
import * as TGEvent from 'owp/TGEvent';
import { restoreGridSelect } from '../../GridUtill';

class DialogReweighing extends Component {
    constructor(props) {
        super(props);
        this.owpDialog = React.createRef();
        this.clickUpdate = this.clickUpdate.bind(this);
        this.onUpdate = this.onUpdate.bind(this);
        this.state = {};
    }

    handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
    };

    // 수정 화면 열기
    updateDialogOpen = () => {
        if (this.props.CODEID === '' || this.props.CODEID === null || this.props.CODEID === undefined) {
            OwpMessage({
                message: '수정할 데이터를 선택해 주세요.',
                variant: 'warning',
            });
            this.updateDialogClose();
        } else {
            this.setState({ updateDialogOpen: true });
            this.onLoad();
        }
    };

    // 수정 화면 닫기
    updateDialogClose = () => {
        this.setState({ updateDialogOpen: false });
        this.owpDialog.handleClose();
        this.claerState();
    };

    // 수정 확인 화면 열기
    updateCheckOpen = () => {

        this.setState({ updateCheckOpen: true });
        this.setState({ popMessage: '저장하시겠습니까?' });
    };

    // 수정 확인 화면 열기
    updateCheckClose = () => {
        this.setState({ updateCheckOpen: false });
    };

    claerState = () => {
        this.setState({
            CODEID: '',
            CODENM: '',
            REMARK: '',
            FLAG: '',
        });
    };

    onLoad = () => {
        query({
            url: '/loadIpxCommoncode/' + this.props.CODEID + '',
        })
            .then((res) => {
                this.setState({
                    CODEID: res['IPX_COMMONCODE.CODEID'],
                    CODENM: res['IPX_COMMONCODE.CODENM'],
                    REMARK: res['IPX_COMMONCODE.REMARK'],
                    FLAG: res['IPX_COMMONCODE.FLAG'],
                });
            })
            .catch((error) => console.error('error...', error));
    };

    onUpdate = async (type) => {
        const data = {
            cudtype: 'update',
            // 'IPX_COMMONCODE.WUSERSEQ': OwpSession('USERSEQ') + '', // : 로그인사용자SEQ
            'IPX_COMMONCODE.WUSERSEQ': '0', // : 로그인사용자SEQ
            'IPX_COMMONCODE.CODEID': this.props.CODEID,
            'IPX_COMMONCODE.CODENM': document.getElementById('CODENM').value,
            'IPX_COMMONCODE.FLAG': this.state.FLAG,
            'IPX_COMMONCODE.REMARK': document.getElementById('REMARK').value,
        };

        const result = await mutate({
            url: '/updateIpxCommoncode',
            data,
        })
            .then((res) => {
                const G = TGEvent.getGridByID('C802001');
                G.ReloadBody();
                restoreGridSelect('C802001');
                this.updateDialogClose();
                this.updateCheckClose();
                this.props.authorityGroupClearProps();
            })
            .catch((error) => {
                console.error(error);
                this.updateCheckClose();
            });
    };

    clickUpdate() {
        this.onUpdate('START');
    }

    render() {
        return (
            <OwpDialog
                title="권한 그룹 수정"
                callbackFunction={this.updateDialogOpen}
                dialogButton="수정"
                dialogActions={
                    <React.Fragment>
                        <div className="w-full center">
                            <OwpButton color="secondary" variant="contained" onClick={this.updateCheckOpen}>
                                저장
                            </OwpButton>
                            <OwpButton color="primary" variant="contained" onClick={this.updateDialogClose}>
                                취소
                            </OwpButton>
                        </div>
                    </React.Fragment>
                }
                innerRef={(ref) => (this.owpDialog = ref)}
                width="auto"
                height="auto"
            >
                <React.Fragment>
                    <OwpDialog // 저장버튼을 누르면 나오는 다이얼로그.
                        onOpen={this.state.updateCheckOpen}
                        dialogActions={
                            <React.Fragment>
                                <OwpButton color="secondary" variant="contained" onClick={this.clickUpdate}>
                                    예
                                </OwpButton>
                                <OwpButton color="primary" variant="contained" onClick={this.updateCheckClose}>
                                    아니오
                                </OwpButton>
                            </React.Fragment>
                        }
                        width="211px"
                        height="100%"
                    >
                        <React.Fragment>{this.state.popMessage}</React.Fragment>
                    </OwpDialog>

                    <Formsy>
                        <table className="table table-bordered table-advance table-hover tlw7">
                            <tbody>
                            <tr>
                                <th className="left">
                                    권한그룹<span className="c04">*</span>
                                </th>
                                <td>
                                    <OwpTextField
                                        id="CODENM"
                                        name="CODENM"
                                        value={this.state.CODENM}
                                        onChange={this.handleChange}
                                    />
                                </td>
                                <th className="left">
                                    사용여부<span className="c04">*</span>
                                </th>
                                <td>
                                    {this.state.FLAG && (
                                        <OwpSelectField
                                            className="w-160 left"
                                            id="FLAG"
                                            name="FLAG"
                                            onChange={this.handleChange}
                                            value={this.state.FLAG}
                                            options={[
                                                { label: '사용', value: 'Y' },
                                                { label: '미사용', value: 'N' },
                                            ]}
                                            useAll={false}
                                        />
                                    )}
                                </td>
                            </tr>
                            <tr>
                                <th className="left">비고</th>
                                <td colSpan={3}>
                                    <OwpTextField
                                        style={{ width: '440px' }}
                                        id="REMARK"
                                        name="REMARK"
                                        value={this.state.REMARK}
                                        onChange={this.handleChange}
                                    />
                                </td>
                            </tr>
                            </tbody>
                        </table>
                    </Formsy>
                </React.Fragment>
            </OwpDialog>
        );
    }
}

export default DialogReweighing;
