import {
    OwpTextField,
    OwpButton,
    OwpSelectField,
    OwpDialog,
    OwpMessage,
    OwpTreeGrid,
    OwpSearchDateTimePickerMulti,
    OwpDateTimePicker,
    OwpAutoComplete,
} from 'owp/wrapper';
import React, { Component } from 'react';
import Formsy from 'formsy-react';
import { mutate } from 'owp/api';
import * as TGEvent from 'owp/TGEvent';

class DialogReweighing extends Component {
    constructor(props) {
        super(props);
        this.owpDialog = React.createRef();
        this.clickCreate = this.clickCreate.bind(this);
        this.onSave = this.onSave.bind(this);
        this.state = {
            FLAG: 'Y',
        };
    }

    handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
    };

    

    // 등록 화면 닫기
    createDialogClose = () => {
        this.setState({ createDialogOpen: false });
        this.owpDialog.handleClose();
    };

    // 등록 확인 화면 열기
    createCheckOpen = () => {

        this.setState({ createCheckOpen: true });
        this.setState({ popMessage: '저장하시겠습니까?' });
    };

    // 등록 확인 화면 닫기
    createCheckClose = () => {
        this.setState({ createCheckOpen: false });
    };

    onSave = async (type) => {
        const data = {
            cudtype: 'create',
            
        };

        const result = await mutate({
            url: '/createIpxCommoncode',
            data,
        })
            
    };

    clickCreate() {
        this.onSave('START');
    }

    render() {
        return (
            <OwpDialog
                //title="교정관리 결재요청"
                title={
                    <div className="pt-0 mt-0 pb-0 mb-0 w-full flex justify-between items-center">
                        <div>
                        교정관리 결재요청
                        </div>
                        <div>

                            <OwpButton 
                                color="secondary" 
                                variant="contained"
                            >
                                결재선
                            </OwpButton>
                        </div>
                    </div>
                }
                callbackFunction={this.createDialogOpen}
                dialogButton="결재요청"
                dialogActions={
                    <React.Fragment>
                        <div className="w-full center">
                            <OwpButton color="secondary" variant="contained" onClick={this.createCheckOpen}>
                            결재요청
                            </OwpButton>
                            <OwpButton color="secondary" variant="outlined" onClick={this.createDialogClose}>
                                닫기
                            </OwpButton>
                        </div>
                    </React.Fragment>
                }
                innerRef={(ref) => (this.owpDialog = ref)}
                width="1100px"
                height="auto"
            >
                <React.Fragment>
                    <div className="row">
                        <div className="col-lg-5">
                            <div className="w-full">
                            <OwpAutoComplete
                                style={{width:'43rem'}}
                                id={'sm01'}
                                name={'sm01'}
                                placeholder="관련 부서/담당자 검색"
                                
                            />
                            </div>
                            
                            <OwpTreeGrid
                                id={'C991002'}
                                height="40vh"
                                LayoutUrl="assets/data/C991002Def.xml"
                            />
                            {/* <OwpTreeGrid
                                id="C0805002"
                                LayoutUrl="assets/data/C0202002Def.xml"
                                width="100%"
                                height="40vh"
                            /> */}
                        </div>

                        <div className="col-lg-7">
                            <div style={{width:'100%', height:'39vh', border:'1px solid #ddd'}}>
                                <table className="table table-bordered table-advance tlw5">
                                    <tbody>
                                    <tr>
                                        <th className="skback2" width="10%"></th>
                                        <th className="skback2" width="30%">이름</th>
                                        <th className="skback2" width="30%">부서</th>
                                        <th className="skback2" width="30%">상태</th>
                                    </tr>
                                    <tr>
                                        <th className="ingbg02">신청 ></th>
                                        <td className="">홍길동</td>
                                        <td className="">품질관리</td>
                                        <td className=""></td>
                                    </tr>
                                    <tr>
                                        <th className="ingbg02">승인 ></th>
                                        <td className="">홍길동</td>
                                        <td className="">품질관리</td>
                                        <td className="">예정</td>
                                    </tr>
                                    
                                    
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    </div>
                </React.Fragment>
            </OwpDialog>
        );
    }
}

export default DialogReweighing;
