import React, { Component } from "react"
import { OwpDialog, OwpButton, OwpSession, OwpSearchTextField, OwpMessage, OwpSearchSelectField } from 'owp/wrapper';
import Formsy from "formsy-react"
import { mutate } from 'owp/api';
import * as TGEvent from 'owp/TGEvent';
import translationHelper from 'main/content/utils/translationHelper';
const pageID = 'C803000';

class DialogReweighing extends Component {
  constructor(props) {
    super(props)
    this.owpDialog = React.createRef()
    this.onSave = this.onSave.bind(this)
    this.clickCreate = this.clickCreate.bind(this)
    this.state = { Flag: "", stateDialogReweighing: false }
  }

  openDialogReweighing = () => {
    if (!Boolean(this.props.GROUPID)) {
      OwpMessage({
        message: translationHelper(pageID + '.message.please_select_top_code'), //Please Select Upper Code.
        variant: "warning"
      })
      this.closeDialogReweighing()
    } else {
      this.setState({ stateDialogReweighing: true })
    }
  }

  closeDialogReweighing = () => {
    this.setState({ stateDialogReweighing: false })
    this.owpDialog.handleClose()
  }

  openDialogReweighing2 = () => {
    this.setState({ stateDialogReweighing2: true })
    this.setState({ popMessage: translationHelper(pageID + '.message.do_you_want_to_save') }) //Would you like to register?
  }

  closeDialogReweighing2 = () => {
    this.setState({ stateDialogReweighing2: false })
  }

  onSave = async type => {
    const data = {
      cudtype: "create",
      "IPX_COMMONCODE.CODEID": "자동생성",
      "IPX_COMMONCODE.GROUPID": document.getElementById("GROUPID").value, //상위코드ID
      "IPX_COMMONCODE.GROUPNM": document.getElementById("GROUPNM").value, //상위코드명
      "IPX_COMMONCODE.CODENM": document.getElementById("CODENM").value, //하위코드명
      "IPX_COMMONCODE.REMARK": document.getElementById("REMARK").value, //비고
      "IPX_COMMONCODE.FLAG": document.getElementById("FLAG").value, //사용여부
      "IPX_COMMONCODE.WUSERSEQ": OwpSession("USERSEQ"),// : 로그인 사용자SEQ
    }

    const result = await mutate({
      url: "/createIPX_CommonCodeLow",
      data
    })
      .then(res => {
        const G = TGEvent.getGridByID("C803002")
        G.ReloadBody()
        this.closeDialogReweighing()
        this.closeDialogReweighing2()
      })
      .catch(error => {
        console.error(error)
        this.closeDialogReweighing2()
      })
  }

  clickCreate() {
    this.onSave("START")
  }
  
  render() {
    return (
      <OwpDialog
        title={translationHelper(pageID + '.title.register_sub_dialog_title')} //Sub Code Registration
        dialogButton={translationHelper(pageID + '.buttons.register_button')} //Registration
        callbackFunction={this.openDialogReweighing}
        onOpen={this.state.stateDialogReweighing}
        dialogActions={
          <React.Fragment>
            <OwpButton color="secondary" variant="contained" onClick={this.openDialogReweighing2}>
            {translationHelper(pageID + '.buttons.dialog_save_button')} {/*Save*/}
            </OwpButton>
            <OwpButton color="secondary" variant="outlined" onClick={this.closeDialogReweighing}>
            {translationHelper(pageID + '.buttons.dialog_close_button')} {/*Close*/}
            </OwpButton>
          </React.Fragment>
        }
        innerRef={ref => (this.owpDialog = ref)}
        width="700px"
      >
        <React.Fragment>
          <OwpDialog // 저장버튼을 누르면 나오는 다이얼로그.
            onOpen={this.state.stateDialogReweighing2}
            dialogActions={
              <React.Fragment>
                <OwpButton color="secondary" variant="contained" onClick={this.clickCreate}>
                {translationHelper(pageID + '.buttons.dialog_ok_button')} {/*Yes*/}
                </OwpButton>
                <OwpButton
                  color="secondary"
                  variant="outlined"
                  onClick={this.closeDialogReweighing2}
                >
                  {translationHelper(pageID + '.buttons.dialog_cancel_button')} {/*No*/}
                </OwpButton>
              </React.Fragment>
            }
            width="211px"
            height="100%"
          >
            <React.Fragment>{this.state.popMessage}</React.Fragment>
          </OwpDialog>
          <div className="col-lg-12 col-xs-12 col-sm-1 pd0">
            <div className="portlet">
              <div className="portlet-body pd0">
                <Formsy>
                  <table className="table table-bordered table-advance table-hover tlw">
                    <tbody>
                      <tr>
                        <th className="left">
                        {translationHelper(pageID + '.fields.upper_code_id')}<span className="c04">*</span> {/*Upper Code ID*/}
                        </th>
                        <td>
                          <OwpSearchTextField
                            id="GROUPID"
                            name="GROUPID"
                            value={this.props.CODEID}
                            disabled={true}
                          />
                        </td>
                        <th className="left">
                        {translationHelper(pageID + '.fields.upper_code_name')}<span className="c04">*</span> {/*Upper Code Name*/}
                        </th>
                        <td>
                          <OwpSearchTextField
                            id="GROUPNM"
                            name="GROUPNM"
                            value={this.props.CODENM}
                            disabled={true}
                          />
                        </td>
                      </tr>
                      <tr>
                        <th className="left"> 
                        {translationHelper(pageID + '.fields.sub_code_id')}<span className="c04">*</span> {/*Sub Code ID*/}
                        </th>
                        <td>
                          <OwpSearchTextField
                            id="CODEID"
                            name="CODEID"
                            value={translationHelper(pageID + '.fields.automatic_generator')}
                            disabled={true}
                          />
                        </td> 
                        <th className="left">
                        {translationHelper(pageID + '.fields.sub_code_name')}<span className="c04">*</span> {/*Sub Code Name*/}
                        </th>
                        <td>
                          <OwpSearchTextField id="CODENM" name="CODENM" />
                        </td>
                      </tr>
                      <tr>
                        <th className="left">{translationHelper(pageID + '.fields.remark')}</th> {/*Remark*/}
                        <td>
                          <OwpSearchTextField id="REMARK" name="REMARK" />
                        </td>
                        <th className="left">
                        {translationHelper(pageID + '.fields.availability')}<span className="c04">*</span> {/*Availability*/}
                        </th>
                        <td>
                          <OwpSearchSelectField
                            className="w-120"
                            id="FLAG"
                            name="FLAG"
                            onChange={this.handleChange}
                            value={this.state.FLAG}
                            items={[
                              { label: "Y", value: "Y" },
                              { label: "N", value: "N" }
                            ]}
                            isall={false}
                          />
                        </td>
                      </tr>
                    </tbody>
                  </table>
                </Formsy>
              </div>
            </div>
          </div>
        </React.Fragment>
      </OwpDialog>
    )
  }
}

export default DialogReweighing
