import { OwpButton, OwpDialog, OwpSelectField, OwpTextField, OwpMessage } from 'owp/wrapper';
import React, { Component } from 'react';
import Formsy from 'formsy-react';
import { mutate } from 'owp/api';
import * as TGEvent from 'owp/TGEvent';

class DialogReweighing extends Component {
    constructor(props) {
        super(props);
        this.clickCreate = this.clickCreate.bind(this);
        this.onSave = this.onSave.bind(this);
        this.state = {};
    }

    handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
    };

    // 등록 화면 열기
    createDialogOpen = () => {
        
         if (this.props.CodeID === "" || this.props.CodeID === null || this.props.CodeID === undefined) {
             OwpMessage({
                 message: "부서를 선택해 주세요.",
                 variant: "warning",
             });
             this.createDialogClose();
         } else {
             this.setState({createDialogOpen: true});
         }
    };

    // 등록 화면 닫기
    createDialogClose = () => {
        this.setState({ createDialogOpen: false });
        this.owpDialog.handleClose();
    };

    // 등록 확인 화면 열기
    createCheckOpen = () => {

        this.setState({ createCheckOpen: true });
        this.setState({ popMessage: '저장하시겠습니까?' });
    };

    // 등록 확인 화면 닫기
    createCheckClose = () => {
        this.setState({ createCheckOpen: false });
    };

    handleChange3 = (event) => {
        const { name, value } = event.target;
        this.setState({
            [name]: value,
        }, () => {
            if (this.state.USERPASSWORD === this.state.USERPASSWORD_Chk) {
                this.setState({
                    passValid: true,
                });
            } else {
                this.setState({
                    passValid: false,
                });
            }
        });
    };

    onSave = async (type) => {
        const data = {
            cudtype: 'create',
            // 'IPX_USER.WUSERSEQ': OwpSession('USERSEQ') + '', // : 로그인사용자SEQ
            'IPX_USER.WUSERSEQ': '0', // : 로그인사용자SEQ
            'IPX_USER.USERNAME': document.getElementById('USERNAME').value, // 성명
            'IPX_USER.USERID': document.getElementById('USERID').value, // 사용자 ID
            'IPX_USER.USERPASSWORD': document.getElementById('USERPASSWORD').value, // 비밀번호
            'IPX_USER.DEPTID': this.props.CodeID, // 부서 ID
            'IPX_USER.RANKID': this.state.RANKID, // 직급
            'IPX_USER.TEL': document.getElementById('TEL').value, // 연락처
            'IPX_USER.EMAIL': document.getElementById('EMAIL').value, // 이메일
            'IPX_USER.PERMISSIONID': this.state.PERMISSIONID, // 권한 그룹
            'IPX_USER.FLAG': document.getElementById('FLAG').value === '@@@@@@' ? '' : document.getElementById('FLAG').value, // 사용여부
            'IPX_USER.REMARK': document.getElementById('REMARK').value, // 비고
        };
        const result = await mutate({
            url: '/createIpxUser',
            data,
        })
            .then((res) => {
                const G = TGEvent.getGridByID('C801002');
                G.ReloadBody();
                this.createDialogClose();
                this.createCheckClose();
            })
            .catch((error) => {
                console.error(error);
                this.createCheckClose();
            });
    };

    clickCreate() {
        if (this.state.passValid === true) {
            this.onSave('START');
        } else {
            OwpMessage({
                message: '비밀번호를 확인해주세요.',
                variant: 'warning',
            });
            this.createCheckClose();
        }
    }

    render() {
        return (
            <OwpDialog
                title="사용자 등록"
                dialogButton="사용자 등록"
                callbackFunction={this.createDialogOpen}
                dialogActions={
                    <React.Fragment>
                        <div className="w-full center">
                            <OwpButton color="secondary" variant="contained" onClick={this.createCheckOpen}>
                                저장
                            </OwpButton>
                            <OwpButton color="primary" variant="contained" onClick={this.createDialogClose}>
                                취소
                            </OwpButton>
                        </div>
                    </React.Fragment>
                }
                innerRef={(ref) => (this.owpDialog = ref)}
                width="auto"
                height="auto"
            >
                <React.Fragment>
                    <OwpDialog // 저장버튼을 누르면 나오는 다이얼로그.
                        onOpen={this.state.createCheckOpen}
                        dialogActions={
                            <React.Fragment>
                                <OwpButton color="secondary" variant="contained" onClick={this.clickCreate}>
                                    예
                                </OwpButton>
                                <OwpButton color="primary" variant="contained" onClick={this.createCheckClose}>
                                    아니오
                                </OwpButton>
                            </React.Fragment>
                        }
                        width="211px"
                        height="100%"
                    >
                        <React.Fragment>{this.state.popMessage}</React.Fragment>
                    </OwpDialog>

                    <Formsy>
                        <table className="table table-bordered table-advance table-hover tlw7">
                            <tbody>
                            <tr>
                                <th className="left">
                                    성명<span className="c04">*</span>
                                </th>
                                <td>
                                    <OwpTextField
                                        id="USERNAME"
                                        name="USERNAME"
                                    />
                                </td>
                                <th className="left">
                                    사용자ID<span className="c04">*</span>
                                </th>
                                <td>
                                    <OwpTextField
                                        id="USERID"
                                        name="USERID"
                                    />
                                </td>
                            </tr>
                            <tr>
                                <th className="left">
                                    비밀번호<span className="c04">*</span>
                                </th>
                                <td>
                                    <OwpTextField
                                        id="USERPASSWORD"
                                        name="USERPASSWORD"
                                        type="password"
                                        onChange={this.handleChange3}
                                    />
                                </td>
                                <th className="left">
                                    비밀번호 확인<span className="c04">*</span>
                                </th>
                                <td>
                                    <OwpTextField
                                        id="USERPASSWORD_Chk"
                                        name="USERPASSWORD_Chk"
                                        type="password"
                                        onChange={this.handleChange3}
                                    />
                                </td>
                            </tr>
                            <tr>
                                <th className="left">부서명</th>
                                <td>
                                    <OwpTextField
                                        id="DEPTID"
                                        name="DEPTID"
                                        value={this.props.CodeNM}
                                        disabled={true}/>
                                </td>
                                <th className="left">
                                    직급<span className="c04">*</span>
                                </th>
                                <td>
                                    <OwpSelectField
                                        className="w-full left"
                                        id="RANKID"
                                        name="RANKID"
                                        groupId={"D001000"}
                                        onChange={this.handleChange}
                                        isall={false}
                                    />
                                </td>
                            </tr>
                            <tr>
                                <th className="left">휴대폰번호</th>
                                <td>
                                    <OwpTextField
                                        id="TEL"
                                        name="TEL"
                                    />
                                </td>
                                <th className="left">이메일</th>
                                <td>
                                    <OwpTextField
                                        id="EMAIL"
                                        name="EMAIL"
                                    />
                                </td>
                            </tr>
                            <tr>
                                <th className="left" style={{ height: '54px' }}>
                                    사용여부<span className="c04">*</span>
                                </th>
                                <td>
                                    <OwpSelectField
                                        className="w-full left"
                                        id="FLAG"
                                        name="FLAG"
                                        onChange={this.handleChange}
                                        options={[
                                            { label: '사용', value: 'Y' },
                                            { label: '미사용', value: 'N' },
                                        ]}
                                        defaultValue={'Y'}
                                        useAll={false}
                                    />
                                </td>
                                <th className="left">
                                    권한 그룹<span className="c04">*</span>
                                </th>
                                <td>
                                    <OwpSelectField
                                        className="w-full left"
                                        id="PERMISSIONID"
                                        name="PERMISSIONID"
                                        onChange={this.handleChange}
                                        groupId={"D002000"}
                                        isall={false}
                                    />
                                </td>
                            </tr>
                            <tr>
                                <th className="left">비고</th>
                                <td colSpan={3}>
                                    <OwpTextField
                                        style={{width : "500px"}}
                                        id="REMARK"
                                        name="REMARK"
                                    />
                                </td>
                            </tr>
                            </tbody>
                        </table>
                    </Formsy>
                </React.Fragment>
            </OwpDialog>
        );
    }
}

export default DialogReweighing;
