import {
    OwpTextField,
    OwpButton,
    OwpSelectField,
    OwpDialog,
    OwpMessage,
    OwpSearchDateField,
    OwpSearchDateTimePickerMulti,
    OwpSearchSelectField,
    OwpSearchTextField,
} from 'owp/wrapper';
import React, { Component } from 'react';
import Formsy from 'formsy-react';
import { mutate,query } from 'owp/api';
import * as TGEvent from 'owp/TGEvent';
import { debounce, isEmpty } from "lodash";
import OwpPresetAutocompleteTextField from "owp/components/Form/OwpPresetAutocompleteTextField";
import moment from "moment"
import translationHelper from 'main/content/utils/translationHelper';
const pageID = 'C404000';
class DialogReweighing extends Component {
    constructor(props) {
        super(props);
        this.owpDialog = React.createRef();
        this.onUpdate = this.onUpdate.bind(this);
        this.onUpdateClick = this.onUpdate.bind(this);
        var td = new Date(),
        time = td.getHours() + ':' + td.getMinutes() + ':' + td.getSeconds();
        this.state = {
            FLAG: 'Y',
            currentTime: time,
        };
    }

    handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
    };

    updateDialogOpen = () => {
        if (!Boolean(this.props.FAC_HIS_SEQ)) {
            OwpMessage({
                message: translationHelper(pageID + '.message.please_select_data'),
                variant: 'warning',
            });
            this.updateDialogClose();
        } else {
            query({
                url: "loadOwpFacilitieshis/" + this.props.FAC_HIS_SEQ,
              }).then(res => {
                this.setState({
                    updateDialogOpen: true,
                    FAC_HIS_CLASSIFICATION : res['OWP_FACILITIESHIS.FAC_HIS_CLASSIFICATION'],
                    FAC_HIS_CONTENTS : res['OWP_FACILITIESHIS.FAC_HIS_CONTENTS'],
                    FAC_HIS_ID : res['OWP_FACILITIESHIS.FAC_HIS_ID'],
                    FAC_HIS_LOCATION : res['OWP_FACILITIESHIS.FAC_HIS_LOCATION'],
                    FAC_HIS_NM : res['OWP_FACILITIESHIS.FAC_HIS_NM'],
                    FAC_HIS_SEQ : res['OWP_FACILITIESHIS.FAC_HIS_SEQ'],

                    FAC_HIS_PRE_METHOD : res['OWP_FACILITIESHIS.FAC_HIS_PRE_METHOD'],
                    FAC_HIS_STATUS : res['OWP_FACILITIESHIS.FAC_HIS_STATUS'],
                    // RDATE : moment(res['OWP_FACILITIESHIS.RDATE']).format("YYYY-MM-DD"),
                    STARTTIME : moment(res['OWP_FACILITIESHIS.STARTTIME']).format("YYYY-MM-DD"),
                    FINISHTIME : moment(res['OWP_FACILITIESHIS.FINISHTIME']).format("YYYY-MM-DD"),
                    FAC_HIS_TROUBLE_PART : res['OWP_FACILITIESHIS.FAC_HIS_TROUBLE_PART'],
                    FAC_HIS_TROUBLE_TIME : res['OWP_FACILITIESHIS.FAC_HIS_TROUBLE_TIME'],
                    FAC_HIS_CAUSE : res['OWP_FACILITIESHIS.FAC_HIS_CAUSE'],
                    FAC_HIS_WORKER : res['OWP_FACILITIESHIS.FAC_HIS_WORKER']
                  });
            })
            .catch(error =>{
               console.error(error);
            })
        }
    };

    // 등록 화면 닫기
    updateDialogClose = () => {
        this.props.callback();
        this.setState({ updateDialogOpen: false });
        this.owpDialog.handleClose();
    };

    // 등록 확인 화면 열기
    updateCheckOpen = () => {

        this.setState({ updateCheckOpen: true });
        this.setState({ popMessage: '저장하시겠습니까?' });
    };

    // 등록 확인 화면 닫기
    updateCheckClose = () => {
        this.setState({ updateCheckOpen: false });
    };

    openDialogReweighing2 = () => {
        this.setState({ stateDialogReweighing2: true })
    }

    closeDialogReweighing2 = () => {
    this.setState({ stateDialogReweighing2: false })
    }


    onSearchFacilityName = (inputValue) => {
    
        query({
          
            url: "listOwpFacilityName",
            params: {
                jsondata: JSON.stringify({
                    "OWP_FACILITIES.FACILITY_NAME": inputValue,
                  }),
               
            },
          }).then(res => {
            this.setState({
                FAC_HIS_NM: inputValue,
                FAC_HIS_LOCATION: res[0]['OWP_FACILITIES.LOCATION_CD'],
                FAC_HIS_ID: res[0]['OWP_FACILITIES.FACILITY_ID'],
              });
        })
        .catch(error =>{
           console.error(error);
        })
    }
    
    onUpdate = async (type) => {
        const data = {
            'cudtype': 'update',
            'OWP_FACILITIESHIS.FAC_HIS_ID' : this.state.FAC_HIS_ID,
            'OWP_FACILITIESHIS.FAC_HIS_LOCATION' : document.getElementById("FAC_HIS_LOCATION").value,
            'OWP_FACILITIESHIS.FAC_HIS_NM' : this.state.FAC_HIS_NM,
            'OWP_FACILITIESHIS.FAC_HIS_CLASSIFICATION' : document.getElementById("FAC_HIS_CLASSIFICATION").value,
            'OWP_FACILITIESHIS.FAC_HIS_CONTENTS' : document.getElementById("FAC_HIS_CONTENTS").value,
            'OWP_FACILITIESHIS.MUSERSEQ' : "1",
            'OWP_FACILITIESHIS.FAC_HIS_SEQ' : this.props.FAC_HIS_SEQ + "",

            'OWP_FACILITIESHIS.FAC_HIS_PRE_METHOD' : document.getElementById("FAC_HIS_PRE_METHOD").value,
            'OWP_FACILITIESHIS.FAC_HIS_STATUS' : document.getElementById("FAC_HIS_STATUS").value,
            // 'OWP_FACILITIESHIS.RDATE' : document.getElementById("RDATE").value + " " + this.state.currentTime,
            'OWP_FACILITIESHIS.STARTTIME' : document.getElementById("STARTTIME").value + " " +this.state.currentTime,
            'OWP_FACILITIESHIS.FINISHTIME' : document.getElementById("FINISHTIME").value + " " + this.state.currentTime,
            'OWP_FACILITIESHIS.FAC_HIS_TROUBLE_PART' : document.getElementById("FAC_HIS_TROUBLE_PART").value,
            'OWP_FACILITIESHIS.FAC_HIS_TROUBLE_TIME' : document.getElementById("FAC_HIS_TROUBLE_TIME").value,
            'OWP_FACILITIESHIS.FAC_HIS_CAUSE' : document.getElementById("FAC_HIS_CAUSE").value,
            'OWP_FACILITIESHIS.FAC_HIS_WORKER' : document.getElementById("FAC_HIS_WORKER").value
        };

        const result = await mutate({
            url: 'updateOwpFacilitieshis',
            data,
        }).then(res => {
            const G = TGEvent.getGridByID('C404000');
            G.ReloadBody();
        }).catch(error => {
            console.error(error);
        }).finally(() => {
            this.updateDialogClose();
            this.closeDialogReweighing2();
        });
    };

    onUpdateClick() {
        this.onUpdate('START');
    }

    render() {
        const { inputValue } = this.state;
        return (
            <OwpDialog
                title={translationHelper(pageID +'.title.modify_dialog_title')}
                callbackFunction={this.updateDialogOpen}
                dialogButton={translationHelper(pageID +'.buttons.modify_button')}
                dialogActions={
                    <React.Fragment>
                        <div className="w-full center">
                            <OwpButton color="secondary" variant="contained" onClick={this.openDialogReweighing2}>
                            {translationHelper(pageID +'.buttons.dialog_save_button')}
                            </OwpButton>
                            <OwpButton color="secondary" variant="outlined" onClick={this.updateDialogClose}>
                            {translationHelper(pageID +'.buttons.dialog_close_button')}
                            </OwpButton>
                        </div>
                    </React.Fragment>
                }
                innerRef={(ref) => (this.owpDialog = ref)}
                width="1000px"
                height="auto"
            >
                <React.Fragment>

                <OwpDialog // 저장버튼을 누르면 나오는 다이얼로그.
                        onOpen={this.state.stateDialogReweighing2}
                        dialogActions={
                        <React.Fragment>
                            <OwpButton color="primary" variant="contained" onClick={this.onUpdateClick}>
                            {translationHelper(pageID +'.buttons.dialog_ok_button')}
                            </OwpButton>
                            <OwpButton
                            color="primary"
                            variant="contained"
                            onClick={this.closeDialogReweighing2}
                            >
                          {translationHelper(pageID +'.buttons.dialog_cancel_button')}
                            </OwpButton>
                        </React.Fragment>
                        }
                        width="300px"
                        height="100%"
                        >
                        <React.Fragment>{translationHelper(pageID +'.message.do_you_want_to_save')}</React.Fragment>
                    </OwpDialog>

                    <Formsy>
                        <div style={{width:'100%'}}>
                            <table className="table table-bordered table-advance tlw5">
                                <tbody>
                                <tr>
                                <th className="left" width="120">{translationHelper(pageID +'.fields.facility_name')}</th>
                                    <td className="left">
                                    
                                         <OwpPresetAutocompleteTextField
                                            className="w-160"
                                            id="FACILITY_NAME"
                                            name="FACILITY_NAME"
                                            minInputLength={2}
                                            
                                            value = {this.state.FAC_HIS_ID}
                                            onInputChange={debounce((inputValue) => this.setState({ inputValue }), 300, {
                                              maxWait: 1000,
                                            })}
                                            query={
                                              !isEmpty(this.state.inputValue) && {
                                                url: "/listOwpFacilityName",
                                                params: {
                                                  "OWP_FACILITIES.FACILITY_NAME": inputValue,
                                                },
                                                item: {
                                                    labelKey: "OWP_FACILITIES.FACILITY_NAME",
                                                    valueKey: "OWP_FACILITIES.FACILITY_NAME"
                                                }
                                              }
                                            }
                                            isMulti={false}
                                            onChange={(value) => {
                                                
                                              this.onSearchFacilityName(value["value"]);
                                            }}
                                            placeholder = {this.state.FAC_HIS_NM}
                                           
                                        
                                    />
                                    </td>
                                    <th className="left" width="120">{translationHelper(pageID +'.fields.facility_no')}</th>
                                    <td className="left">
                                        <OwpTextField
                                            style={{width:'100%'}}
                                            name="FAC_HIS_ID"
                                            id ="FAC_HIS_ID"
                                            value = {this.state.FAC_HIS_ID}
                                            disabled={true}
                                        />
                                    </td>
                                    <th className="left" width="120">{translationHelper(pageID +'.fields.location')}</th>
                                    <td className="left">
                                        <OwpTextField
                                            style={{width:'100%'}}
                                            name="FAC_HIS_LOCATION"
                                            id ="FAC_HIS_LOCATION"
                                            value ={this.state.FAC_HIS_LOCATION}
                                            disabled={true}
                                        />
                                    </td>
                                </tr>
                                <tr>
                                    
                                <th className="left" width="120">{translationHelper(pageID +'.fields.history_classification')}</th>
                                    <td className="left">
                                        <OwpSelectField
                                            className="w-full"
                                            id="FAC_HIS_CLASSIFICATION"
                                            name="FAC_HIS_CLASSIFICATION"
                                            groupId ={"D802000"}
                                             value = {this.state.FAC_HIS_CLASSIFICATION}
                                        />
                                    </td> 
                                    <th className="left" width="120">{translationHelper(pageID +'.fields.preservation_method')}</th>
                                    <td className="left">
                                        <OwpTextField
                                            style={{width:'100%'}}
                                            name="FAC_HIS_PRE_METHOD"
                                            id ="FAC_HIS_PRE_METHOD"
                                            value ={this.state.FAC_HIS_PRE_METHOD}
                                           
                                        />
                                    </td>

                                    <th className="left" width="120">{translationHelper(pageID +'.fields.status')}</th>
                                    <td className="left">
                                        <OwpTextField
                                            style={{width:'100%'}}
                                            name="FAC_HIS_STATUS"
                                            id ="FAC_HIS_STATUS"
                                            value ={this.state.FAC_HIS_STATUS}
                                        />
                                    </td>         
                                    
                                </tr>

                                <tr>
                                    
                                    {/* <th className="left" width="120">등록일</th>
                                    <td className="left">
                                        <OwpSearchDateField
                                            style={{width:'100%'}}
                                            name="RDATE"
                                            id ="RDATE"
                                            value ={this.state.RDATE}
                                        />
                                    </td>    */}
                                     
                                     <th className="left" width="120">{translationHelper(pageID +'.fields.start_time')}</th>
                                    <td className="left">
                                        <OwpSearchDateField
                                            style={{width:'100%'}}
                                            name="STARTTIME"
                                            id ="STARTTIME"
                                            value ={this.state.STARTTIME}
                                        />
                                    </td>

                                    <th className="left" width="120">{translationHelper(pageID +'.fields.finish_time')}</th>
                                    <td className="left">
                                        <OwpSearchDateField
                                            style={{width:'100%'}}
                                            name="FINISHTIME"
                                            id ="FINISHTIME"
                                            value ={this.state.FINISHTIME}
                                        />
                                    </td>         
                                    <th className="left" width="120">{translationHelper(pageID +'.fields.cause')}</th>
                                    <td className="left">
                                        <OwpTextField
                                            style={{width:'100%'}}
                                            name="FAC_HIS_CAUSE"
                                            id ="FAC_HIS_CAUSE"
                                            value = {this.state.FAC_HIS_CAUSE}
                                         
                                        />
                                    </td>   
                                </tr>

                                <tr>
                                    
                                <th className="left" width="120">{translationHelper(pageID +'.fields.trouble_parts')}</th>
                                    <td className="left">
                                        <OwpTextField
                                            style={{width:'100%'}}
                                            name="FAC_HIS_TROUBLE_PART"
                                            id ="FAC_HIS_TROUBLE_PART"
                                            value ={this.state.FAC_HIS_TROUBLE_PART}
                                        />
                                    </td>   
                                     
                                    <th className="left" width="120">{translationHelper(pageID +'.fields.trouble_time')}</th>
                                    <td className="left">
                                        <OwpTextField
                                            style={{width:'100%'}}
                                            name="FAC_HIS_TROUBLE_TIME"
                                            id ="FAC_HIS_TROUBLE_TIME"
                                            value ={this.state.FAC_HIS_TROUBLE_TIME}
                                        />
                                    </td>

                                    <th className="left" width="120">{translationHelper(pageID +'.fields.worker')}</th>
                                    <td className="left">
                                        <OwpTextField
                                            style={{width:'100%'}}
                                            name="FAC_HIS_WORKER"
                                            id ="FAC_HIS_WORKER"
                                            value ={this.state.FAC_HIS_WORKER}
                                        />
                                    </td>         
                                    
                                </tr>

                                <tr>
                                   
                                <th className="left">{translationHelper(pageID +'.fields.history_content')}</th>
                                    <td className="left" colspan="5">
                                           <OwpTextField
                                            style={{width:'100%', maxHeight: '200px',}}
                                            name="FAC_HIS_CONTENTS"
                                            id = "FAC_HIS_CONTENTS"
                                            value = {this.state.FAC_HIS_CONTENTS}
                                        />
                                    </td>
                                </tr>
                                </tbody>
                            </table>
                        </div>
                    </Formsy>
                </React.Fragment>
            </OwpDialog>
        );
    }
}

export default DialogReweighing;
