import { FuseAnimate, FuseSearch, FuseShortcuts } from "owp/@fuse";
import {
    Avatar,
    Button,
    Hidden,
    Icon,
    IconButton,
    ListItemIcon,
    ListItemText,
    MenuItem,
    Popover,
    Typography,
} from '@material-ui/core';
import { withStyles } from '@material-ui/core/styles/index';
import { Breadcrumb, LoadingIndicator } from "owp/components";
import * as authActions from "owp/auth/store/actions";
import classNames from 'classnames';
import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import { bindActionCreators } from 'redux';
import {OwpDialog, OwpMessage} from "owp/wrapper";
import LoginPWChangeForm from "./content/login/LoginPWChangeForm";
import moment from 'moment';
//import OwpSession from "../@wrapper/OwpSession";
//import C902003 from "./content/pages/C902003/C902003";

const styles = theme => ({
    root: {
        display: 'flex',
        alignItems: 'center',
        width: '100%',
    },
    separator: {
        width: 1,
        height: 34,
        backgroundColor: theme.palette.divider,
    },
});

const loadingIndicatorStyle = {
    position: 'absolute',
    bottom: 0,
    width: '100%',
};

class MainToolbar extends Component {
    state = {
        userMenu: null,
        stateDialog:false,
        title:""
    };

    userMenuClick = event => {
        this.setState({ userMenu: event.currentTarget });
    };

    userMenuClose = () => {
        this.setState({ userMenu: null });
    };

    closeDialog = () => {
        this.setState({stateDialog: false});
        this.owpDialog.handleClose();
        localStorage.setItem('pwchange','ok');
    };

    componentDidMount() {

        if(localStorage.getItem('pwchange')!=="ok"){
            let session = window.localStorage.getItem('session');
            let username = "";

            if(session){
                session = JSON.parse(window.localStorage.getItem('session'));
                username = session.USERNAME
            }
            this.setState({USERNAME : username});
        }
    }

    render() {
        const {
            classes,
            // toggleQuickPanel,
            user,
            logout,
            openChatPanel,
            breadcrumb,
        } = this.props;
        const { userMenu } = this.state;

        return (
            <React.Fragment>
                <div className={classNames(classes.root, 'flex flex-row skback')}>
                    <div
                        className="flex flex-1 text-2xl"
                        style={{ alignItems: 'center' }}
                    >
                        <Icon style={{ margin: '0px 5px 0 22px' ,  fontSize: '26px'}}>home</Icon>
                        <Breadcrumb items={breadcrumb} />
                    </div>

                    <div className="flex">

                        <div className={classes.separator} />

                        <FuseAnimate delay={300}>
                            <Button
                                className="h-32"
                                onClick={this.userMenuClick}
                            >
                                {/*{user.data.photoURL ? (*/}
                                {/*    <Avatar*/}
                                {/*        className=""*/}
                                {/*        alt="user photo"*/}
                                {/*        src={user.data.photoURL}*/}
                                {/*    />*/}
                                {/*) : (*/}
                                {/*    <Avatar className="">*/}
                                {/*        {user.data.displayName[0]}*/}
                                {/*    </Avatar>*/}
                                {/*)}*/}

                                <div className="hidden md:flex flex-col ml-12 items-start">
                                    <Typography
                                        component="span"
                                        className="normal-case font-600 flex"
                                    >
                                        {user.data.displayName}
                                    </Typography>
                                    <Typography
                                        className="text-11 capitalize"
                                        color="textSecondary"
                                    >
                                        {this.state.USERNAME}
                                    </Typography>
                                </div>

                                <Icon
                                    className="text-16 ml-12 hidden sm:flex"
                                    variant="action"
                                >
                                    keyboard_arrow_down
                                </Icon>
                            </Button>
                        </FuseAnimate>

                        <Popover
                            open={Boolean(userMenu)}
                            anchorEl={userMenu}
                            onClose={this.userMenuClose}
                            anchorOrigin={{
                                vertical: 'bottom',
                                horizontal: 'center',
                            }}
                            transformOrigin={{
                                vertical: 'top',
                                horizontal: 'center',
                            }}
                            classes={{
                                paper: 'py-8',
                            }}
                        >
                            {user.role === 'guest' ? (
                                <React.Fragment>
                                    <MenuItem component={Link} to="/login">
                                        <ListItemIcon>
                                            <Icon>lock</Icon>
                                        </ListItemIcon>
                                        <ListItemText
                                            className="pl-0"
                                            primary="Login"
                                        />
                                    </MenuItem>
                                    {/*<MenuItem component={Link} to="/register">
                                        <ListItemIcon>
                                            <Icon>person_add</Icon>
                                        </ListItemIcon>
                                        <ListItemText
                                            className="pl-0"
                                            primary="Register"
                                        />
                                    </MenuItem>*/}
                                </React.Fragment>
                            ) : (
                                <React.Fragment>
                                    {/*<MenuItem*/}
                                    {/*    component={Link}*/}
                                    {/*    to="/pages/profile"*/}
                                    {/*    onClick={this.userMenuClose}*/}
                                    {/*>*/}
                                    {/*    <ListItemIcon>*/}
                                    {/*        <Icon>account_circle</Icon>*/}
                                    {/*    </ListItemIcon>*/}
                                    {/*    <ListItemText*/}
                                    {/*        className="pl-0"*/}
                                    {/*        primary="My Profile"*/}
                                    {/*    />*/}
                                    {/*</MenuItem>*/}
                                    {/*<MenuItem*/}
                                    {/*    component={Link}*/}
                                    {/*    to="/apps/mail"*/}
                                    {/*    onClick={this.userMenuClose}*/}
                                    {/*>*/}
                                    {/*    <ListItemIcon>*/}
                                    {/*        <Icon>mail</Icon>*/}
                                    {/*    </ListItemIcon>*/}
                                    {/*    <ListItemText*/}
                                    {/*        className="pl-0"*/}
                                    {/*        primary="Inbox"*/}
                                    {/*    />*/}
                                    {/*</MenuItem>*/}
                                    <MenuItem
                                        onClick={() => {
                                            logout();
                                            this.userMenuClose();
                                        }}
                                    >
                                        <ListItemIcon>
                                            <Icon>exit_to_app</Icon>
                                        </ListItemIcon>
                                        <ListItemText
                                            className="pl-0"
                                            primary="로그아웃"
                                        />
                                    </MenuItem>
                                    {/*<MenuItem*/}
                                    {/*    component={Link}*/}
                                    {/*    to="/pages/C902003"*/}
                                    {/*    onClick={this.userMenuClose}*/}
                                    {/*>*/}
                                    {/*    <ListItemIcon>*/}
                                    {/*        <Icon>settings</Icon>*/}
                                    {/*    </ListItemIcon>*/}
                                    {/*    <ListItemText*/}
                                    {/*        className="pl-0"*/}
                                    {/*        primary="개인정보 수정"*/}
                                    {/*    />*/}
                                    {/*</MenuItem>*/}
                                    {/*<MenuItem
                                        component={Link}
                                        to="/examples/owpgrid"
                                        onClick={this.userMenuClose}
                                    >
                                        <ListItemIcon>
                                            <Icon>school</Icon>
                                        </ListItemIcon>
                                        <ListItemText
                                            className="pl-0"
                                            primary="그리드"
                                        />
                                    </MenuItem>
                                    <MenuItem
                                        component={Link}
                                        to="/examples/owptabs"
                                        onClick={this.userMenuClose}
                                    >
                                        <ListItemIcon>
                                            <Icon>school</Icon>
                                        </ListItemIcon>
                                        <ListItemText
                                            className="pl-0"
                                            primary="탭"
                                        />
                                    </MenuItem>
                                    <MenuItem
                                        component={Link}
                                        to="/examples/owpmessage"
                                        onClick={this.userMenuClose}
                                    >
                                        <ListItemIcon>
                                            <Icon>school</Icon>
                                        </ListItemIcon>
                                        <ListItemText
                                            className="pl-0"
                                            primary="메세지"
                                        />
                                    </MenuItem>
                                    <MenuItem
                                        component={Link}
                                        to="/examples/owpdrawer"
                                        onClick={this.userMenuClose}
                                    >
                                        <ListItemIcon>
                                            <Icon>school</Icon>
                                        </ListItemIcon>
                                        <ListItemText
                                            className="pl-0"
                                            primary="상세보기"
                                        />
                                    </MenuItem>*/}
                                </React.Fragment>
                            )}
                        </Popover>

                        <div className={classes.separator} />

                        {/*<FuseSearch />*/}

                        <div className={classes.separator} />

                        <FuseShortcuts />

                        <Hidden lgUp>
                            <div className={classes.separator} />

                            <IconButton
                                className="w-64 h-32"
                                onClick={openChatPanel}
                            >
                                <Icon>chat</Icon>
                            </IconButton>
                        </Hidden>

                        <div className={classes.separator} />
                        {/* <IconButton
            className="w-64 h-64"
            onClick={() => toggleQuickPanel(true)}
          >
            <Icon>format_list_bulleted</Icon>
          </IconButton> */}
                    </div>
                </div>
                <LoadingIndicator
                    style={loadingIndicatorStyle}
                    {...this.props.loadingIndicatorProps}
                />

                <OwpDialog
                    title={this.state.title}
                    onOpen={this.state.stateDialog}
                    innerRef={ ref=> this.owpDialog = ref}
                    width="400px"
                    height="300px"
                >
                    <React.Fragment>{/**/}
                        <LoginPWChangeForm closeFunc={this.closeDialog} />
                    </React.Fragment>
                </OwpDialog>

            </React.Fragment>
        );
    }
}

function mapDispatchToProps(dispatch) {
    return bindActionCreators(
        {
            // toggleQuickPanel: quickPanelActions.toggleQuickPanel,
            logout: authActions.logoutUser,
        },
        dispatch
    );
}

function mapStateToProps({ auth, owp }) {
    if((window.location.pathname.indexOf('C103000') > -1 || window.location.pathname.indexOf('C104000') > -1 || window.location.pathname.indexOf('C105000') > -1) && owp.network.status === 'START') {
        owp.network.status = '';
    }
    return {
        user: auth.user,
        breadcrumb: owp.page.breadcrumb,
        loadingIndicatorProps: {
            loading:
                owp.network.status === 'START' ||
                owp.network.status === 'LOADING',
            variant:
                owp.network.status === 'LOADING' ? 'determinate' : undefined,
            value: owp.network.value,
        },
    };
}

export default withStyles(styles, { withTheme: true })(
    connect(
        mapStateToProps,
        mapDispatchToProps
    )(MainToolbar)
);
