import React, { Component } from "react";
import { OwpButton, OwpSearchDateField, OwpSearchSelectField, OwpSearchTextField, OwpTextField } from 'owp/wrapper';
import Formsy from "formsy-react";
import * as TGEvent from 'owp/TGEvent';
import moment from "moment";
import { query } from 'owp/api';
import Tabs from "@material-ui/core/Tabs"
import Tab from "@material-ui/core/Tab"
import { Typography, withStyles } from "@material-ui/core"
import C0202002_tab05_tab01 from './C0202002_tab05_tab01';
import C0202002_tab05_tab02 from './C0202002_tab05_tab02';

function TabContainer(props) {
    return (
        <Typography component="div" style={{ padding: "0" }}>
            {props.children}
        </Typography>
    )
}
const styles = theme => ({
    tabsRoot: {
        borderBottom: "1px solid #e8e8e8"
    },
    tabsIndicator: {
        backgroundColor: "#1890ff"
    },
    tabRoot: {
        textTransform: "initial",
        fontSize: 14,
        minWidth: 72,
        fontWeight: theme.typography.fontWeightRegular,
        marginRight: theme.spacing.unit * 4,
        fontFamily: [
            "NotoSans",
            "-apple-system",
            "BlinkMacSystemFont",
            '"Segoe UI"',
            "Roboto",
            '"Helvetica Neue"',
            "Arial",
            "sans-serif",
            '"Apple Color Emoji"',
            '"Segoe UI Emoji"',
            '"Segoe UI Symbol"'
        ].join(","),
        "&:hover": {
            color: "#40a9ff",
            opacity: 1
        },
        "&$tabSelected": {
            borderBottom: "3px solid #e8e8e8",
            color: "#1890ff",
            fontWeight: theme.typography.fontWeightMedium
        },
        "&:focus": {
            color: "#40a9ff"
        }
    },
    tabSelected: {}
})
class Page extends Component {
    constructor(props) {
        super(props);
        this.state = {
            tabValue: 0
        };
    }
    /**
     * Owp에서 사용하는 인풋에 대해 모두 사용할 수 있는 change 이벤트 리시버
     * @param {Event} event 발생한 이벤트 객체
     * @param {Function} callback 콜백 함수. 인풋의 name, value를 인수로 전달함.
     */
    componentDidMount() {
        const _showApprovalDialog = this.showApprovalDialog;
    }

    handleChangeTabs = (event, tabValue) => {
        //탭을 변환할때마다 조회하기 위한 변수를 담아줌
        this.setState({ tabValue })
    }
    //날짜 디폴드값 설정 함수

    render() {
        const { classes } = this.props
        const { tabValue } = this.state
        return (
            <React.Fragment>
            <div className="row mg0 pd0">
                    {/*TAB*/}
                    <Tabs
                        style={{ border: "0px solid" }}
                        value={tabValue}
                        onChange={this.handleChangeTabs}
                        classes={{ root: classes.tabsRoot, indicator: classes.tabsIndicator }}
                    >
                        <Tab
                            disableRipple
                            classes={{ root: classes.tabRoot, selected: classes.tabSelected }}
                            label="투입원료물질"
                        />
                        <Tab
                            disableRipple
                            classes={{ root: classes.tabRoot, selected: classes.tabSelected }}
                            label="출입고 관리"
                        />
                    </Tabs>
                    {tabValue === 0 && (
                        <TabContainer>
                            {/*일단위 등록 현황*/}
                            <C0202002_tab05_tab01
                                MBRSEQ = {this.props.MBRSEQ}
                                TYPE = {this.props.TYPE}
                            />
                        </TabContainer>
                    )}
                    {tabValue === 1 && (
                        <TabContainer>
                            {/*주단위 등록 현황*/}
                            <C0202002_tab05_tab02 />
                        </TabContainer>
                    )}
                </div>
            </React.Fragment>
        );
    }
}

export default withStyles(styles, { withTheme: true })(Page);
