import _ from 'lodash';
import PropTypes from 'prop-types';
import React from 'react';
import 'velocity-animate/velocity.ui';
import { VelocityTransitionGroup } from 'velocity-react';

const enterAnimationDefaults = {
    animation: 'transition.fadeIn',
    stagger: 50,
    duration: 200,
    display: null,
    visibility: 'visible',
    delay: 0,
};

const leaveAnimationDefaults = {
    stagger: 50,
    duration: 200,
    display: null,
    visibility: 'visible',
    delay: 0,
};

const FuseAnimateGroup = (props) => {
    const newProps = _.merge(
        {},
        {
            enter: enterAnimationDefaults,
            leave: leaveAnimationDefaults,
        },
        props
    );

    return <VelocityTransitionGroup {...newProps} children={props.children} />;
};

FuseAnimateGroup.propTypes = {
    children: PropTypes.any,
};

FuseAnimateGroup.defaultProps = {
    enter: enterAnimationDefaults,
    leave: leaveAnimationDefaults,
    easing: [0.4, 0.0, 0.2, 1],
    runOnMount: true,
    enterHideStyle: {
        visibility: 'visible',
    },
    enterShowStyle: {
        visibility: 'hidden',
    },
};

export default FuseAnimateGroup;
