import {
    Badge,
    Checkbox,
    Chip,
    Collapse,
    FormControlLabel,
    List,
    ListItem,
    ListItemText,
    Popover,
    SvgIcon,
    Typography,
} from '@material-ui/core';
import { withStyles } from '@material-ui/core/styles';
import classNames from 'classnames';
import every from 'lodash/every';
import get from 'lodash/get';
import isEmpty from 'lodash/isEmpty';
import noop from 'lodash/noop';
import orderBy from 'lodash/orderBy';
import React, { Fragment, useMemo, useState } from 'react';

const styles = theme => ({
    mt8: { marginTop: 8 },
    width120: { width: 120 },
    nestedListItem: {
        borderLeft: `1px dashed ${theme.palette.text.hint}`,
    },
    mlLv1: { marginLeft: theme.spacing.unit * 3 },
    mlLv2: { marginLeft: theme.spacing.unit * 6 },
    mlLv3: { marginLeft: theme.spacing.unit * 9 },
    badge: {
        top: '10%',
        right: 6,
        fontSize: '16px',
    },
    popover: {
        pointerEvents: 'none',
    },
    chip: {
        margin: theme.spacing.unit / 2,
    },
});

function MinusSquare({ fontSize = 'default', color = 'action', ...restProps }) {
    return (
        <SvgIcon fontSize={fontSize} color={color} {...restProps}>
            {/* tslint:disable-next-line: max-line-length */}
            <path d="M22.047 22.074v0 0-20.147 0h-20.12v0 20.147 0h20.12zM22.047 24h-20.12q-.803 0-1.365-.562t-.562-1.365v-20.147q0-.776.562-1.351t1.365-.575h20.147q.776 0 1.351.575t.575 1.351v20.147q0 .803-.575 1.365t-1.378.562v0zM17.873 11.023h-11.826q-.375 0-.669.281t-.294.682v0q0 .401.294 .682t.669.281h11.826q.375 0 .669-.281t.294-.682v0q0-.401-.294-.682t-.669-.281z" />
        </SvgIcon>
    );
}

function PlusSquare({ fontSize = 'default', color = 'action', ...restProps }) {
    return (
        <SvgIcon fontSize={fontSize} color={color} {...restProps}>
            {/* tslint:disable-next-line: max-line-length */}
            <path d="M22.047 22.074v0 0-20.147 0h-20.12v0 20.147 0h20.12zM22.047 24h-20.12q-.803 0-1.365-.562t-.562-1.365v-20.147q0-.776.562-1.351t1.365-.575h20.147q.776 0 1.351.575t.575 1.351v20.147q0 .803-.575 1.365t-1.378.562v0zM17.873 12.977h-4.923v4.896q0 .401-.281.682t-.682.281v0q-.375 0-.669-.281t-.294-.682v-4.896h-4.923q-.401 0-.682-.294t-.281-.669v0q0-.401.281-.682t.682-.281h4.923v-4.896q0-.401.294-.682t.669-.281v0q.401 0 .682.281t.281.682v4.896h4.923q.401 0 .682.281t.281.682v0q0 .375-.281.669t-.682.294z" />
        </SvgIcon>
    );
}

const EquipAttrButtonItems = ({ classes, data = [], handleChangeVisibleState, MBRSEQ }) => {
    const [openItems, setOpenItems] = useState({});
    const [openItemsLevel1, setOpenItemsLevel1] = useState({});
    const [openItemsLevel2, setOpenItemsLevel2] = useState({});
    const buttonData = useMemo(
        () =>
            data.map((d = {}, index) => {
                let items = get(d, 'items', []).map((items = {}, indexlv1) => {
                    if (!isEmpty(items)) {
                        let itemlv1 = get(items, 'items', []).map((itemlv1 = {}, indexlv2) => {
                            if (!isEmpty(itemlv1)) {
                                setOpenItemsLevel2({ [index + '_' + indexlv1 + '_' + indexlv2]: true });
                                return ({
                                    gname: itemlv1,
                                    vid: `${get(d, 'gname')}_${get(items, 'gname')}_${itemlv1}`
                                })
                            }
                        });
                        setOpenItemsLevel1({ [index + '_' + indexlv1]: true });
                        return ({
                            gname: items.gname,
                            vid: `${get(d, 'gname')}_${get(items, 'gname')}`,
                            itemlv1
                        });
                    }
                });
                setOpenItems({ [index]: true });
                return (
                    {
                        gname: d.gname,
                        vid: `${get(d, 'gname')}`,
                        items
                    }
                )
            }),
        [data]
    );
    const handleClickMenu = (level, index, key) => () => {
        switch (level) {

            case 0:
                setOpenItems({ [index]: !openItems[index] });
                break;
            case 1:
                setOpenItemsLevel1({ [index]: !openItemsLevel1[index] });
                break;
            case 2:
                setOpenItemsLevel2({ [index]: !openItemsLevel2[index] });
                break;

        }
        handleEventClickMenu(key);
    };

    const handleEventClickMenu = (key) => {
        handleChangeVisibleState(key);
    }
    return (
        <List component="nav">
            {buttonData.map(({ gname, items = [] } = {}, rootIndex) => {
                const IconComponent = openItems[rootIndex] ? MinusSquare : PlusSquare;
                let key = gname;
                return (
                    <Fragment key={`step-${key}`}>
                        <div className="flex item-center">
                            <ListItem button onClick={handleClickMenu(0, rootIndex, { "OWP_MBR_UNIT_PROCESS.MBRSEQ": MBRSEQ })}>
                                <IconComponent className="mr-16" />
                                <ListItemText primary={<Typography>{gname}</Typography>} />
                            </ListItem>
                        </div>
                        <Collapse in={openItems[rootIndex]} timeout="auto" unmountOnExit>
                            <List component="div" disablePadding>
                                {items.map(({ gname, itemlv1 = [] } = {}, subIndex) => {
                                    const IconComponentItem = isEmpty(itemlv1) ? 'span' : openItemsLevel1[rootIndex + '_' + subIndex] ? MinusSquare : PlusSquare;
                                    let keylv1 = key + '-' + gname;
                                    let mainProcess = gname;
                                    return (
                                        <Fragment key={`step-${keylv1}`}>
                                            <div className="flex item-center">
                                                <ListItem
                                                    button
                                                    className={classNames(classes.nestedListItem, classes.mlLv1)}
                                                    onClick={handleClickMenu(1, rootIndex + '_' + subIndex, { "OWP_MBR_UNIT_PROCESS.MBRSEQ": MBRSEQ, "OWP_MBR_UNIT_PROCESS.MAINPROCESS": mainProcess })}
                                                >
                                                    <Typography className="-ml-16" color="textSecondary">---</Typography>
                                                    <IconComponentItem className="mr-16" />
                                                    <ListItemText
                                                        inset
                                                        className="-ml-16"
                                                        primary={<Typography>{gname}</Typography>}
                                                    />
                                                </ListItem>
                                            </div>
                                            <Collapse in={openItemsLevel1[rootIndex + '_' + subIndex]} timeout="auto" unmountOnExit>
                                                <List component="div" disablePadding>
                                                    {itemlv1.map(({ gname } = {}, subIndexLevel2) => {
                                                        let keylv2 = keylv1 + '-' + gname;
                                                        let subProcess = gname;
                                                        return (
                                                            <Fragment key={`step-${keylv2}`}>
                                                                <div className="flex item-center">
                                                                    <ListItem
                                                                        button
                                                                        className={classNames(classes.nestedListItem, classes.mlLv2)}
                                                                        onClick={handleClickMenu(2, rootIndex + '_' + subIndex + '_' + subIndexLevel2, { "OWP_MBR_UNIT_PROCESS.MBRSEQ": MBRSEQ, "OWP_MBR_UNIT_PROCESS.MAINPROCESS": mainProcess, "OWP_MBR_UNIT_PROCESS.SUBPROCESS": subProcess })}
                                                                    >
                                                                        <Typography className="-ml-16" color="textSecondary">---<span>&nbsp;&nbsp;</span></Typography>
                                                                        <ListItemText
                                                                            inset
                                                                            className="-ml-16"
                                                                            primary={<Typography>{gname}</Typography>}
                                                                        />
                                                                    </ListItem>
                                                                </div>
                                                            </Fragment>
                                                        );
                                                    })}
                                                </List>
                                            </Collapse>
                                        </Fragment>
                                    );
                                })}
                            </List>
                        </Collapse>
                    </Fragment>
                );
            })}
        </List>
    );
};

export default withStyles(styles)(EquipAttrButtonItems);
