import {
    OwpTextField,
    OwpButton,
    OwpSelectField,
    OwpDialog,
    OwpMessage,
    OwpTreeGrid,
    OwpSearchDateTimePickerMulti,
    OwpDateTimePicker,
} from 'owp/wrapper';
import React, { Component } from 'react';
import Formsy from 'formsy-react';
import { mutate } from 'owp/api';
import * as TGEvent from 'owp/TGEvent';

class DialogReweighing extends Component {
    constructor(props) {
        super(props);
        this.owpDialog = React.createRef();
        this.clickCreate = this.clickCreate.bind(this);
        this.onSave = this.onSave.bind(this);
        this.state = {
            FLAG: 'Y',
        };
    }

    handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
    };

    

    // 등록 화면 닫기
    createDialogClose = () => {
        this.setState({ createDialogOpen: false });
        this.owpDialog.handleClose();
    };

    // 등록 확인 화면 열기
    createCheckOpen = () => {

        this.setState({ createCheckOpen: true });
        this.setState({ popMessage: '저장하시겠습니까?' });
    };

    // 등록 확인 화면 닫기
    createCheckClose = () => {
        this.setState({ createCheckOpen: false });
    };

    onSave = async (type) => {
        const data = {
            cudtype: 'create',
            
        };

        const result = await mutate({
            url: '/createIpxCommoncode',
            data,
        })
            
    };

    clickCreate() {
        this.onSave('START');
    }

    render() {
        return (
            <OwpDialog
                // title="수불 대장"
                title={
                    <div className="pt-0 mt-0 pb-0 mb-0 w-full flex justify-between items-center">
                        <div>
                        MBR 작성
                        </div>
                        <div style={{right: '150px', position:'absolute'}}>
                            
                            
                        </div>
                        <div>

                            <OwpButton 
                                color="secondary" 
                                variant="contained"
                            >
                                EXCEL 업로드
                            </OwpButton>
                        </div>
                    </div>
                }

                callbackFunction={this.createDialogOpen}
                dialogButton="신규작성"
                dialogActions={
                    <React.Fragment>
                        <div className="w-full center">
                            <OwpButton color="secondary" variant="contained" onClick={this.createCheckOpen}>
                                임시저장
                            </OwpButton>
                            <OwpButton color="secondary" variant="contained" onClick={this.createCheckOpen}>
                                작성완료
                            </OwpButton>
                            <OwpButton color="secondary" variant="outlined" onClick={this.createDialogClose}>
                                취소
                            </OwpButton>
                        </div>
                    </React.Fragment>
                }
                innerRef={(ref) => (this.owpDialog = ref)}
                width="1100px"
                height="auto"
            >
                <React.Fragment>
                    <div style={{width:'100%'}}>
                        <OwpTreeGrid
                            id="C0202002"
                            LayoutUrl="assets/data/C0202002Def.xml"
                            width="100%"
                            height="40vh"
                        />
                    </div>
                    <div style={{textAlign:'right'}}>
                            <OwpButton color={'secondary'} variant={'contained'}>
                                행 추가
                            </OwpButton>
                            <OwpButton color={'secondary'} variant={'contained'}>
                                삭제
                            </OwpButton>
                        </div>
                </React.Fragment>
            </OwpDialog>
        );
    }
}

export default DialogReweighing;
