import { OwpDialog, OwpButton, OwpMessage} from 'owp/wrapper';
import React, { Component } from "react"
import { mutate } from "owp/api";
import * as TGEvent from 'owp/TGEvent';
import translationHelper from 'main/content/utils/translationHelper';
const pageID = 'C403000';

class DialogReweighing extends Component {
    constructor(props) {
        super(props)
        this.owpDialog = React.createRef()
        this.onDelete = this.onDelete.bind(this)
        this.onDeleteClick = this.onDelete.bind(this)
        this.state = { stateDialogReweighing: false }
    }

    openDialogReweighing = () => {
        if (!Boolean(this.props.FACILITY_SEQ)) {
            OwpMessage({
                message: "설비/장비를 선택해주세요.",
                variant: "warning"
            })
            this.closeDialogReweighing()
        } else {
            this.setState({ stateDialogReweighing: true })
        }
    }

    closeDialogReweighing = () => {
        this.owpDialog.handleClose()
        this.setState({ stateDialogReweighing: false })
    }

    onDelete = async type => {
        const data = {
            cudtype: "delete",
            "OWP_FACILITIES.FACILITY_SEQ": this.props.FACILITY_SEQ
        }

        console.log('-----> '+data);

        const result = await mutate({
            url: "/deleteOwpFacilities/" + this.props.FACILITY_SEQ + "",
            data
        })
        .then(res => {
            const G = TGEvent.getGridByID("C403000")
            G.ReloadBody()
        })
        .catch(error => {
            console.error(error)
        })
        .finally(() => {
            this.closeDialogReweighing()
            this.props.ClearProps()
        })
    }

    onDeleteClick() {
        this.onDelete("START")
    }

    render() {
        return (
            <OwpDialog
                title={null}
                dialogButton={translationHelper('C803000.buttons.delete_button')} // Delete
                callbackFunction={this.openDialogReweighing}
                onOpen={this.state.stateDialogReweighing}
                dialogActions={
                    <React.Fragment>
                        <OwpButton color="secondary" variant="contained" onClick={this.onDeleteClick}>
                           Yes
                        </OwpButton>
                        <OwpButton color="secondary" variant="outlined" onClick={this.closeDialogReweighing}>
                            No
                        </OwpButton>
                    </React.Fragment>
                }
                innerRef={ref => (this.owpDialog = ref)}
                width="211px"
                height="100%"
            >
                <React.Fragment>
                    <div className="left mg0 pd0" style={{ fontSize: "14px" }}>
                        {translationHelper('C803000.message.do_you_want_to_delete')} {/*Are you sure to delete?*/}
                    </div>
                </React.Fragment>
            </OwpDialog>
        )
    }
}

export default DialogReweighing
