import {
    OwpTextField,
    OwpButton,
    OwpDialog,
    OwpMessage,
} from 'owp/wrapper';
import React, { Component } from 'react';
import Formsy from 'formsy-react';
import { mutate, query } from 'owp/api';
import * as TGEvent from 'owp/TGEvent';
import { debounce, isEmpty } from "lodash";
import OwpPresetAutocompleteTextField from "owp/components/Form/OwpPresetAutocompleteTextField";

class DialogReweighing extends Component {
    constructor(props) {
        super(props);
        this.owpDialog = React.createRef();
        this.onUpdate = this.onUpdate.bind(this);
        this.onUpdateClick = this.onUpdate.bind(this);
        this.state = {};
    }

    updateDialogOpen = () => {
        if (!Boolean(this.props.MULTYLANGSEQ)) {
            OwpMessage({
                message: '사용자를 선택해 주세요.',
                variant: 'warning',
            });
            this.updateDialogClose();
        } else {
           
            query({
                url: "loadIpxMultylang/" + this.props.MULTYLANGSEQ,
              }).then(res => {
                this.setState({
                    updateDialogOpen: true,
                    MULTYLANGSEQ : res['IPX_MULTYLANG.MULTYLANGSEQ'],
                    FIRSTMENUNM : res['IPX_MULTYLANG.FIRSTMENUNM'],
                    SECONDMENUNM : res['IPX_MULTYLANG.SECONDMENUNM'],
                    THIRDMENUNM : res['IPX_MULTYLANG.THIRDMENUNM'],
                    LANGID : res['IPX_MULTYLANG.LANGID'],
                    LANGKR : res['IPX_MULTYLANG.LANGKR'],
                    LANGEN : res['IPX_MULTYLANG.LANGEN'],
                    LOGSEQ : res['IPX_MULTYLANG.LOGSEQ'],
                    PAGEID : res['IPX_MULTYLANG.PAGEID']
                  });
                if(!isEmpty(res['IPX_MULTYLANG.THIRDMENUID'])){
                    document.getElementById("hideThirdMenu").style.display = "contents"
                }else{
                    document.getElementById("hideThirdMenu").style.display = "none"
                } 
            })
            .catch(error =>{
               console.error(error);
            })
            // this.setState({ updateDialogOpen: true });
        }
    };

    updateDialogClose = () => {
        this.props.callback();
        this.owpDialog.handleClose();
        
    };

    onUpdate = async (type) => {
        const data = {
            'cudtype': 'update',
            'IPX_MULTYLANG.PAGEID' : this.state.PAGEID,
            'IPX_MULTYLANG.LANGEN' : document.getElementById("LANGEN").value,
            'IPX_MULTYLANG.LANGKR' : document.getElementById("LANGKR").value,
            'IPX_MULTYLANG.LOGSEQ' : document.getElementById("LOGSEQ").value,
            'IPX_MULTYLANG.LANGID' : document.getElementById("LANGID").value,
            'IPX_MULTYLANG.MUSERSEQ' : "1",
            'IPX_MULTYLANG.MULTYLANGSEQ' : this.props.MULTYLANGSEQ + "",
        };

        const result = await mutate({
            url: 'updateIpxMultylang',
            data,
        }).then(res => {
            const G = TGEvent.getGridByID('C806000');
            G.ReloadBody();
        }).catch(error => {
            console.error(error);
        }).finally(() => {
            this.updateDialogClose();
            this.closeDialogReweighing2();
        });
    };

    onUpdateClick() {
        this.onUpdate('START');
    }

    openDialogReweighing2 = () => {
        this.setState({ stateDialogReweighing2: true })
    }

    closeDialogReweighing2 = () => {
    this.setState({ stateDialogReweighing2: false })
    }


    onSearchScreenID = (inputValue) => {
    
        query({
            url: "listOwpScreenID",
            params: {
                jsondata: JSON.stringify({
                    "IPX_COMMONCODE.CODEID": inputValue,
                  }),
               
            },
          }).then(res => {
            if(!isEmpty(res[0]['IPX_COMMONCODE.FIRSTMENUID'])){
                this.setState({
                    PAGEID: res[0]['IPX_COMMONCODE.PAGEID'],
                    FIRSTMENUNM: res[0]['IPX_COMMONCODE.FIRSTMENUNM'],
                    SECONDMENUNM: res[0]['IPX_COMMONCODE.SECONDMENUNM'],
                    THIRDMENUNM: res[0]['IPX_COMMONCODE.THIRDMENUNM']
                  });
                document.getElementById("hideThirdMenu").style.display = "contents"
            }else{
                this.setState({
                    PAGEID: res[0]['IPX_COMMONCODE.PAGEID'],
                    FIRSTMENUNM: res[0]['IPX_COMMONCODE.SECONDMENUNM'],
                    SECONDMENUNM: res[0]['IPX_COMMONCODE.THIRDMENUNM'],
                    THIRDMENUNM: null
                  });
                document.getElementById("hideThirdMenu").style.display = "none"
            }
            
            
        })
        .catch(error =>{
           console.error(error);
        })
    }

    render() {
        const { inputValue } = this.state;
        return (
            <OwpDialog
                title="다국어 수정"
                callbackFunction={this.updateDialogOpen}
                dialogButton="수정"
                dialogActions={
                    <React.Fragment>
                        <div className="w-full center">
                            <OwpButton color="secondary" variant="contained" onClick={this.openDialogReweighing2}>
                                수정
                            </OwpButton>
                            <OwpButton color="secondary" variant="outlined" onClick={this.updateDialogClose}>
                                닫기
                            </OwpButton>
                        </div>
                    </React.Fragment>
                }
                innerRef={(ref) => (this.owpDialog = ref)}
                width="800px"
                height="auto"
            >
                
                <React.Fragment>
                    <OwpDialog // 저장버튼을 누르면 나오는 다이얼로그.
                        onOpen={this.state.stateDialogReweighing2}
                        dialogActions={
                        <React.Fragment>
                            <OwpButton color="primary" variant="contained" onClick={this.onUpdateClick}>
                            예
                            </OwpButton>
                            <OwpButton
                            color="primary"
                            variant="contained"
                            onClick={this.closeDialogReweighing2}
                            >
                            아니요
                            </OwpButton>
                        </React.Fragment>
                        }
                        width="300px"
                        height="100%"
                        >
                        <React.Fragment>수정하시겠습니까?</React.Fragment>
                    </OwpDialog>

                    <Formsy>
                        <div style={{width:'100%'}}>
                            <table className="table table-bordered table-advance tlw5">
                                <tbody>
                                <tr>
                                    <th className="left" width="120">Screen ID</th>
                                    <td className="left">
                                       
                                        <OwpPresetAutocompleteTextField
                                            className="w-160"
                                            id="PAGEID"
                                            name="PAGEID"
                                            minInputLength={2}
                                            
                                            value = {this.state.PAGEID}
                                            onInputChange={debounce((inputValue) => this.setState({ inputValue }), 300, {
                                              maxWait: 1000,
                                            })}
                                            query={
                                              !isEmpty(this.state.inputValue) && {
                                                url: "/listOwpScreenID",
                                                params: {
                                                  "IPX_COMMONCODE.CODEID": inputValue,
                                                },
                                                item: {
                                                    labelKey: "IPX_COMMONCODE.PAGEID",
                                                    valueKey: "IPX_COMMONCODE.PAGEID"
                                                }
                                              }
                                            }
                                            isMulti={false}
                                            onChange={(value) => {
                                                
                                              this.onSearchScreenID(value["value"]);
                                            }}
                                            placeholder = {this.state.PAGEID}
                                           
                                        
                                    />


                                    </td>
                                    <th className="left" width="120">다국어ID</th>
                                    <td className="left">
                                        <OwpTextField
                                            style={{width:'100%'}}
                                            name = "LANGID"
                                            id = "LANGID"
                                            value = {this.state.LANGID}
                                            disabled={true}
                                            // variant = "outlined"
                                            // requied
                                        />
                                    </td>
                                            
                                </tr>
                                <tr>
                                    {/* <th className="left" width="120">대메뉴</th> */}
                                    <th className="left" width="120">1st Menu</th>
                                    <td className="left">
                                        <OwpTextField
                                            style={{width:'100%'}}
                                            name="FIRSTMENUNM"
                                            id="FIRSTMENUNM"
                                            disabled={true}
                                            value ={this.state.FIRSTMENUNM}
                                        />
                                    </td>
                                    {/* <th className="left" width="120">소메뉴</th> */}
                                    <th className="left" width="120">2nd Menu</th>
                                    <td className="left">
                                        <OwpTextField
                                            style={{width:'100%'}}
                                            name = "SECONDMENUNM"
                                            id = "SECONDMENUNM"
                                            disabled={true}
                                            value ={this.state.SECONDMENUNM}
                                        />
                                    </td>
                                   
                                </tr>
                                <tr  id="hideThirdMenu" style={{ display: "none" }}>
                                 
                                 {/* <th className="left" width="120">Third Menu</th> */}
                                 <th className="left" width="120">3rd Menu</th>
                                 <td className="left">
                                     <OwpTextField
                                         style={{width:'100%'}}
                                         name = "THIRDMENUNM"
                                         id = "THIRDMENUNM"
                                         value = {this.state.THIRDMENUNM}
                                         disabled={true}
                                     />
                                 </td>
                              
                             </tr> 
                                <tr>
                                    <th className="left">국문</th>
                                    <td className="left">
                                        <OwpTextField
                                            style={{width:'100%'}}
                                            name="LANGKR"
                                            id="LANGKR"
                                            value = {this.state.LANGKR}
                                        />
                                    </td>
                                    <th className="left">영문</th>
                                    <td className="left">
                                        <OwpTextField
                                            style={{width:'100%'}}
                                            name="LANGEN"
                                            id="LANGEN"
                                            value = {this.state.LANGEN}
                                        />
                                    </td>
                                </tr>
                                <tr>
                                    <th className="left">비고</th>
                                    <td className="left" colspan="3">
                                        <OwpTextField
                                            // className="t_textarea"
                                            // style={{ maxHeight: '200px', maxWidth: '100%' }}
                                            style={{width:'100%', maxHeight: '200px',}}
                                            name="LOGSEQ"
                                            id = "LOGSEQ"
                                            value = {this.state.LOGSEQ}
                                        />
                                    </td>
                                </tr>
                                </tbody>
                            </table>
                        </div>
                    </Formsy>
                </React.Fragment>
            </OwpDialog>
        );
    }
}

export default DialogReweighing;
