import React from "react";
import jsPDF from 'jspdf';
import OwpButton from "owp/wrapper/OwpButton";


var generateData = function (amount) {
    var result = [];
    var data =
        {
            coin: "100",
            game_group: "GameGroup",
            game_name: "XPTO2",
            game_version: "25",
            machine: "20485861",
            vlt: "0"
        };
    for (var i = 0; i < amount; i += 1) {
        data.id = (i + 1).toString();
        result.push(Object.assign({}, data));
    }
    return result;
};

function createHeaders(keys) {
    var result = [];
    for (var i = 0; i < keys.length; i += 1) {
        result.push({
            'id' : keys[i],
            'name': keys[i],
            'prompt': keys[i],
            'width': 65,
            'align': 'center',
            'padding': 0
        });
    }
    return result;
}

class Page extends React.Component {


    gopdf = () => {

        var headers = createHeaders(["id", "coin", "game_group", "game_name", "game_version", "machine", "vlt"]);

        var doc = new jsPDF({ putOnlyUsedFonts: true, orientation: 'landscape' });
        doc.table(1, 1, generateData(100), headers, { autoSize: true });
        doc.save('Test.pdf');

    };
    gopdf2 = () => {

        var employees = [
            {"firstName":"John", "lastName":"Doe"},
            {"firstName":"Anna", "lastName":"Smith"},
            {"firstName":"Peter", "lastName":"Jones"}
        ];

        var doc = new jsPDF();
        employees.forEach(function(employee, i){
            doc.text(20, 10 + (i * 10),
                "First Name: " + employee.firstName +
                "Last Name: " + employee.lastName);
        });
        doc.save('Test.pdf');

    };




    render() {
        return (
            <React.Fragment>

                <OwpButton onClick={this.gopdf} variant="contained"
                           color="secondary">cellpdf</OwpButton>

                <OwpButton onClick={this.gopdf2} variant="contained"
                           color="secondary">gopdf</OwpButton>

            </React.Fragment>
        );
    }
}



export default Page;
