import React, { Component } from 'react';
import {
    OwpButton,
    OwpSearchDateTimePickerMulti,
    OwpPageCarded,
    OwpSearchHeader,
    OwpSearchSelectField,
    OwpSearchTextField,
    OwpToolbar,
    OwpTreeGrid,
    OwpMessage,
    OwpSearchAutoComplete,
} from 'owp/wrapper';
import * as TGEvent from 'owp/TGEvent';
import Pop_C0202002 from '../../include/Pop_C0202002';
import Pop_C0202005 from '../../include/Pop_C0202005';
import Pop_C0202006 from '../../include/Pop_C0202006';
import Dialog_C202026 from '../../include/Dialog_C202026';
import Dialog_C201104 from '../../include/Dialog_C201104';

class Page extends Component {
    constructor(props) {
        super(props);
        this.state = {};
    }
    componentDidMount() {
        TGEvent.OWPTGSelect('C0202000', (is, row) => {
            //TODO question here
            if (is) {
                this.setState((state) => {
                    return { MBRSEQ: row['OWP_MBR.MBRSEQ'] };
                });
                document.getElementById('hideButtonCopy').style.display = 'block';
                document.getElementById('hideButtonModify').style.display = 'block';
                document.getElementById('hideButtonDelete').style.display = 'block';
            } else {
                this.setState((state) => {
                    return { MBRSEQ: null };
                });
                document.getElementById('hideButtonCopy').style.display = 'none';
                document.getElementById('hideButtonModify').style.display = 'none';
                document.getElementById('hideButtonDelete').style.display = 'none';
            }
        });

        const _show = this.show;
        window.TGSetEvent('OnClick', 'C0202000', function (grid, row, col, val, bool, event) {
            event.preventDefault();
            if (col === 'DETAIL' && row.Kind === 'Data') {
                _show(row);
            }
        });
    }

    show = (row) => {
        console.log('RowData', row);
        if (!Boolean(row['OWP_MBR.MBRSEQ'])) {
            OwpMessage({
                message: 'Data empty',
                variant: 'warning',
            });
            return false;
        } else {
            alert('Show Detail with SEQ:' + row['OWP_MBR.MBRSEQ']);
            //   this.setState({
            //     dialogData: row,
            //     });
            //     this.Dialog_C404005.openDialog_customer();
        }
    };

    callbackHidenButton = () => {
        document.getElementById('hideButtonDelete').style.display = 'none';
        document.getElementById('hideButtonModify').style.display = 'none';
        document.getElementById('hideButtonCopy').style.display = 'none';
    };

    render() {
        return (
            <React.Fragment>
                <OwpPageCarded
                    header={
                        <OwpSearchHeader url="listOwpMbr" autoSubmit={true}>
                            <OwpSearchTextField
                                className="w-160"
                                id={'OWP_MBR.PRODUCTNAME'}
                                name={'OWP_MBR.PRODUCTNAME'}
                                label={'제품명'}
                            />

                            <OwpSearchTextField
                                className="w-160"
                                id={'OWP_MBR.PRODUCTID'}
                                name={'OWP_MBR.PRODUCTID'}
                                label={'제품ID번호'}
                            />

                            <OwpSearchSelectField
                                className="w-160"
                                id={'OWP_MBR.MBRSTAUS'}
                                name={'OWP_MBR.MBRSTAUS'}
                                label="진행현황"
                                groupId={'D020000'}
                                useAll
                            />
                            <OwpSearchTextField
                                className="w-160"
                                id={'OWP_MBR.MBRNO'}
                                name={'OWP_MBR.MBRNO'}
                                label={'MBR 번호'}
                            />

                            <OwpSearchTextField
                                className="w-160"
                                id={'OWP_MBR.MBRVER'}
                                name={'OWP_MBR.MBRVER'}
                                label={'버전번호'}
                            />
                            <OwpSearchTextField
                                className="w-160"
                                id={'OWP_MBR.WUSERSEQ'}
                                name={'OWP_MBR.WUSERSEQ'}
                                label={'작가'}
                            />
                            <OwpSearchDateTimePickerMulti
                                id="OWP_MBR.WDATE"
                                name="OWP_MBR.WDATE"
                                initNow={true} // TODO: true 인 경우 현재 시로 init (* default = false)
                                labels={{ start: '배치등록시작일', end: '배치등록종료일' }}
                                inputProps={{
                                    start: { style: { width: 120 } },
                                    end: { style: { width: 120 } },
                                }}
                                required={true}
                            />
                        </OwpSearchHeader>
                    }
                    contentToolbar={
                        <OwpToolbar title="Master 제조지시 조회">
                            <OwpButton color={'secondary'} variant={'outlined'}>
                                엑셀저장
                            </OwpButton>
                            <Dialog_C202026 />
                            {/* <Pop_C0202002/> */}
                            <div id="hideButtonCopy" style={{ display: 'none' }}>
                                <OwpButton color={'secondary'} variant={'contained'}>
                                    COPY
                                </OwpButton>
                            </div>
                            <div id="hideButtonModify" style={{ display: 'none' }}>
                                <OwpButton color={'secondary'} variant={'contained'}>
                                    수정
                                </OwpButton>
                            </div>
                            <div id="hideButtonDelete" style={{ display: 'none' }}>
                                <Dialog_C201104
                                    MBRSEQ={this.state.MBRSEQ}
                                    callbackHidenButton={this.callbackHidenButton}
                                />
                            </div>
                            <Pop_C0202005 />
                            <Pop_C0202006 />
                        </OwpToolbar>
                    }
                    content={
                        <React.Fragment>
                            <OwpTreeGrid id="C0202000" LayoutUrl={'/assets/data/C0202000Def.xml'} />
                        </React.Fragment>
                    }
                    innerScroll
                />
            </React.Fragment>
        );
    }
}

export default Page;
