import React, { Component } from "react";
import {
    OwpButton,
    OwpSearchDateField,
    OwpSearchSelectField,
    OwpSearchTextField,
    OwpTextField,
    OwpSelectField
} from 'owp/wrapper';
import OwpPresetAutocompleteTextField from "owp/components/Form/OwpPresetAutocompleteTextField";
import { debounce, isEmpty } from "lodash";
import Formsy from "formsy-react";
import * as TGEvent from 'owp/TGEvent';
import moment from "moment";
import { mutate, query } from 'owp/api';

const KOREAN_FORMAT_DATE = 'YYYY-MM-DD';
class Page extends Component {
    constructor(props) {
        super(props);
        this.state = {

        };
    }
    /**
     * Owp에서 사용하는 인풋에 대해 모두 사용할 수 있는 change 이벤트 리시버
     * @param {Event} event 발생한 이벤트 객체
     * @param {Function} callback 콜백 함수. 인풋의 name, value를 인수로 전달함.
     */
    componentDidMount() {
        const _showApprovalDialog = this.showApprovalDialog;
        this.loadData();
    }

    //날짜 디폴드값 설정 함수

    loadData = async () => {
        query({
            // url: "loadOWP_MBRCover/" + this.props.MULTYLANGSEQ,
            // url: "loadOWP_MBRCover/67",
            url: "loadOWP_MBRCover/" + this.props.MBRSEQ,
        }).then(res => {
            for (let key in res) {
                let stateObj = {};
                stateObj[key] = res[key];
                this.setState(stateObj);
            }
        })
        .catch(error =>{
           console.error(error);
        })
    };

    onSelectProductId = (inputValue) => {
        const loadProduct = async () => {
            const data = await query({
                url: `loadOwpMaterial/${inputValue}`
            }).catch(error => {
                console.error(error)
            });

            this.setState({ 'OWP_MBR.MBRNO': `SBR-${data['OWP_MATERIAL.MATERIALCODE']}` });
            this.setState({ 'OWP_MBR.PRODUCTNAME': data['OWP_MATERIAL.MATERIALDECS'] });

            query({
                url: `getMbrVersion/${data['OWP_MATERIAL.MATERIALCODE']}`
            }).then(res => {
                if (res) {
                    this.setState({ 'OWP_MBR.MBRVER': res });
                }
            }).catch(error => {
                console.error(error)
            });;
        };

        loadProduct();
    }

    handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
    };
    handleChangeProductName = (event) => {
        this.props.handleChangeProductName(event.target.value);
    };
    render() {
        const { inputValue } = this.state;
        return (
            <React.Fragment>
                <Formsy>
                    <div style={{ width: '100%' }}>
                        <div>
                            <div className="b mgt20">Document Information and Document registration approval information</div>
                            <table className="table table-bordered table-advance tlw5 mgt10">
                                <tbody>
                                    <tr>
                                        <th className="left" width="140">Plant</th>
                                        <td className="left">
                                            <OwpSelectField
                                                className="w-full left"
                                                id="OWP_MBR.MBRPLANT"
                                                name="OWP_MBR.MBRPLANT"
                                                groupId={"D019000"}
                                                onChange={this.handleChange}
                                                isall={false}
                                                value={this.state['OWP_MBR.MBRPLANT']}
                                            />
                                        </td>
                                        <th className="left" width="140">Record</th>
                                        <td className="left">
                                            <OwpSelectField
                                                className="w-full left"
                                                id="OWP_MBR.MBRRECORD"
                                                name="OWP_MBR.MBRRECORD"
                                                onChange={this.handleChange}
                                                options={[
                                                    { label: 'Batch', value: 'Batch' }
                                                ]}
                                                defaultValue={'Batch'}
                                                useAll={false}
                                            />
                                        </td>
                                    </tr>
                                    <tr>
                                        <th className="left" width="140">MBR No.</th>
                                        <td className="left">
                                            <OwpTextField
                                                style={{ width: '100%' }}
                                                id="OWP_MBR.MBRNO"
                                                name="OWP_MBR.MBRNO"
                                                value={this.state['OWP_MBR.MBRNO']}
                                            />
                                        </td>
                                        <th className="left" width="140">Version</th>
                                        <td className="left">
                                            <OwpTextField
                                                style={{ width: '100%' }}
                                                id="OWP_MBR.MBRVER"
                                                name="OWP_MBR.MBRVER"
                                                value={this.state['OWP_MBR.MBRVER']}
                                            />
                                        </td>
                                    </tr>
                                    <tr>
                                        <th className="left" width="140">Product</th>
                                        <td className="left">
                                            <OwpTextField
                                                style={{ width: '100%' }}
                                                id="OWP_MBR.PRODUCTNAME"
                                                name="OWP_MBR.PRODUCTNAME"
                                                value={this.state['OWP_MBR.PRODUCTNAME']}
                                                onChange={this.handleChangeProductName}
                                            />
                                        </td>
                                        <th className="left" width="140">Product ID No.</th>
                                        <td className="left">
                                            <OwpPresetAutocompleteTextField
                                                style={{ width: '100%' }}
                                                id="OWP_MBR.PRODUCTID"
                                                name="OWP_MBR.PRODUCTID"
                                                minInputLength={2}
                                                onInputChange={debounce((inputValue) => this.setState({ inputValue }), 300, {maxWait: 1000})}
                                                query={
                                                    !isEmpty(this.state.inputValue) && {
                                                        url: "/listOwpMaterial",
                                                        params: {
                                                            "OWP_MATERIAL.MATERIALCODE": inputValue,
                                                            "OWP_MATERIAL.MATERIALTYPE": 'FERT',
                                                        },
                                                        item: {
                                                            labelKey: "OWP_MATERIAL.MATERIALCODE",
                                                            valueKey: "OWP_MATERIAL.MATERIALSEQ"
                                                        }
                                                    }
                                                }
                                                isMulti={false}
                                                onChange={(value) => {
                                                    this.setState({ 'OWP_MATERIAL.MATERIALCODE': value["label"] });
                                                    this.onSelectProductId(value["value"]);
                                                }}
                                            />
                                        </td>
                                    </tr>
                                    <tr>
                                        <th className="left" width="140">Written by Production Engineer(Writer)</th>
                                        <td className="left">
                                            <OwpPresetAutocompleteTextField
                                                style={{ width: '100%' }}
                                                id="OWP_MBR.USERNAME"
                                                name="OWP_MBR.USERNAME"
                                                minInputLength={2}
                                                onInputChange={debounce((inputValue) => this.setState({ inputValue }), 300, {maxWait: 1000})}
                                                query={
                                                    !isEmpty(this.state.inputValue) && {
                                                        url: "/listIpxUser",
                                                        params: {
                                                            "IPX_USER.USERNAME": inputValue,
                                                            "IPX_USER.FLAG": 'Y',
                                                        },
                                                        item: {
                                                            labelKey: "IPX_USER.USERNAME",
                                                            valueKey: "IPX_USER.USERSEQ"
                                                        }
                                                    }
                                                }
                                                isMulti={false}
                                                onChange={(value) => {
                                                    this.setState({ 'OWP_MBR.USERNAME': value["label"] });
                                                    this.setState({ 'OWP_MBR.WUSERSEQ': value["value"] });
                                                }}
                                            />
                                        </td>
                                        <th className="left" width="140">Writer Registration Date</th>
                                        <td className="left">
                                            <OwpSearchDateField
                                                style={{ width: '100%' }}
                                                id="OWP_MBR.WDATE"
                                                name="OWP_MBR.WDATE"
                                                value={this.state['OWP_MBR.WDATE'] ? moment(this.state['OWP_MBR.WDATE']).format(KOREAN_FORMAT_DATE)
                                                : ''}
                                                onChange={this.handleChange}
                                                initNow={true}
                                            />
                                        </td>
                                    </tr>
                                    <tr>
                                        <th className="left" width="140">Approved by Production Manager(Reviewer1)</th>
                                        <td className="left">
                                            <OwpPresetAutocompleteTextField
                                                style={{ width: '100%' }}
                                                id="OWP_MBR.MBRAPPRBYPM"
                                                name="OWP_MBR.MBRAPPRBYPM"
                                                minInputLength={2}
                                                onInputChange={debounce((inputValue) => this.setState({ inputValue }), 300, {maxWait: 1000})}
                                                query={
                                                    !isEmpty(this.state.inputValue) && {
                                                        url: "/listIpxUser",
                                                        params: {
                                                            "IPX_USER.USERNAME": inputValue,
                                                            "IPX_USER.FLAG": 'Y',
                                                        },
                                                        item: {
                                                            labelKey: "IPX_USER.USERNAME",
                                                            valueKey: "IPX_USER.USERSEQ"
                                                        }
                                                    }
                                                }
                                                isMulti={false}
                                                onChange={(value) => {
                                                    this.setState({ 'OWP_MBR.MBRAPPRBYPM': value["label"] });
                                                }}
                                            />
                                        </td>
