    OwpSelectField,
    OwpDialog,
    OwpMessage,
    OwpTreeGrid,
    OwpSearchDateTimePickerMulti,
    OwpDateTimePicker,
    OwpTabs,
    OwpCheckBox,
} from 'owp/wrapper';
import React, { Component } from 'react';
import Formsy from 'formsy-react';
import { mutate, query } from 'owp/api';
import * as TGEvent from 'owp/TGEvent';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import { Typography, withStyles } from '@material-ui/core';
// import { OwpDext5Editor } from 'main/OwpDext5.js';
import Pop_C0202002_01 from './Pop_C0202002_01';
import Pop_C0202002_02 from './Pop_C0202002_02';
import C0202002_tab01 from './C0202002_tab01';
import C0202002_tab02 from './C0202002_tab02';
import C0202002_tab03 from './C0202002_tab03';
import C0202002_tab04 from './C0202002_tab04';
import C0202002_tab05 from './C0202002_tab05';
import C0202002_tab06 from './C0202002_tab06';
import C0202002_tab07 from './C0202002_tab07';
import DextEditor from 'main/custom-component/DextEditor';
import translationHelper from 'main/content/utils/translationHelper';

function TabContainer(props) {
    return (
        <Typography component="div" style={{ padding: '0' }}>
            {props.children}
        </Typography>
    );
}
const styles = (theme) => ({
    tabsRoot: {
        borderBottom: '1px solid #e8e8e8',
    },
    tabsIndicator: {
        backgroundColor: '#1890ff',
    },
    tabRoot: {
        textTransform: 'initial',
        fontSize: 14,
        minWidth: 72,
        fontWeight: theme.typography.fontWeightRegular,
        marginRight: theme.spacing.unit * 4,
        fontFamily: [
            'NotoSans',
            '-apple-system',
            'BlinkMacSystemFont',
            '"Segoe UI"',
            'Roboto',
            '"Helvetica Neue"',
            'Arial',
            'sans-serif',
            '"Apple Color Emoji"',
            '"Segoe UI Emoji"',
            '"Segoe UI Symbol"',
        ].join(','),
        '&:hover': {
            color: '#40a9ff',
            opacity: 1,
        },
        '&$tabSelected': {
            borderBottom: '3px solid #e8e8e8',
            color: '#1890ff',
            fontWeight: theme.typography.fontWeightMedium,
        },
        '&:focus': {
            color: '#40a9ff',
        },
    },
    tabSelected: {},
});

const pageID = 'C202012';
class DialogReweighing extends Component {
    constructor(props) {
        super(props);
        // this.owpDialog = React.createRef();

        this.clickCreate = this.clickCreate.bind(this);
        this.onSave = this.onSave.bind(this);
        console.log(this.props);
        this.C0202002_tab01Ref = React.createRef();
        this.C0202002_tab07Ref = React.createRef();
        this.state = {
            createDialogOpen: false,
            FLAG: 'Y',
            tabValue: 0,
            MBRSEQ: 7,
            // MBRSEQ :  this.props.MBRSEQ,
            TYPE: this.props.TYPE,
            productName: 'Unidentified Product Name',
        };
    }

    openDialog_Register = () => {
        this.setState({
            createDialogOpen: true,
            MBRSEQ: this.props.MBRSEQ,
            TYPE: this.props.TYPE,
        });
    };
    componentDidMount() {
        this.getProcessTree();
    }

    handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
    };

    getProcessTree = async () => {
        try {
            const data =
                (await query({
                    url: 'listOWP_ProcessTree/' + this.state.MBRSEQ,
                })) || [];
            this.setState({
                groupNamesProcessTree: [{ gname: this.state.productName, items: data.items }],
            });
        } catch (error) {
            console.error(error);
        }
    };

    // 등록 화면 닫기
    createDialogClose = () => {
        this.setState({ createDialogOpen: false });
        this.owpDialog.handleClose();
        const TYPE = this.state.TYPE;
        if (TYPE == 'Register') {
            this.props.callback();
        }
    };

    // 등록 확인 화면 열기
    handleTemporarySave = () => {
        this.setState({ createCheckOpen: true });
        this.setState({ popMessage: '저장하시겠습니까?' });
        this.onTemporarySave('TEMP');
    };

    // 등록 확인 화면 닫기
    createCheckClose = () => {
        this.setState({ createCheckOpen: false });
    };

    onTemporarySave = async (type) => {
        switch (this.state.tabValue) {
            case 0:
                if (this.C0202002_tab01Ref) {
                    this.C0202002_tab01Ref.current.opUpdate('START');
                }
                break;
            case 1:
                break;
            case 2:
                break;
            case 3:
                break;
            case 4:
                break;
            case 5:
                break;
            case 6:
                console.log('call test');
                if (this.C0202002_tab07Ref && this.C0202002_tab07Ref.current) {
                    console.log(this.C0202002_tab07Ref);
                    //this.C0202002_tab07Ref.current.saveMainConfig();
                }
                break;
        }
    };

    onSave = async (type) => {
        let _type = '';

        if (type == 'TEMP') {
            _type = 'D020001';
        } else {
            _type = 'D020002';
        }

        const data = {
            cudtype: 'create',
            'OWP_MBR.MBRSTAUS': _type,
            'OWP_MBR.FACILITY_NAME':
                document.getElementById('OWP_MBR.FACILITY_NAME').value == null
                    ? ''
                    : document.getElementById('OWP_MBR.FACILITY_NAME').value,
            'OWP_MBR.FACILITY_ID':
                document.getElementById('OWP_MBR.FACILITY_ID').value == null
                    ? ''
                    : document.getElementById('OWP_MBR.FACILITY_ID').value,
            'OWP_MBR.MBRNO':
                document.getElementById('OWP_MBR.MBRNO').value == null
                    ? ''
                    : document.getElementById('OWP_MBR.MBRNO').value,
            'OWP_MBR.MBRVER':
                document.getElementById('OWP_MBR.MBRVER').value == null
                    ? ''
                    : document.getElementById('OWP_MBR.MBRVER').value,
            'OWP_MBR.WUSERSEQ': '1',

            /*  "OWP_MBR.MBRPLANT":document.getElementById('OWP_MBR.MBRPLANT').value==null? '': document.getElementById('OWP_MBR.MBRPLANT').value,
            "OWP_MBR.MBRAPPRBYPM":document.getElementById('OWP_MBR.MBRAPPRBYPM').value==null? '': document.getElementById('OWP_MBR.MBRAPPRBYPM').value,
            "OWP_MBR.MBRAPPRBYQM":document.getElementById('OWP_MBR.MBRAPPRBYQM').value==null? '': document.getElementById('OWP_MBR.MBRAPPRBYQM').value,
            "OWP_MBR.MBREFFDATE":document.getElementById('OWP_MBR.MBREFFDATE').value==null? '': document.getElementById('OWP_MBR.MBREFFDATE').value,
            "OWP_MBR.MATERIALSEQ":"1",//document.getElementById('OWP_MBR.MATERIALSEQ').value==null? '': document.getElementById('OWP_MBR.MATERIALSEQ').value,
            "OWP_MBR.PRODUCTNAME":document.getElementById('OWP_MBR.PRODUCTNAME').value==null? '': document.getElementById('OWP_MBR.PRODUCTNAME').value,
            "OWP_MBR.PRODUCTID":document.getElementById('OWP_MBR.PRODUCTID').value==null? '': document.getElementById('OWP_MBR.PRODUCTID').value,
            "OWP_MBR.BRISSUEDATE":document.getElementById('OWP_MBR.BRISSUEDATE').value==null? '': document.getElementById('OWP_MBR.BRISSUEDATE').value,
            "OWP_MBR.THEOREYIELD":document.getElementById('OWP_MBR.THEOREYIELD').value==null? '': document.getElementById('OWP_MBR.THEOREYIELD').value,
            "OWP_MBR.PRODSTARTDATE":document.getElementById('OWP_MBR.PRODSTARTDATE').value==null? '': document.getElementById('OWP_MBR.PRODSTARTDATE').value,
            "OWP_MBR.PRODCOMPDATE":document.getElementById('OWP_MBR.PRODCOMPDATE').value==null? '': document.getElementById('OWP_MBR.PRODCOMPDATE').value,
            "OWP_MBR.ACTUALPRODWT":document.getElementById('OWP_MBR.ACTUALPRODWT').value==null? '': document.getElementById('OWP_MBR.ACTUALPRODWT').value,
            "OWP_MBR.EXTPERYIELD":document.getElementById('OWP_MBR.EXTPERYIELD').value==null? '': document.getElementById('OWP_MBR.EXTPERYIELD').value,
            "OWP_MBR.ACTUALPERYIELD":document.getElementById('OWP_MBR.ACTUALPERYIELD').value==null? '': document.getElementById('OWP_MBR.ACTUALPERYIELD').value,
           // "OWP_MBR.GENERALINST": this.Editor.getContents(),
           */
        };

        /*      const data = {
            cudtype: 'create',
            "OWP_MBR.GENERALINST": this.Editor.getContents(),
        };
*/

        const result = await mutate({
            url: '/createOwpMbr',
            data,
        })
            .then((res) => {
                const G = TGEvent.getGridByID('C0202000');
                G.ReloadBody();
                this.createDialogClose();
                this.createCheckClose();
            })
            .catch((error) => {
                console.error(error);
                this.createCheckClose();
            });
    };
    openDialog_Register = () => {
        this.setState({
            createDialogOpen: true,
            // MBRSEQ :  7,
            MBRSEQ: this.props.MBRSEQ,
            TYPE: this.props.TYPE,
        });
    };

    clickCreate() {
        this.onSave('START');
    }
    handleChangeTabs = (event, tabValue) => {
        //탭을 변환할때마다 조회하기 위한 변수를 담아줌
        this.setState({ tabValue });
    };

    setProductName = (productName) => {
        this.setState({ productName: productName });
    };
    // productName = {document.getElementById('OWP_MBR.PRODUCTNAME').value
    render() {
        const { classes } = this.props;
        const { tabValue } = this.state;

        return (
            <OwpDialog
                // title="수불 대장"
                title={
                    <div className="pt-0 mt-0 pb-0 mb-0 w-full flex justify-between items-center">
                        <div>{translationHelper(pageID + '.register_po_management_title')}</div>
                        <div style={{ right: '150px', position: 'absolute' }}></div>
                        <div></div>
                    </div>
                }
                callbackFunction={this.openDialog_Register}
                onOpen={this.state.createDialogOpen}
                dialogButton="등록"
                dialogActions={
                    <React.Fragment>
                        <div className="w-full center">
                            <OwpButton
                                color="secondary"
                                variant="contained"
                                onClick={this.handleTemporarySave}
                            >
                                {translationHelper(pageID + '.temporary_save_button')}
                            </OwpButton>
                            <OwpButton
                                color="secondary"
                                variant="contained"
                                onClick={this.clickCreate}
                            >
                                {translationHelper(pageID + '.save_button')}
                            </OwpButton>
                            <OwpButton
                                color="secondary"
                                variant="outlined"
                                onClick={this.createDialogClose}
                            >
                                {translationHelper(pageID + '.close_button')}
                            </OwpButton>
                        </div>
                    </React.Fragment>
                }
                innerRef={(ref) => (this.owpDialog = ref)}
                width="80vw"
                height="60vh"
            >
                <React.Fragment>
                    <div className="row mg0 pd0">
                        {/*TAB*/}
                        <Tabs
                            style={{ border: '0px solid' }}
                            value={tabValue}
                            onChange={this.handleChangeTabs}
                            classes={{ root: classes.tabsRoot, indicator: classes.tabsIndicator }}
                        >
                            <Tab
                                disableRipple
                                classes={{ root: classes.tabRoot, selected: classes.tabSelected }}
                                label={translationHelper(pageID + '.cover_tab_title')}
                            />
                            <Tab
                                disableRipple
                                classes={{ root: classes.tabRoot, selected: classes.tabSelected }}
                                label={translationHelper(pageID + '.general_inst_tab_title')}
                            />
                            <Tab
                                disableRipple
                                classes={{ root: classes.tabRoot, selected: classes.tabSelected }}
                                label={translationHelper(pageID + '.msds_summary_tab_title')}
                            />
                            <Tab
                                disableRipple
                                classes={{ root: classes.tabRoot, selected: classes.tabSelected }}
                                label={translationHelper(pageID + '.carppe_summary_tab_title')}
                            />
                            <Tab
                                disableRipple
                                classes={{ root: classes.tabRoot, selected: classes.tabSelected }}
                                label={translationHelper(pageID + '.material_summary_tab_title')}
                            />
                            <Tab
                                disableRipple
                                classes={{ root: classes.tabRoot, selected: classes.tabSelected }}
                                label={translationHelper(pageID + '.equipment_info_tab_title')}
                            />
                            <Tab
                                disableRipple
                                classes={{ root: classes.tabRoot, selected: classes.tabSelected }}
                                label={translationHelper(pageID + '.unit_process_tab_title')}
                            />
                        </Tabs>
                        {tabValue === 0 && (
                            <TabContainer>
                                {/*일단위 등록 현황*/}
                                <C0202002_tab01
                                    ref={this.C0202002_tab01Ref}
                                    MBRSEQ={this.state.MBRSEQ}
                                    TYPE={this.state.TYPE}
                                    handleChangeProductName={this.setProductName}
                                />
                            </TabContainer>
                        )}
                        {tabValue === 1 && (
                            <TabContainer>
                                {/*주단위 등록 현황*/}
                                <C0202002_tab02 />
                            </TabContainer>
                        )}
                        {tabValue === 2 && (
                            <TabContainer>
                                {/*월단위 등록 현황*/}
                                <C0202002_tab03 MBRSEQ={this.state.MBRSEQ} TYPE={this.state.TYPE} />
                            </TabContainer>
                        )}
                        {tabValue === 3 && (
                            <TabContainer>
                                {/*분기단위 등록 현황*/}
                                <C0202002_tab04 MBRSEQ={this.state.MBRSEQ} TYPE={this.state.TYPE} />
                            </TabContainer>
                        )}
                        {tabValue === 4 && (
                            <TabContainer>
                                {/*분기단위 등록 현황*/}
                                <C0202002_tab05 MBRSEQ={this.state.MBRSEQ} TYPE={this.state.TYPE} />
                            </TabContainer>
                        )}
                        {tabValue === 5 && (
                            <TabContainer>
                                {/*분기단위 등록 현황*/}
                                <C0202002_tab06 MBRSEQ={this.state.MBRSEQ} TYPE={this.state.TYPE} />
                            </TabContainer>
                        )}
                        {tabValue === 6 && (
                            <TabContainer>
                                {/*분기단위 등록 현황*/}
                                <C0202002_tab07
                                    ref={this.C0202002_tab07Ref}
                                    MBRSEQ={this.state.MBRSEQ}
                                    TYPE={this.state.TYPE}
                                    productName={this.state.productName}
                                    groupNamesProcessTree={this.state.groupNamesProcessTree}
                                />
                            </TabContainer>
                        )}
                    </div>
                </React.Fragment>
            </OwpDialog>
        );
    }
}

export default withStyles(styles, { withTheme: true })(DialogReweighing);
