import React, { Component } from "react";
import { OwpButton, OwpSearchDateField, OwpSearchSelectField, OwpSearchTextField, OwpTextField, OwpTreeGrid, OwpMessage } from 'owp/wrapper';
import Formsy from "formsy-react";
import * as TGEvent from 'owp/TGEvent';
import moment from "moment";
import { query } from 'owp/api';
import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";
import { Typography, withStyles } from "@material-ui/core";
import { onGridLoad } from '../../../main/GridUtill';
import translationHelper from 'main/content/utils/translationHelper';

const pageID = 'C202012.tab0501';
class Page extends Component {
    constructor(props) {
        super(props);
        this.state = {

        };
    }
    /**
     * Owp에서 사용하는 인풋에 대해 모두 사용할 수 있는 change 이벤트 리시버
     * @param {Event} event 발생한 이벤트 객체
     * @param {Function} callback 콜백 함수. 인풋의 name, value를 인수로 전달함.
     */
    componentDidMount() {
        const _showApprovalDialog = this.showApprovalDialog;
        if (this.props.MBRSEQ === "" || this.props.MBRSEQ === null || this.props.MBRSEQ === undefined) {
            OwpMessage({
                message: translationHelper('C202012.undefined_mbrseq'),
                variant: "warning",
            });
        } else {
            this.loadData();
        }
    }

    loadData = async () => {
        onGridLoad("C0202005_01", () => {
            TGEvent.OWPTGDataList("C0202005_01", "listMaterialSummary", {
                'OWP_MBR_UNIT_PROCESS.MBRSEQ' : this.props.MBRSEQ + ''
            })
        });
    };

    //날짜 디폴드값 설정 함수

    render() {
        return (
            <React.Fragment>
                <div style={{ width: '100%' }}>
                    <div>
                        <OwpTreeGrid
                            id="C0202005_01"
                            LayoutUrl="assets/data/C0202005_01Def.xml"
                            width="100%"
                            height="45vh"
                        />
                    </div>
                </div>
            </React.Fragment>
        );
    }
}

export default Page;
