import React, { Component } from "react"
import { OwpDialog, OwpButton, OwpSession, OwpSearchTextField, OwpMessage } from 'owp/wrapper';
import Formsy from "formsy-react"
import { query, mutate } from 'owp/api';
import * as TGEvent from 'owp/TGEvent';
import translationHelper from 'main/content/utils/translationHelper';
const pageID = 'C803000';

class DialogReweighing extends Component {
  constructor(props) {
    super(props)
    this.owpDialog = React.createRef()
    this.onSave = this.onSave.bind(this)
    this.onSaveClick = this.onSaveClick.bind(this)
    this.state = {
      popMessage: "",
      stateDialogReweighing: false
    }
  }

  openDialogReweighing = () => {
    if (!Boolean(this.props.LOWCODEID)) {
      OwpMessage({
        message: translationHelper(pageID + '.message.please_select_sub_code'), //Please select a Sub Code.
        variant: "warning"
      })
      this.closeDialogReweighing()
    } else {
      this.loadValue()
      this.setState({ stateDialogReweighing: true })

    }
  }

  closeDialogReweighing = () => {
    this.setState({ stateDialogReweighing: false })
    this.owpDialog.handleClose()
  }

  openDialogReweighing2 = () => {
    this.setState({ stateDialogReweighing2: true })
    this.setState({ popMessage: translationHelper(pageID + '.message.do_you_want_to_save') }) //Would you like to modify?
  }

  closeDialogReweighing2 = () => {
    this.setState({ stateDialogReweighing2: false })
  }

  loadValue = () => {
    query({
      url: "/loadIpxCommoncode/" + this.props.LOWCODEID + ""
    })
      .then(res => {
        this.setState({
          GROUPID: res["IPX_COMMONCODE.GROUPID"],
          GROUPNM: res["IPX_COMMONCODE.GROUPNM"],
          CODEID: res["IPX_COMMONCODE.CODEID"],
          CODENM: res["IPX_COMMONCODE.CODENM"],
          REMARK: res["IPX_COMMONCODE.REMARK"],
          FLAG: res["IPX_COMMONCODE.FLAG"]
        })
      })
      .catch(error => console.error("error...", error))
  }

  onSave = async type => {
    const data = {
      cudtype: "update",
      "IPX_COMMONCODE.CODEID": document.getElementById("CODEID").value, //하위코드 CODE
      "IPX_COMMONCODE.CODENM": document.getElementById("CODENM").value, //코드명
      "IPX_COMMONCODE.REMARK": document.getElementById("REMARK").value, //비고
      "IPX_COMMONCODE.FLAG": "Y", //사용여부
      // "IPX_COMMONCODE.FLAG": document.getElementById("FLAG").value, //사용여부
      "IPX_COMMONCODE.WUSERSEQ": OwpSession("USERSEQ"),// : 로그인 사용자SEQ
    }

    const result = await mutate({
      url: "/updateIPX_CommonCodeLow",
      data
    })
      .then(res => {
        const G = TGEvent.getGridByID("C803002")
        G.ReloadBody()
        this.props.LowClearProps()
        this.closeDialogReweighing()
        this.closeDialogReweighing2()
      })
      .catch(error => {
        console.error(error)
        this.closeDialogReweighing2()
      })
  }

  onSaveClick() {
    this.onSave("START")
  }

  handleChange = event => {
    this.setState({ [event.target.name]: event.target.value })
  }
  render() {
    console.log(this.state);
    return (
      <OwpDialog
        title={translationHelper(pageID + '.title.modify_sub_dialog_title')} //Sub Code Modify
        dialogButton={translationHelper(pageID + '.buttons.modify_button')} //Modify
        callbackFunction={this.openDialogReweighing}
        onOpen={this.state.stateDialogReweighing}
        dialogActions={
          <React.Fragment>
            <OwpButton color="secondary" variant="contained" onClick={this.openDialogReweighing2}>
            {translationHelper(pageID + '.buttons.dialog_save_button')} {/*Save*/}
            </OwpButton>
            <OwpButton color="secondary" variant="outlined" onClick={this.closeDialogReweighing}>
            {translationHelper(pageID + '.buttons.dialog_close_button')} {/*Close*/}
            </OwpButton>
          </React.Fragment>
        }
        innerRef={ref => (this.owpDialog = ref)}
        width="700px"
      >
        <React.Fragment>
          <OwpDialog // 저장버튼을 누르면 나오는 다이얼로그.
            onOpen={this.state.stateDialogReweighing2}
            dialogActions={
              <React.Fragment>
                <OwpButton color="secondary" variant="contained" onClick={this.onSaveClick}>
                {translationHelper(pageID + '.buttons.dialog_ok_button')} {/*Yes*/}
                </OwpButton>
                <OwpButton
                  color="secondary"
                  variant="outlined"
                  onClick={this.closeDialogReweighing2}
                >
                  {translationHelper(pageID + '.buttons.dialog_cancel_button')} {/*No*/}
                </OwpButton>
              </React.Fragment>
            }
            width="211px"
            height="100%"
          >
            <React.Fragment>{this.state.popMessage}</React.Fragment>
          </OwpDialog>
          <Formsy>
            <table className="table table-bordered table-advance table-hover tlw">
              <tbody>
                <tr>
                  <th className="left">
                  {translationHelper(pageID + '.fields.upper_code_id')}<span className="c04">*</span> {/*Upper Code ID*/}
                  </th>
                  <td>
                    <OwpSearchTextField
                      id="GROUPID"
                      name="GROUPID"
                      value={this.state.GROUPID}
                      disabled={true}
                    />
                  </td>
                  <th className="left">
                  {translationHelper(pageID + '.fields.upper_code_name')}<span className="c04">*</span> {/*Upper Code Name*/}
                  </th>
                  <td>
                    <OwpSearchTextField
                      id="GROUPNM"
                      name="GROUPNM"
                      value={this.state.GROUPNM}
                      disabled={true}
                    />
                  </td>
                </tr>
                <tr>
                  <th className="left">
                  {translationHelper(pageID + '.fields.sub_code_id')}<span className="c04">*</span> {/*Lower Code ID*/}
                  </th>
                  <td>
                    <OwpSearchTextField
                      id="CODEID"
                      name="CODEID"
                      value={this.state.CODEID}
                      disabled={true}
                    />
                  </td>
                  <th className="left">
                  {translationHelper(pageID + '.fields.sub_code_name')}<span className="c04">*</span> {/*Lower Code Name*/}
                  </th>
                  <td>
                    <OwpSearchTextField
                      id="CODENM"
                      name="CODENM"
                      value={this.state.CODENM}
                    />
                  </td>
                </tr>
                <tr>
                  <th  className="left">{translationHelper(pageID + '.fields.remark')}</th> {/*Remark*/}
                  <td className="left" colSpan={3}>
                    <OwpSearchTextField id="REMARK" name="REMARK" style={{width:'100%', maxHeight: '200px',}} value={this.state.REMARK} />
                  </td>
                </tr>
              </tbody>
            </table>
          </Formsy>
        </React.Fragment>
      </OwpDialog>
    )
  }
}

export default DialogReweighing
