import React, {Component} from 'react';
import {OwpDrawer, OwpPageCarded, OwpToolbar} from "owp/wrapper";
import FuseHighlight from "owp/@fuse/components/FuseHighlight/FuseHighlight";
import Button from "@material-ui/core/Button";

class Page extends Component {

    state = {
        MANUFACTPAPERSEQ:0
    };

    constructor(props) {
        super(props);
        this.owpDrawer = React.createRef();
    }

    onClick = () => {
        this.setState({MANUFACTPAPERSEQ:'1'});
        this.owpDrawer.showDrawer();
    };

    render()
    {
        return (
            <React.Fragment>
                <OwpPageCarded
                    contentToolbar={
                        <OwpToolbar
                            title={"OwpDrawer 사용법"}
                        />
                    }
                    content={
                        <React.Fragment>
                            <FuseHighlight component="pre" className="language-js">{`
import React, {Component} from 'react';
import {OwpDrawer, OwpPageCarded, OwpToolbar} from '../../../@wrapper';
import FuseHighlight from "../../../@fuse/components/FuseHighlight/FuseHighlight";
import Button from "@material-ui/core/Button";

class Page extends Component {

    state = {
        MANUFACTPAPERSEQ:0
    };

    constructor(props) {
        super(props);
        this.owpDrawer = React.createRef();
    }

    onClick = () => {
        this.setState({MANUFACTPAPERSEQ:'1'});
        this.owpDrawer.showDrawer();
    };

    render()
    {
        return (
            <React.Fragment>
                <OwpPageCarded
                    contentToolbar={
                        <OwpToolbar
                            title={"OwpDrawer 사용법"}
                        />
                    }
                    content={
                        <React.Fragment>
                            <OwpToolbar
                                title={"OwpDrawer 예제"}
                            />

                            <OwpDrawer
                                innerRef={ ref => this.owpDrawer = ref }
                                MANUFACTPAPERSEQ={this.state.MANUFACTPAPERSEQ}
                            >
                                <div>CONTENTS</div>
                            </OwpDrawer>
                            <Button variant="outlined" onClick={this.onClick} >상세보기</Button>
                        </React.Fragment>
                    }innerScroll
                />
            </React.Fragment>
        )
    };
}

export default Page;
