                            </FuseHighlight>


                            <OwpToolbar
                                title={"OwpDrawer 예제"}
                            />

                            <OwpDrawer
                                innerRef={ ref => this.owpDrawer = ref }
                                MANUFACTPAPERSEQ={this.state.MANUFACTPAPERSEQ}
                            >
                                <div>CONTENTS</div>
                            </OwpDrawer>
                            <Button variant="outlined" onClick={this.onClick} >상세보기</Button>
                        </React.Fragment>
                    }innerScroll
                />
            </React.Fragment>
        )
    };
}

export default Page;
