import React, { Component } from "react";
import {
    OwpButton,
    OwpSearchDateField,
    OwpSearchSelectField,
    OwpSearchTextField,
    OwpTextField,
    OwpSelectField,
    OwpMessage
} from 'owp/wrapper';
import OwpPresetAutocompleteTextField from "owp/components/Form/OwpPresetAutocompleteTextField";
import { debounce, isEmpty } from "lodash";
import Formsy from "formsy-react";
import * as TGEvent from 'owp/TGEvent';
import moment from "moment";
import { mutate, query } from 'owp/api';
import translationHelper from 'main/content/utils/translationHelper';

const KOREAN_FORMAT_DATE = 'YYYY-MM-DD';
const pageID = 'C202012.tab01';
class Page extends Component {
    constructor(props) {
        super(props);
        this.state = {

        };
    }
    /**
     * Owp에서 사용하는 인풋에 대해 모두 사용할 수 있는 change 이벤트 리시버
     * @param {Event} event 발생한 이벤트 객체
     * @param {Function} callback 콜백 함수. 인풋의 name, value를 인수로 전달함.
     */
    componentDidMount() {
        const _showApprovalDialog = this.showApprovalDialog;
        this.loadData();
        if (this.props.MBRSEQ === "" || this.props.MBRSEQ === null || this.props.MBRSEQ === undefined) {
            OwpMessage({
                message: translationHelper(pageID + '.undefined_mbrseq'),
                variant: "warning",
            });
        } else {
            this.loadData();
        }
    }

    //날짜 디폴드값 설정 함수

    loadData = async () => {
        query({
            // url: "loadOWP_MBRCover/" + this.props.MULTYLANGSEQ,
            url: "loadOWP_MBRCover/1",
            // url: "loadOWP_MBRCover/" + this.props.MBRSEQ,
        }).then(res => {
            for (let key in res) {
                let stateObj = {};
                stateObj[key] = res[key];
                this.setState(stateObj);
            }

            this.setState({ 'OWP_MBR.MBRRECORD': 'Batch' });
        })
        .catch(error =>{
           console.error(error);
        })
    };

    onSelectProductId = (inputValue) => {
        const loadProduct = async () => {
            const data = await query({
                url: `loadOwpMaterial/${inputValue}`
            }).catch(error => {
                console.error(error)
            });

            this.setState({ 'OWP_MBR.MBRNO': `SBR-${data['OWP_MATERIAL.MATERIALCODE']}` });
            this.setState({ 'OWP_MBR.PRODUCTNAME': data['OWP_MATERIAL.MATERIALDECS'] });

            query({
                url: `getMbrVersion/${data['OWP_MATERIAL.MATERIALCODE']}`
            }).then(res => {
                if (res) {
                    this.setState({ 'OWP_MBR.MBRVER': res });
                }
            }).catch(error => {
                console.error(error)
            });
        };

        loadProduct();
    }

