import React, {Component} from 'react';
import {
    OwpDrawer,
    OwpPageCarded, OwpSearchDateField,
    OwpSearchHeader,
    OwpSearchSelectField,
    OwpSearchTextField,
    OwpToolbar,
    OwpTreeGrid
} from "owp/wrapper";

class Page extends Component {

    state = {
        MANUFACTPAPERSEQ:0
    };

    constructor(props) {
        super(props);
        this.owpDrawer = React.createRef();
    }

    componentDidMount() {
        window.Grids.OnClick = function (grid, row, col, x, y) {



                //this.setState({MANUFACTPAPERSEQ:''+row['OWP_MANUFACTPAPER.MANUFACTPAPERSEQ']});

                console.log(row);

                //go(row['OWP_MANUFACTPAPER.MANUFACTPAPERSEQ']);
                //this.owpDrawer.showDrawer();

        }
    }

    onClick = () => {
        this.owpDrawer.showDrawer();
    };

    render()
    {
        return (
            <React.Fragment>
                <OwpPageCarded
                     header={
                        <OwpSearchHeader gridIndex={0} url="listOwpProduct"
                            >
                            <OwpSearchTextField
                                label="품목코드"
                                name="OWP_PRODUCT.PRODUCTCODE"
                            />
                            <OwpSearchSelectField
                                label="품목분류"
                                name="OWP_PRODUCT.ItemKind"
                                groupid="D008000"
                            />
                            <OwpSearchSelectField
                                label="제형구분"
                                name="OWP_PRODUCT.FormulID"
                                groupid="D010000"
                            />
                            <OwpSearchSelectField
                                label="원자재유형"
                                name="OWP_PRODUCT.RawMatTypeID"
                                groupid="D006000"
                            />
                            <OwpSearchSelectField
                                label="제재구분"
                                name="OWP_PRODUCT.PRODUCTKDID"
                                groupid="D005000"
                            />
                            <OwpSearchTextField
                                label="품목명"
                                name="OWP_PRODUCT.PRODUCTNAME"
                            />
                            <OwpSearchSelectField
                                label="품목상태"
                                name="OWP_PRODUCT.ProductStatus"
                                groupid="D016000"
                                value="D016001"
                            />
                            <OwpSearchDateField
                                label="날짜"
                                name="OWP_PRODUCT.DATETEST"
                            />
                        </OwpSearchHeader>
                    }
                    contentToolbar={
                        <React.Fragment>
                            <OwpToolbar
                                title={"OwpTreeGrid 예제"}
                                buttons={['add','excel','print']}
                            />
                            <OwpDrawer
                                //ref={this.owpDrawer}
                                //ref={(owpDrawer) => this.owpDrawer = owpDrawer}
                                innerRef={ ref => this.owpDrawer = ref }
                                MANUFACTPAPERSEQ={this.state.MANUFACTPAPERSEQ}
                            ><div>CONTENTS</div></OwpDrawer>
                        </React.Fragment>
                    }
                    content={
                        <React.Fragment>
                            <OwpTreeGrid
                                id="grid2"
                                LayoutUrl={'/assets/data/owp_601000_02Def.xml'}
                                DataUrl={'/assets/data/owp_601000_02Data.xml'}
                            />
                        </React.Fragment>

                    }innerScroll
                />
            </React.Fragment>
        )
    };
}

export default Page;
//export default withStyles(styles, {withTheme: true})(Page);
