import {
    OwpTextField,
    OwpButton,
    OwpSelectField,
    OwpDialog,
    OwpMessage,
    OwpSearchMonthPicker,
    OwpSearchDateField,
} from 'owp/wrapper';
import React, { Component } from 'react';
import Formsy from 'formsy-react';
import { mutate } from 'owp/api';
import * as TGEvent from 'owp/TGEvent';
import moment from 'moment';
import { isMissingParam } from '../../CommonUtils';
import { query } from 'owp/api';

const FUNCTION_HAS_FORMULA = ['D023020', 'D023005', 'D023006', 'D023007', 'D023008', 'D023012'];

class DialogReweighing extends Component {
    constructor(props) {
        super(props);
        this.owpDialog = React.createRef();
        this.clickCreate = this.clickCreate.bind(this);
        this.onSave = this.onSave.bind(this);
        this.state = {
            isDialogOpen: false,
            isDialogCheckOpen: false,
            popMessage: '저장하시겠습니까',
            DISABLE_FORMULA: true,
            FORMULA: '1',
            LIST_FACILITY: [],
            LIST_MATERIAL: [],
        };
    }

    handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
    };

    handleFunctionChange = (event) => {
        //TODO:reload another
        this.setState({
            [event.target.name]: event.target.value,
            DISABLE_FORMULA: !FUNCTION_HAS_FORMULA.includes(event.target.value),
        });
    };

    closeDialog = () => {
        // this.resetStates();
        this.setState({ isDialogOpen: false });
        this.owpDialog.handleClose();
    };

    // 등록 화면 닫기
    createDialogClose = () => {
        this.setState({ isDialogCheckOpen: false });
        this.owpDialog.handleClose();
    };

    openDialogCheck = () => {
        // if (this.isMissingMandatoryField()) {
        //     OwpMessage({
        //         message: '양식을 작성하십시오',
        //         variant: 'warning',
        //     });
        // } else {
        //     this.setState({ isDialogCheckOpen: true });
        //     this.setState({ popMessage: '저장하시겠습니까?' });
        // }
        this.setState({ isDialogCheckOpen: true });
        this.setState({ popMessage: '저장하시겠습니까?' });
    };

    closeDialogCheck = () => {
        this.setState({ isDialogCheckOpen: false });
    };

    clickCreate() {
        this.onSave('START');
    }

    loadListEquipmentInfo = () => {
        query({
            url: 'listEquimentInfo',
            params: {
                jsondata: JSON.stringify({
                    'OWP_MBR_PO_EQUIPMENT_INFO.MBRSEQ': '5',
                }),
            },
        }).then((res) => {
            let arrFacility = [];
            let setFacility = new Set();
            if (res) {
                res.forEach((item) => {
                    let equipNo = item['OWP_MBR_PO_EQUIPMENT_INFO.EQUIPNO'];
                    if (!setFacility.has(equipNo)) {
                        arrFacility.push({
                            label: equipNo,
                            value: equipNo,
                        });
                    }
                    setFacility.add(equipNo);
                });
                this.setState((state) => {
                    return {
                        ...state,
                        LIST_FACILITY: arrFacility,
                    };
                });
            }
        });
    };

    loadListMaterialSummary = () => {
        query({
            url: 'listMaterialSummary',
            params: {
                jsondata: JSON.stringify({
                    'OWP_MBR_UNIT_PROCESS.MBRSEQ': '5',
                }),
            },
        }).then((res) => {
            let arrMaterial = [];
            let setMaterial = new Set();
            if (res) {
                console.log(res);
                res.forEach((item) => {
                    let materialId = item['OWP_MBR_UNIT_PROCESS.MATERIALID'];
                    if (!setMaterial.has(materialId)) {
                        arrMaterial.push({
                            label: item['OWP_MBR_UNIT_PROCESS.MATERIALDECS'],
                            value: materialId,
                        });
                    }
                    setMaterial.add(materialId);
                });
                this.setState((state) => {
                    return {
                        ...state,
                        LIST_MATERIAL: arrMaterial,
                    };
                });
            }
        });
    };

    onDialogOpen = () => {
        this.loadListEquipmentInfo();
        this.loadListMaterialSummary();
    };

    onSave = async (type) => {
        const data = {
            cudtype: 'create',
            'OWP_MBR_PROCESS_STD.FLAG': 'Y',
            'OWP_MBR_PROCESS_STD.UNITPROCESSSEQ': this.state.UNITPROCESSSEQ
                ? this.state.UNITPROCESSSEQ
                : '',
            'OWP_MBR_PROCESS_STD.MATERIALCODE': this.state.MATERIALCODE
                ? this.state.MATERIALCODE
                : '',
            'OWP_MBR_PROCESS_STD.FACILITYCODE': this.state.FACILITYCODE
                ? this.state.FACILITYCODE
                : '',
            'OWP_MBR_PROCESS_STD.STD_FORMULA': this.state.STD_FORMULA ? this.state.STD_FORMULA : '',
            'OWP_MBR_PROCESS_STD.STD_FUNCTION': this.state.STD_FUNCTION
                ? this.state.STD_FUNCTION
                : '',
            'OWP_MBR_PROCESS_STD.STD_VALUE': this.state.STD_VALUE ? this.state.STD_VALUE : '',
            'OWP_MBR_PROCESS_STD.STD_RATE': this.state.STD_RATE ? this.state.STD_RATE : '',
            'OWP_MBR_PROCESS_STD.EQUIPMENTCODE': this.state.EQUIPMENTCODE
                ? this.state.EQUIPMENTCODE
                : '',
        };

        mutate({
            url: '/createOwpMbrProcessStd',
            data,
        }).then((res) => {
            console.log(res);
            this.closeDialogCheck();
            this.closeDialog();
        });
    };

    render() {
        return (
            <OwpDialog
                title={'추가'}
                callbackFunction={this.onDialogOpen}
                dialogButton="등록"
                dialogActions={
                    <React.Fragment>
                        <div className="w-full center">
                            <OwpButton
                                color="secondary"
                                variant="contained"
                                onClick={this.openDialogCheck}
                            >
                                저장
                            </OwpButton>
                            <OwpButton
                                color="secondary"
                                variant="outlined"
                                onClick={this.closeDialog}
                            >
                                취소
                            </OwpButton>
                        </div>
                    </React.Fragment>
                }
                innerRef={(ref) => (this.owpDialog = ref)}
                width="1100px"
                height="auto"
            >
                <React.Fragment>
                    <OwpDialog
                        onOpen={this.state.isDialogCheckOpen}
                        dialogActions={
                            <React.Fragment>
                                <div className="w-full center">
                                    <OwpButton
                                        color="secondary"
                                        variant="contained"
                                        onClick={this.clickCreate}
                                    >
                                        저장
                                    </OwpButton>
                                    <OwpButton
                                        color="secondary"
                                        variant="outlined"
                                        onClick={this.closeDialogCheck}
                                    >
                                        취소
                                    </OwpButton>
                                </div>
                            </React.Fragment>
                        }
                    >
                        <React.Fragment>{this.state.popMessage}</React.Fragment>
                    </OwpDialog>

                    <Formsy>
                        <div style={{ width: '100%' }}>
                            <table className="table table-bordered table-advance tlw5">
                                <tbody>
                                    <tr>
                                        <th className="center" width="120">
                                            사용 장비
                                        </th>
                                        <td className="center">
                                            <OwpSelectField
                                                id="FACILITYCODE"
                                                name="FACILITYCODE"
                                                className="w-full"
                                                value={this.state.FACILITYCODE}
                                                options={this.state.LIST_FACILITY}
                                                onChange={this.handleChange}
                                                useAll={false}
                                            />
                                        </td>
                                        <th className="center" width="120">
                                            투입 원료
                                        </th>
                                        <th className="center" width="120">
                                            사용 장비
                                        </th>
                                        <td className="center">
                                            <OwpSelectField
                                                id="MATERIALCODE"
                                                name="MATERIALCODE"
                                                className="w-full"
                                                value={this.state.MATERIALCODE}
                                                options={this.state.LIST_MATERIAL}
                                                onChange={this.handleChange}
                                                useAll={false}
                                            />
                                        </td>
                                        <th className="center" width="120">
                                            투입 원료
                                        </th>
                                        <td className="center">
                                            <OwpSelectField
                                                id="STD_FUNCTION"
                                                name="STD_FUNCTION"
                                                className="w-full"
                                                value={this.state.STD_FUNCTION}
                                                groupId={'D023000'}
                                                onChange={this.handleFunctionChange}
                                                useAll={false}
                                            />
                                        </td>
                                        <td className="center">
                                            <OwpSelectField
                                                id="STD_FORMULA"
                                                name="STD_FORMULA"
                                                className="w-full"
                                                onChange={this.handleChange}
                                                options={[
                                                    { label: '+', value: '1' },
                                                    { label: '-', value: '2' },
                                                    { label: '*', value: '3' },
                                                    { label: ':', value: '4' },
                                                ]}
                                                value={this.state.STD_FORMULA}
                                                disabled={this.state.DISABLE_FORMULA}
                                            />
                                        </td>
                                    </tr>
                                    <tr>
                                        <th className="center" width="120">
                                            사용 장비
                                        </th>
                                        <td className="center">
                                            <OwpSelectField
                                                name="STD_RATE"
                                                id="STD_RATE"
                                                className="w-full"
                                                value={this.state.STD_RATE}
                                                options={[
                                                    { label: 'kg/hr', value: '1' },
                                                    { label: 'kg/min', value: '2' },
                                                ]}
                                                onChange={this.handleChange}
                                                useAll={false}
                                            />
                                        </td>
                                        <th className="center" width="120">
                                            투입 원료
                                        </th>
                                        <td className="center">
                                            <OwpSelectField
                                                id="EQUIPMENTCODE"
                                                name="EQUIPMENTCODE"
                                                className="w-full"
                                                value={this.state.EQUIPMENTCODE}
                                                onChange={this.handleChange}
                                                useAll={false}
                                            />
                                        </td>
                                    </tr>
                                </tbody>
                            </table>
                        </div>
                    </Formsy>
                </React.Fragment>
            </OwpDialog>
        );
    }
}

export default DialogReweighing;
