import React, { Component } from 'react';
import {
    OwpPageCarded,
    OwpToolbar,
    OwpTreeGrid,
} from 'owp/wrapper';
import * as TGEvent from 'owp/TGEvent';
import Pop_C806001 from '../../include/Pop_C806001';
import Pop_C806002 from '../../include/Pop_C806002';
import Pop_C806003 from '../../include/Pop_C806003';
import { onGridLoad, restoreGridSelect, saveGridSelect } from 'main/GridUtill';

class Page extends Component {
    constructor(props) {
        super(props);
        this.state = {};
    }

    componentDidMount() {
        
        onGridLoad("C806000", ()=>{
            TGEvent.OWPTGDataList("C806000", "listIpxMultylang", {
                'IPX_MULTYLANG.FLAG' : 'Y'
            })
          })

          TGEvent.OWPTGSelect("C806000", (is, row) => {
              saveGridSelect("C806000","IPX_MULTYLANG.MULTYLANGSEQ")
          if (is) {
              console.log( row["IPX_MULTYLANG.MULTYLANGSEQ"]);
              this.setState({
                MULTYLANGSEQ: row["IPX_MULTYLANG.MULTYLANGSEQ"]
              });
          } else {
              this.setState({
                MULTYLANGSEQ: null
              });
          }
        });  

      }

    callback = () => {
        restoreGridSelect("C806000");
    }

    render() {
        return (
            <React.Fragment>
                <OwpPageCarded
                    
                    contentToolbar={
                        <OwpToolbar title="다국어관리">
                            <Pop_C806001/>
                            {/* <Pop_C0802002/> */}
                            
                            <Pop_C806002
                                MULTYLANGSEQ = {this.state.MULTYLANGSEQ}
                                callback = {this.callback}
                            />
                            <Pop_C806003
                                MULTYLANGSEQ = {this.state.MULTYLANGSEQ}
                            />
                          
                        </OwpToolbar>
                    }
                    content={
                        <React.Fragment>
                            <OwpTreeGrid
                                id="C806000"
                                LayoutUrl={'/assets/data/C806001Def.xml'}
                                // DataUrl={'/assets/data/C0202000Data.xml'}
                            />
                        </React.Fragment>
                    }
                    innerScroll
                />
            </React.Fragment>
        );
    }
}

export default Page;
