import {
    OwpTextField,
    OwpButton,
    OwpSelectField,
    OwpDialog,
    OwpMessage,
    OwpTreeGrid,
    OwpSearchDateTimePickerMulti,
    OwpDateTimePicker,
    OwpTabs,
    OwpCheckBox,
} from 'owp/wrapper';
import React, { Component } from 'react';
import Formsy from 'formsy-react';
import { mutate } from 'owp/api';
import * as TGEvent from 'owp/TGEvent';
// import { OwpDext5Editor } from 'main/OwpDext5.js';
import Pop_C0202002_01 from './Pop_C0202002_01';
import Pop_C0202002_02 from './Pop_C0202002_02';

import DextEditor from "main/custom-component/DextEditor"

class DialogReweighing extends Component {
    constructor(props) {
        super(props);
        this.owpDialog = React.createRef();
        this.clickCreate = this.clickCreate.bind(this);
        this.onSave = this.onSave.bind(this);
        this.state = {
            FLAG: 'Y',
        };
    }

    handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
    };

    // 등록 화면 닫기
    createDialogClose = () => {
        this.setState({ createDialogOpen: false });
        this.owpDialog.handleClose();
    };

    // 등록 확인 화면 열기
    createCheckOpen = () => {
        this.setState({ createCheckOpen: true });
        this.setState({ popMessage: '저장하시겠습니까?' });
        this.onSave('TEMP');
    };

    // 등록 확인 화면 닫기
    createCheckClose = () => {
        this.setState({ createCheckOpen: false });
    };

    onSave = async (type) => {

        let _type = '';

        if(type == 'TEMP'){
            _type = 'D020001';
        }else{
            _type = 'D020002';
        }

        const data = {
            cudtype: 'create',
            "OWP_MBR.MBRSTAUS": _type,
            'OWP_MBR.FACILITY_NAME': document.getElementById('OWP_MBR.FACILITY_NAME').value==null? '': document.getElementById('OWP_MBR.FACILITY_NAME').value,
