import {
    OwpTextField,
    OwpButton,
    OwpSelectField,
    OwpDialog,
    OwpMessage,
    OwpSearchMonthPicker,
    OwpSearchDateField,
} from 'owp/wrapper';
import React, { Component } from 'react';
import Formsy from 'formsy-react';
import { mutate } from 'owp/api';
import * as TGEvent from 'owp/TGEvent';
import moment from 'moment';
import ImageUpload from '../../custom-component/ImageUpload';
import { isMissingParam } from '../../CommonUtils';

const FUNCTION_HAS_FORMULA = ['D024004', 'D024005', 'D024006', 'D024007', 'D024011', 'D023020'];

class DialogReweighing extends Component {
    constructor(props) {
        super(props);
        this.owpDialog = React.createRef();
        this.clickCreate = this.clickCreate.bind(this);
        this.onSave = this.onSave.bind(this);
        this.state = {
            isDialogOpen: false,
            isDialogCheckOpen: false,
            popMessage: '저장하시겠습니까',
            DISABLE_FORMULA: true,
        };
    }

    handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
    };

    handleFunctionChange = (event) => {
        //TODO:reload another
        this.setState({
            [event.target.name]: event.target.value,
            DISABLE_FORMULA: FUNCTION_HAS_FORMULA.includes(event.target.value),
        });
        console.log('-->', FUNCTION_HAS_FORMULA.includes(event.target.value));
    };

    closeDialog = () => {
        // this.resetStates();
        this.setState({ isDialogOpen: false });
        this.owpDialog.handleClose();
    };

    // 등록 화면 닫기
    createDialogClose = () => {
        this.setState({ isDialogCheckOpen: false });
        this.owpDialog.handleClose();
    };

    openDialogCheck = () => {
        if (this.isMissingMandatoryField()) {
            OwpMessage({
                message: '양식을 작성하십시오',
                variant: 'warning',
            });
        } else {
            this.setState({ isDialogCheckOpen: true });
            this.setState({ popMessage: '저장하시겠습니까?' });
        }
    };

    closeDialogCheck = () => {
        this.setState({ isDialogCheckOpen: false });
    };

    clickCreate() {
        this.onSave('START');
    }

    onSave = async (type) => {};

    render() {
        return (
            <OwpDialog
                title={'추가'}
                callbackFunction={this.createDialogOpen}
                dialogButton="등록"
                dialogActions={
                    <React.Fragment>
                        <div className="w-full center">
                            <OwpButton
                                color="secondary"
                                variant="contained"
                                onClick={this.openDialogCheck}
                            >
                                저장
                            </OwpButton>
                            <OwpButton
                                color="secondary"
                                variant="outlined"
                                onClick={this.closeDialog}
                            >
                                취소
                            </OwpButton>
                        </div>
                    </React.Fragment>
                }
                innerRef={(ref) => (this.owpDialog = ref)}
                width="1100px"
                height="auto"
            >
                <React.Fragment>
                    <OwpDialog
                        onOpen={this.state.isDialogCheckOpen}
                        dialogActions={
                            <React.Fragment>
                                <div className="w-full center">
                                    <OwpButton
                                        color="secondary"
                                        variant="contained"
                                        onClick={this.clickCreate}
                                    >
                                        저장
                                    </OwpButton>
                                    <OwpButton
                                        color="secondary"
                                        variant="outlined"
                                        onClick={this.closeDialogCheck}
                                    >
                                        취소
                                    </OwpButton>
                                </div>
                            </React.Fragment>
                        }
                    >
                        <React.Fragment>{this.state.popMessage}</React.Fragment>
                    </OwpDialog>

                    <Formsy>
                        <div style={{ width: '100%' }}>
                            <table className="table table-bordered table-advance tlw5">
                                <tbody>
                                    <tr>
                                        <th className="center" width="120">
                                            사용 장비
                                        </th>
                                        <td className="center">
                                            <OwpSelectField
                                                id="FACILITY"
                                                name="FACILITY"
                                                className="w-full"
                                                // value={this.state.LOCATION_CD}
                                                // groupId={'D027000'}
                                                onChange={this.handleChange}
                                                useAll={false}
                                            />
                                        </td>
                                        <th className="center" width="120">
                                            투입 원료
                                        </th>
                                        <td className="center">
                                            <OwpSelectField
                                                id="FUNCTION"
                                                name="FUNCTION"
                                                className="w-full"
                                                // value={this.state.LOCATION_CD}
                                                groupId={'D023000'}
                                                onChange={this.handleFunctionChange}
                                                useAll={false}
                                            />
                                        </td>
                                        <td className="center">
                                            <OwpSelectField
                                                id="FORMULA"
                                                name="FORMULA"
                                                className="w-full"
                                                // value={this.state.LOCATION_CD}
                                                // groupId={'D023000'}
                                                onChange={this.handleChange}
                                                options={[
                                                    { label: '+', value: '1' },
                                                    { label: '-', value: '2' },
                                                    { label: '*', value: '3' },
                                                    { label: ':', value: '4' },
                                                ]}
                                                value={'1'}
                                                disabled={this.state.DISABLE_FORMULA}
                                            />
                                        </td>
                                        <th className="center" width="120">
                                            투입 예정량
                                        </th>
                                        <td className="center">
                                            <OwpSelectField
                                                id="CONFIG_TYPE"
                                                name="CONFIG_TYPE"
                                                className="w-full"
                                                // value={this.state.LOCATION_CD}
                                                groupId={'D027000'}
                                                onChange={this.handleChange}
                                                useAll={false}
                                            />
                                        </td>
                                    </tr>
                                    <tr>
                                        <th className="center" width="120">
                                            사용 장비
                                        </th>
                                        <td className="center">
                                            <OwpSelectField
                                                id="TOTAL_INPUT"
                                                name="TOTAL_INPUT"
                                                className="w-full"
                                                // value={this.state.LOCATION_CD}
                                                groupId={'D027000'}
                                                onChange={this.handleChange}
                                                useAll={false}
                                            />
                                        </td>
                                        <th className="center" width="120">
                                            투입 원료
                                        </th>
                                        <td className="center">
                                            <OwpSelectField
                                                id="INPUT_SPEED"
                                                name="INPUT_SPEED"
                                                className="w-full"
                                                // value={this.state.LOCATION_CD}
                                                groupId={'D027000'}
                                                onChange={this.handleChange}
                                                useAll={false}
                                            />
                                        </td>
                                        <th className="center" width="120">
                                            투입 예정량
                                        </th>
                                        <td className="center">
                                            <OwpSelectField
                                                id="MEASURING_EQUIPMENT"
                                                name="MEASURING_EQUIPMENT"
                                                className="w-full"
                                                // value={this.state.LOCATION_CD}
                                                groupId={'D027000'}
                                                onChange={this.handleChange}
                                                useAll={false}
                                            />
                                        </td>
                                    </tr>
                                </tbody>
                            </table>
                        </div>
                    </Formsy>
                </React.Fragment>
            </OwpDialog>
        );
    }
}

export default DialogReweighing;
