import {
    OwpTextField,
    OwpButton,
    OwpSelectField,
    OwpDialog,
    OwpMessage,
    OwpTreeGrid,
    OwpSearchDateTimePickerMulti,
    OwpSearchSelectField,
    OwpSearchTextField,
    OwpDateTimePickerMulti,
} from 'owp/wrapper';
import Icon from '@material-ui/core/Icon';
import TextField from '@material-ui/core/TextField';
import InputAdornment from '@material-ui/core/InputAdornment';
import React, { Component } from 'react';
import Formsy from 'formsy-react';
import { mutate, query } from 'owp/api';
import * as TGEvent from 'owp/TGEvent';
import { setGridData, setRowAttribute, restoreGridSelect, setRowAttributes } from 'main/GridUtill';

const GRID_ID = 'C0202010';
const PARENT_GRID_ID = 'C0202009';
class DialogReweighing extends Component {
    constructor(props) {
        super(props);
        this.owpDialog = React.createRef();
        this.state = {
            FLAG: 'Y',
            isDialogCheckOpen: false,
            isDialogOpen: false,
            popMessage: '저장하시겠습니까',
            UNITPROCESSID: 139,
            sopConfig: new Set(),
        };
    }

    componentDidMount() {
        this.loadSopConfig();
        window.TGSetEvent('OnSelect', GRID_ID, function (G, row) {
            console.log(row);
            if (row) {
                if (row['Selected'] === 1) {
                    this.setState((state) => {
                        return {
                            ...state,
                            sopConfig: state.sopConfig.add(row['OWP_SOP.SOPSEQ']),
                        };
                    });
                } else {
                    // this.setState((state) => {
                    //     return {
                    //         ...state,
                    //         sopConfig: state.sopConfig.delete(row['OWP_SOP.SOPSEQ']),
                    //     };
                    // });
                }
            }
        });
    }

    handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
    };

    openDialogCheck = () => {
        this.setState({ isDialogCheckOpen: true });
        this.setState({ popMessage: '저장하시겠습니까?' });
    };

    closeDialogCheck = () => {
        this.setState({ isDialogCheckOpen: false });
    };

    closeDialog = () => {
        // this.resetStates();
        this.setState({ isDialogOpen: false });
        this.clearState();
        this.owpDialog.handleClose();
    };

    clickSearch = () => {
        this.loadGridData();
    };

    clearState = () => {
        this.setState((state) => {
            return {
                ...state,
                sopConfig: new Set(),
            };
        });
    };

    clickReset = () => {};

    loadSopConfig = () => {
        query({
            url: 'listOwpMbrProcessSopByUnitProcess',
            params: {
                jsondata: JSON.stringify({
                    'OWP_MBR_PROCESS_SOP.PROCESSSEQ': this.state.UNITPROCESSID + '',
                }),
            },
        })
            .then((res) => {
                if (res) {
                    let setSopConfig = new Set();
                    res.forEach((item) => {
                        setSopConfig.add(item['OWP_MBR_PROCESS_SOP.SOPSEQ']);
                    });
                    this.setState((state) => {
                        return {
                            ...state,
                            sopConfig: setSopConfig,
                        };
                    });
                }
            })
            .catch((error) => {
                console.error(error);
            });
    };

    loadGridData = () => {
        query({
            url: 'listOwpSopInPopC0202002',
            params: {
                jsondata: JSON.stringify({
                    'OWP_SOP.SOPNUM': this.state['OWP_SOP.SOPNUM']
                        ? this.state['OWP_SOP.SOPNUM'] + ''
                        : '',
                    'OWP_SOP.SOPTITLE_KR': this.state['OWP_SOP.SOPTITLE_KR']
                        ? this.state['OWP_SOP.SOPTITLE_KR'] + ''
                        : '',
                }),
            },
        })
            .then((res) => {
                setGridData(GRID_ID, res);
                this.setSelectedState();
            })
            .catch((error) => {
                console.error(error);
            });
    };

    setSelectedState = () => {
        const G = TGEvent.getGridByID(GRID_ID);
        if (G) {
            const _rows = Object.values(G['Rows']).filter((item) => item.Kind == 'Data');
            _rows.forEach((row) => {
                if (this.state.sopConfig.has(row['OWP_SOP.SOPSEQ'])) {
                    G.SelectRow(row, 1, 0);
                }
            });
        }
    };

    clickUpdate = () => {
        this.onSave('START');
        this.closeDialog();
    };

    onSave = async (type) => {
        const G = TGEvent.getGridByID(GRID_ID);
        if (G) {
            const _rows = G.GetSelRows();
            let arrData = [];
            _rows
                .filter((item) => item.Kind === 'Data')
                .map((row) => {
                    arrData.push({
                        PROCESSSEQ: this.state.UNITPROCESSID + '',
                        SOPSEQ: row['OWP_SOP.SOPSEQ'] + '',
                    });
                });

            const data = {
                cudtype: 'create',
                'OWP_MBR_PROCESS_SOP.FLAG': 'Y',
                'OWP_MBR_PROCESS_SOP.PROCESSSEQ': arrData[0]['PROCESSSEQ'],
                'OWP_MBR_PROCESS_SOP.SOPSEQ': arrData[0]['SOPSEQ'],
            };

            mutate({
                url: '/createOwpMbrProcessSop',
                data,
            }).then((res) => {
                const parentGrid = TGEvent.getGridByID(PARENT_GRID_ID);
                parentGrid.ReloadBody();
                this.closeDialogCheck();
                this.closeDialog();
            });
        }
    };

    render() {
        return (
            <OwpDialog
                title="생산계획 설정"
                callbackFunction={this.createDialogOpen}
                dialogButton="추가"
                dialogActions={
                    <React.Fragment>
                        <div className="w-full center">
                            <OwpButton
                                color="secondary"
                                variant="contained"
                                onClick={this.openDialogCheck}
                            >
                                등록
                            </OwpButton>
                            <OwpButton
                                color="secondary"
                                variant="outlined"
                                onClick={this.closeDialog}
                            >
                                취소
                            </OwpButton>
                        </div>
                    </React.Fragment>
                }
                innerRef={(ref) => (this.owpDialog = ref)}
                width="800px"
                height="auto"
            >
                <React.Fragment>
                    <OwpDialog
                        onOpen={this.state.isDialogCheckOpen}
                        dialogActions={
                            <React.Fragment>
                                <div className="w-full center">
                                    <OwpButton
                                        color="secondary"
                                        variant="contained"
                                        onClick={this.clickUpdate}
                                    >
                                        저장
                                    </OwpButton>
                                    <OwpButton
                                        color="secondary"
                                        variant="outlined"
                                        onClick={this.closeDialogCheck}
                                    >
                                        취소
                                    </OwpButton>
                                </div>
                            </React.Fragment>
                        }
                    >
                        <React.Fragment>{this.state.popMessage}</React.Fragment>
                    </OwpDialog>
                    <Formsy>
                        <div style={{ width: '100%' }}>
                            <div className="row">
                                <div className="col-lg-12 pd0">
                                    <div className="portlet light mgb0" style={{ paddingTop: '0' }}>
                                        <div className="portlet-title" style={{ minHeight: '0' }}>
                                            <div className="caption">
                                                <Icon className="ico_m01 c04">insert_chart</Icon>
                                                <span className="font-dark bold uppercase">
                                                    {' '}
                                                    작업표준관리(SOP) 조회{' '}
                                                </span>
                                                <span className="caption-helper"> </span>
                                            </div>
                                            <div className="flex justify-end">
                                                <OwpButton
                                                    color="secondary"
                                                    variant="contained"
                                                    onClick={this.clickSearch}
                                                >
                                                    검색
                                                </OwpButton>
                                                <OwpButton
                                                    color="secondary"
                                                    variant="outlined"
                                                    onClick={this.clickReset}
                                                >
                                                    초기화
                                                </OwpButton>
                                            </div>
                                        </div>
                                        <table className="table table-bordered table-advance tlw5">
                                            <tbody>
                                                <tr>
                                                    <th className="left" width="150">
                                                        분류 · SOP명
                                                    </th>
                                                    <td className="left">
                                                        <OwpTextField
                                                            onChange={this.handleChange}
                                                            id="OWP_SOP.SOPNUM"
                                                            name="OWP_SOP.SOPNUM"
                                                            style={{ width: '100%' }}
                                                        />
                                                    </td>
                                                    <th className="left" width="150">
                                                        문서번호
                                                    </th>
                                                    <td className="left">
                                                        <OwpTextField
                                                            onChange={this.handleChange}
                                                            id="OWP_SOP.SOPTITLE_KR"
                                                            name="OWP_SOP.SOPTITLE_KR"
                                                            style={{ width: '100%' }}
                                                        />
                                                    </td>
                                                </tr>
                                            </tbody>
                                        </table>
                                    </div>
                                </div>
                            </div>

                            <div className="row">
                                <div className="col-lg-12 pd0">
                                    <div className="portlet light mgb0" style={{ paddingTop: '0' }}>
                                        <div className="portlet-title" style={{ minHeight: '0' }}>
                                            <div className="caption">
                                                <Icon className="ico_m01 c04">insert_chart</Icon>
                                                <span className="font-dark bold uppercase">
                                                    {' '}
                                                    작업표준관리(SOP) 설정{' '}
                                                </span>
                                                <span className="caption-helper"> </span>
                                            </div>
                                            <div className="flex justify-end"></div>
                                        </div>
                                        <OwpTreeGrid
                                            id="C0202010"
                                            LayoutUrl={'/assets/data/C0202010Def.xml'}
                                            width="100%"
                                            height="30vh"
                                        />
                                    </div>
                                </div>
                            </div>
                        </div>
                    </Formsy>
                </React.Fragment>
            </OwpDialog>
        );
    }
}

export default DialogReweighing;
