import {
    OwpTextField,
    OwpButton,
    OwpSelectField,
    OwpDialog,
    OwpMessage,
    OwpTreeGrid,
    OwpSearchDateTimePickerMulti,
    OwpSearchSelectField,
    OwpSearchTextField,
} from 'owp/wrapper';
import React, { Component } from 'react';
import Formsy from 'formsy-react';
import { mutate } from 'owp/api';
import * as TGEvent from 'owp/TGEvent';

class DialogReweighing extends Component {
    constructor(props) {
        super(props);
        this.owpDialog = React.createRef();
        this.clickCreate = this.clickCreate.bind(this);
        this.onSave = this.onSave.bind(this);
        this.state = {
            FLAG: 'Y',
        };
    }

    handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
    };

    

    // 등록 화면 닫기
    createDialogClose = () => {
        this.setState({ createDialogOpen: false });
        this.owpDialog.handleClose();
    };

    // 등록 확인 화면 열기
    createCheckOpen = () => {

        this.setState({ createCheckOpen: true });
        this.setState({ popMessage: '저장하시겠습니까?' });
    };

    // 등록 확인 화면 닫기
    createCheckClose = () => {
        this.setState({ createCheckOpen: false });
    };

    onSave = async (type) => {
        const data = {
            cudtype: 'create',
            
        };

        const result = await mutate({
            url: '/createIpxCommoncode',
            data,
        })
            
    };

    clickCreate() {
        this.onSave('START');
    }

    render() {
        return (
            <OwpDialog
                title="보호구등록"
                callbackFunction={this.createDialogOpen}
                dialogButton="등록"
                dialogActions={
                    <React.Fragment>
                        <div className="w-full center">
                            <OwpButton color="secondary" variant="contained" onClick={this.createCheckOpen}>
                                저장
                            </OwpButton>
                            <OwpButton color="secondary" variant="outlined" onClick={this.createDialogClose}>
                                취소
                            </OwpButton>
                        </div>
                    </React.Fragment>
                }
                innerRef={(ref) => (this.owpDialog = ref)}
                width="1100px"
                height="auto"
            >
                <React.Fragment>
                    <Formsy>
                        <div style={{width:'100%'}}>
                            <table className="table table-bordered table-advance tlw5">
                                <tbody>
                                <tr>
                                    <th className="left" width="120">보호구코드</th>
                                    <td className="left">
                                        <OwpSelectField
                                            className="w-full"
                                            id="b"
                                            name="b"
                                            options={[
                                                { label: '설비/장비명1', value: '1' },
                                                { label: '설비/장비명1', value: '2' },
                                            ]}
                                            value={'1'}
                                        />
                                    </td>
                                    <th className="left" width="120">보호구이름</th>
                                    <td className="left">
                                        <OwpTextField
                                            style={{width:'100%'}}
                                            name="c"
                                        />
                                    </td>
                                    <th className="left" width="120">보호구구분</th>
                                    <td className="left">
                                        <OwpSelectField
                                            className="w-full"
                                            id="b"
                                            name="b"
                                            options={[
                                                { label: '구분1', value: '1' },
                                                { label: '구분2', value: '2' },
                                            ]}
                                            value={'1'}
                                        />
                                    </td>
                                </tr>
                                <tr>
                                    <th className="left">제조사</th>
                                    <td className="left">
                                        <OwpTextField
                                            style={{width:'100%'}}
                                            name="c"
                                        />
                                    </td>
                                    <th className="left">유행성</th>
                                    <td className="left">
                                        <OwpTextField
                                            style={{width:'100%'}}
                                            name="c"
                                        />
                                    </td>
                                    <th className="left">노출기준</th>
                                    <td className="left">
                                        <OwpTextField
                                            style={{width:'100%'}}
                                            name="c"
                                        />
                                    </td>
                                </tr>
                                <tr>
                                    <th className="left">비고</th>
                                    <td className="left" colspan="3">
                                        <textarea
                                            className="t_textarea"
                                            style={{height:'150px'}}
                                        />
                                    </td>
                                    <th className="left">이미지</th>
                                    <td className="left">
                                        
                                    </td>
                                </tr>
                                </tbody>
                            </table>
                        </div>
                    </Formsy>
                </React.Fragment>
            </OwpDialog>
        );
    }
}

export default DialogReweighing;
