import React, { Component } from "react"
import {
    OwpButton,
    OwpDialog, OwpMessage, OwpSession
} from 'owp/wrapper';
import { mutate, query } from  'owp/api';
import { getGridByID } from "owp/TGEvent"
import * as TGEvent from 'owp/TGEvent';
import axios from "axios"
import Formsy from "formsy-react"
import translationHelper from 'main/content/utils/translationHelper';
import Pop_C0202002 from './Pop_C0202002';
const pageID = 'C404000';

class DialogReweighing extends Component {
  constructor(props) {
    super(props)
    this.Pop_C0202002 = React.createRef();
  
    this.state = {
      stateDialog: false,
      dialogData: ""
    }
  }

  componentWillMount() {
    // that = this;
    // window["show"] = () => { console.log(that.state) }
  }


  onChange = e => {
    this.setState({ file: e.target.files[0] })
    this.setState({ FILENAMEREAL: e.target.files[0].name })
  }

  // 창닫기
  closeDialogReweighing = () => {
    this.setState({
      stateDialog: false
    })
    this.owpDialog.handleClose()
  }

  // 팝업 입력란 데이터 변경 함수.
  handleChange2 = event => {
    this.setState({ [event.target.name]: event.target.value })
  }

  // 저장시 돌아가는 함수
  clickUpdate = () => {
    if (this.checkFileExtenstion()) {
      this.fileUpload("START")
    } else {
      OwpMessage({
        message:translationHelper(pageID + '.message.please_check_file'),
        variant: "warning"
      })
    }
  }


  checkFileExtenstion = () => {
    let allowExts = ["hwp", "doc", "xls", "xlsx", "ppt", "pptx", "pdf"]
    let file = this.state.file
    if (!!file) {
      let ext = file.name.substr(file.name.lastIndexOf(".")).replace(".", "")

      return allowExts.find(allowExt => allowExt == ext) ? true : false
    } else {
      return false
    }
  }
  callback = () => {
    this.closeDialogReweighing()
}
  // 파일 저장
  fileUpload = async type => {
    const formData = new FormData()
    formData.append("file", this.state.file)
    formData.append("cudtype", "UPDATE")
    // formData.append("WUSERSEQ", OwpSession("USERSEQ")) 
    formData.append("WUSERSEQ","3") 
// 
// this.Pop_C0202002.openDialog_Register();
    try {
      await axios({
        method: "post",
        url: "/uploadExcelMBR",
        config: {
          headers: {
            "Content-Type": "multipart/form-data"
          }
        },
        data: formData
      })
        .then(res => {
          if (res.data.resultCode === "STATUS_1") {
            const G = TGEvent.getGridByID("C0202000")
            G.ReloadBody()
            // this.closeDialogReweighing()
            this.setState({
              dialogData: res.data.MBRSEQ
            })
            this.Pop_C0202002.openDialog_Register();
          }else{
            
            OwpMessage({
              message: "Upload Excel fail.",
              variant: "warning"
            })
            this.closeDialogReweighing()
          }
        })
        .catch(error => console.error(error))
    
    } catch (error) {
      console.error(error)
    }
  }

 

  render() {
    const FILEPATH =
    process.env.REACT_APP_REST_API_URL +
    "/downloadNFile/template/Template_MBR.xlsx"

    return (
      <OwpDialog
        onOpen={this.state.stateDialog}
        dialogButton='등록'
        dialogActions={
          <React.Fragment>
            <OwpButton onClick={this.clickUpdate} variant="contained" color="primary">
            {translationHelper(pageID +'.buttons.dialog_save_button')}
            </OwpButton>
            <OwpButton onClick={this.closeDialogReweighing} variant="contained" color="primary">
            {translationHelper(pageID +'.buttons.dialog_close_button')}
            </OwpButton>
          </React.Fragment>
        }
        innerRef={ref => (this.owpDialog = ref)}
        width="600px"
      >
        <React.Fragment>
                          <div  style={{ display: "none" }}>
                            <Pop_C0202002
                                dialogData={this.state.dialogData}
                                callback = {this.callback}
                                innerRef={(ref) => (this.Pop_C0202002 = ref)}
                            />
                            </div>
          <Formsy>
            <table className="table table-bordered table-advance table-hover tlw">
              <tbody>
                <tr>
                  {/* <th className="bg_th">성적서 No.</th> */}
                  <th className="bg_th">첨부파일</th>
                </tr>
                <tr>
                  <td>
                    <input
                      type="file"
                      accept=".hwp,.doc,.xls,.xlsx,.ppt,.pptx"
                      onChange={this.onChange}
                    />
                  </td>
                </tr>
                <tr>
                  <td>
                    <a href={FILEPATH}>Template Excel file.</a>
                  </td>
                </tr>
              </tbody>
            </table>
          </Formsy>
        </React.Fragment>
      </OwpDialog>
    )
  }
}

export default DialogReweighing
