import {
    OwpTextField,
    OwpButton,
    OwpSelectField,
    OwpDialog,
    OwpMessage,
    OwpTreeGrid,
    OwpSearchDateTimePickerMulti,
    OwpSearchSelectField,
    OwpSearchTextField,
    OwpDateTimePickerMulti,
} from 'owp/wrapper';
import Icon from '@material-ui/core/Icon';
import TextField from '@material-ui/core/TextField';
import InputAdornment from '@material-ui/core/InputAdornment';
import React, { Component } from 'react';
import Formsy from 'formsy-react';
import { mutate, query } from 'owp/api';
import * as TGEvent from 'owp/TGEvent';
import { setGridData, setRowAttribute, restoreGridSelect, setRowAttributes } from 'main/GridUtill';

const GRID_ID = 'C0202010';
class DialogReweighing extends Component {
    constructor(props) {
        super(props);
        this.owpDialog = React.createRef();
        this.clickCreate = this.clickCreate.bind(this);
        this.onSave = this.onSave.bind(this);
        this.state = {
            FLAG: 'Y',
        };
    }

    handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
    };

    // 등록 화면 닫기
    createDialogClose = () => {
        this.setState({ createDialogOpen: false });
        this.owpDialog.handleClose();
    };

    // 등록 확인 화면 열기
    createCheckOpen = () => {
        this.setState({ createCheckOpen: true });
        this.setState({ popMessage: '저장하시겠습니까?' });
    };

    // 등록 확인 화면 닫기
    createCheckClose = () => {
        this.setState({ createCheckOpen: false });
    };

    onSave = async (type) => {
        const data = {
            cudtype: 'create',
        };

        const result = await mutate({
            url: '/createIpxCommoncode',
            data,
        });
    };

    clickCreate() {
        this.onSave('START');
    }

    clickSearch = () => {
        console.log('clickSearch');
        this.loadGridData();
    };

    clickReset = () => {
        console.log('clickReset');
    };

    loadGridData = () => {
        console.log(this.state);
        query({
            url: 'listOwpSop',
            params: {
                jsondata: JSON.stringify({
                    'OWP_SOP.SOPNUM': this.state['OWP_SOP.SOPNUM']
                        ? this.state['OWP_SOP.SOPNUM'] + ''
                        : '',
                    'OWP_SOP.SOPTITLE_KR': this.state['OWP_SOP.SOPTITLE_KR']
                        ? this.state['OWP_SOP.SOPTITLE_KR'] + ''
                        : '',
                }),
            },
        })
            .then((res) => {
                console.log(res);
                let G = TGEvent.getGridByID(GRID_ID);
                setGridData(GRID_ID, res);
                // this.setState({
                //     FACILITY_NAME: inputValue,
                //     LOCATION_CD: res[0]['OWP_FACILITIES.LOCATION_CD'],
                //     FACILITY_ID: res[0]['OWP_FACILITIES.FACILITY_ID'],
                // });
            })
            .catch((error) => {
                console.error(error);
            });
    };

    render() {
        return (
            <OwpDialog
                title="생산계획 설정"
                callbackFunction={this.createDialogOpen}
                dialogButton="추가"
                dialogActions={
                    <React.Fragment>
                        <div className="w-full center">
                            <OwpButton
                                color="secondary"
                                variant="contained"
                                onClick={this.createCheckOpen}
                            >
                                등록
                            </OwpButton>
                            <OwpButton
                                color="secondary"
                                variant="outlined"
                                onClick={this.createDialogClose}
                            >
                                취소
                            </OwpButton>
                        </div>
                    </React.Fragment>
                }
                innerRef={(ref) => (this.owpDialog = ref)}
                width="800px"
                height="auto"
            >
                <React.Fragment>
                    <Formsy>
                        <div style={{ width: '100%' }}>
                            <div className="row">
                                <div className="col-lg-12 pd0">
                                    <div className="portlet light mgb0" style={{ paddingTop: '0' }}>
                                        <div className="portlet-title" style={{ minHeight: '0' }}>
                                            <div className="caption">
                                                <Icon className="ico_m01 c04">insert_chart</Icon>
                                                <span className="font-dark bold uppercase">
                                                    {' '}
                                                    작업표준관리(SOP) 조회{' '}
                                                </span>
                                                <span className="caption-helper"> </span>
                                            </div>
                                            <div className="flex justify-end">
                                                <OwpButton
                                                    color="secondary"
                                                    variant="contained"
                                                    onClick={this.clickSearch()}
                                                >
                                                    검색
                                                </OwpButton>
                                                <OwpButton
                                                    color="secondary"
                                                    variant="outlined"
                                                    onClick={this.clickReset()}
                                                >
                                                    초기화
                                                </OwpButton>
                                            </div>
                                        </div>
                                        <table className="table table-bordered table-advance tlw5">
                                            <tbody>
                                                <tr>
                                                    <th className="left" width="150">
                                                        분류 · SOP명
                                                    </th>
                                                    <td className="left">
                                                        <OwpTextField
                                                            onChange={this.handleChange}
                                                            id="OWP_SOP.SOPNUM"
                                                            name="OWP_SOP.SOPNUM"
                                                            style={{ width: '100%' }}
                                                        />
                                                    </td>
                                                    <th className="left" width="150">
                                                        문서번호
                                                    </th>
                                                    <td className="left">
                                                        <OwpTextField
                                                            onChange={this.handleChange}
                                                            id="OWP_SOP.SOPTITLE_KR"
                                                            name="OWP_SOP.SOPTITLE_KR"
                                                            style={{ width: '100%' }}
                                                        />
                                                    </td>
                                                </tr>
                                            </tbody>
                                        </table>
                                    </div>
                                </div>
                            </div>

                            <div className="row">
                                <div className="col-lg-12 pd0">
                                    <div className="portlet light mgb0" style={{ paddingTop: '0' }}>
                                        <div className="portlet-title" style={{ minHeight: '0' }}>
                                            <div className="caption">
                                                <Icon className="ico_m01 c04">insert_chart</Icon>
                                                <span className="font-dark bold uppercase">
                                                    {' '}
                                                    작업표준관리(SOP) 설정{' '}
                                                </span>
                                                <span className="caption-helper"> </span>
                                            </div>
                                            <div className="flex justify-end"></div>
                                        </div>
                                        <OwpTreeGrid
                                            id="C0202010"
                                            LayoutUrl={'/assets/data/C0202010Def.xml'}
                                            DataUrl={'/assets/data/C0202000Data.xml'}
                                            width="100%"
                                            height="30vh"
                                        />
                                    </div>
                                </div>
                            </div>
                        </div>
                    </Formsy>
                </React.Fragment>
            </OwpDialog>
        );
    }
}

export default DialogReweighing;
