import {
    OwpTextField,
    OwpButton,
    OwpSelectField,
    OwpDialog,
    OwpMessage,
    OwpSearchMonthPicker,
    OwpSearchDateField,
} from 'owp/wrapper';
import React, { Component } from 'react';
import Formsy from 'formsy-react';
import { mutate } from 'owp/api';
import * as TGEvent from 'owp/TGEvent';
import moment from 'moment';
import ImageUpload from '../../custom-component/ImageUpload';
import { isMissingParam } from '../../CommonUtils';

class DialogReweighing extends Component {
    constructor(props) {
        super(props);
        this.owpDialog = React.createRef();
        this.clickCreate = this.clickCreate.bind(this);
        this.onSave = this.onSave.bind(this);
        this.state = {
            isDialogOpen: false,
            isDialogCheckOpen: false,
            popMessage: '저장하시겠습니까',
            FACILITY_IMAGE: [],
            FLOOR_PLAN_FILESEQ: [],
        };
    }

    handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
    };

    handleMultiSelectChange = (event) => {
        let sortedCode = this.sortCommonCode(event.target.value);
        this.setState({ [event.target.name]: sortedCode });
    };

    sortCommonCode = (commonCodes) => {
        if (commonCodes) {
            let arrCode = commonCodes.split(',');
            arrCode.sort();
            return arrCode.join(',');
        }
        return commonCodes;
    };

    closeDialog = () => {
        this.resetStates();
        this.setState({ isDialogOpen: false });
        this.owpDialog.handleClose();
    };

    resetStates = () => {
        this.setState((state) => {
            return {
                FACILITY_ID: '',
                FACILITY_NAME: '',
                LEVEL_CD: '',
                LOCATION_CD: '',
                FACILITY_COMMENT: '',
                FACILITY_MT_FREQUENCY: '',

                FACILITY_METHOD: '',
                FACILITY_SIZE: '',
                FACILITY_MANUFACTURE: '',
                FACILITY_MANUF_COUNTRY: '',
                FACILITY_YEAR_MONTH: '',
                FACILITY_MODEL_NAME: '',
                FACILITY_SERIAL_NO: '',
                FACILITY_CONTACT_POINT: '',
                FACILITY_ASSET_NO: '',
                FACILITY_PURCHASE_AMT: '',
                FACILITY_PURCHASE_DATE: '',
                FACILITY_CAPACITY: '',
                FACILITY_TYPE: '',
                FACILITY_MATERIAL: '',
                FACILITY_POWER: '',
                FACILITY_CAPACITY_KW: '',
                FACILITY_CHECK_CYCLE: '',
                FACILITY_IMAGE: [],
                FLOOR_PLAN_FILESEQ: [],
            };
        });
    };

    // 등록 화면 닫기
    createDialogClose = () => {
        this.setState({ isDialogCheckOpen: false });
        this.owpDialog.handleClose();
    };

    openDialogCheck = () => {
        if (this.isMissingMandatoryField()) {
            OwpMessage({
                message: '양식을 작성하십시오',
                variant: 'warning',
            });
        } else {
            this.setState({ isDialogCheckOpen: true });
            this.setState({ popMessage: '저장하시겠습니까?' });
        }
    };

    isMissingMandatoryField = () => {
        if (isMissingParam(this.state.FACILITY_NAME) || isMissingParam(this.state.FACILITY_ID)) {
            return true;
        }
        return false;
    };

    onUploadImages = (image) => {
        this.setState((state) => {
            return { FACILITY_IMAGE: [...state.FACILITY_IMAGE, image] };
        });
    };

    onDeleteImage = (image) => {
        if (image) {
            this.setState((state) => {
                return {
                    ...state,
                    FACILITY_IMAGE: state.FACILITY_IMAGE.filter(
                        (item) => item.fileSeq != image.fileSeq
                    ),
                };
            });
        }
    };

    closeDialogCheck = () => {
        this.setState({ isDialogCheckOpen: false });
    };

    clickCreate() {
        this.onSave('START');
    }

    onSave = async (type) => {};

    render() {
        return (
            <OwpDialog
                title={'test here'}
                callbackFunction={this.createDialogOpen}
                dialogButton="등록"
                dialogActions={
                    <React.Fragment>
                        <div className="w-full center">
                            <OwpButton
                                color="secondary"
                                variant="contained"
                                onClick={this.openDialogCheck}
                            >
                                저장
                            </OwpButton>
                            <OwpButton
                                color="secondary"
                                variant="outlined"
                                onClick={this.closeDialog}
                            >
                                취소
                            </OwpButton>
                        </div>
                    </React.Fragment>
                }
                innerRef={(ref) => (this.owpDialog = ref)}
                width="1100px"
                height="auto"
            >
                <React.Fragment>
                    <OwpDialog
                        onOpen={this.state.isDialogCheckOpen}
                        dialogActions={
                            <React.Fragment>
                                <div className="w-full center">
                                    <OwpButton
                                        color="secondary"
                                        variant="contained"
                                        onClick={this.clickCreate}
                                    >
                                        저장
                                    </OwpButton>
                                    <OwpButton
                                        color="secondary"
                                        variant="outlined"
                                        onClick={this.closeDialogCheck}
                                    >
                                        취소
                                    </OwpButton>
                                </div>
                            </React.Fragment>
                        }
                    >
                        <React.Fragment>{this.state.popMessage}</React.Fragment>
                    </OwpDialog>

                    <Formsy>
                        <div style={{ width: '100%' }}>
                            <table className="table table-bordered table-advance tlw5">
                                <tbody>
                                    <tr>
                                        <th className="center" width="120">
                                            설비/장비명<span className="c04">*</span>
                                        </th>
                                        <td className="center">
                                            <OwpTextField
                                                className="w-full"
                                                id="FACILITY_NAME"
                                                name="FACILITY_NAME"
                                                value={this.state.FACILITY_NAME}
                                                onChange={this.handleChange}
                                            />
                                        </td>
                                        <th className="center" width="120">
                                            설비번호<span className="c04">*</span>
                                        </th>
                                        <td className="center">
                                            <OwpTextField
                                                style={{ width: '100%' }}
                                                id="FACILITY_ID"
                                                name="FACILITY_ID"
                                                value={this.state.FACILITY_ID}
                                                onChange={this.handleChange}
                                            />
                                        </td>
                                        <th className="center" width="120">
                                            설치장소
                                        </th>
                                        <td className="center">
                                            <OwpSelectField
                                                className="w-full"
                                                id="LOCATION_CD"
                                                name="LOCATION_CD"
                                                value={this.state.LOCATION_CD}
                                                groupId={'D027000'}
                                                onChange={this.handleChange}
                                                useAll={false}
                                            />
                                        </td>
                                    </tr>
                                    <tr>
                                        <th className="center">크기</th>
                                        <td className="center">
                                            <OwpTextField
                                                style={{ width: '100%' }}
                                                id="FACILITY_SIZE"
                                                name="FACILITY_SIZE"
                                                value={this.state.FACILITY_SIZE}
                                                onChange={this.handleChange}
                                            />
                                        </td>
                                        <th className="center">제조사</th>
                                        <td className="center">
                                            <OwpTextField
                                                style={{ width: '100%' }}
                                                id="FACILITY_MANUFACTURE"
                                                name="FACILITY_MANUFACTURE"
                                                value={this.state.FACILITY_MANUFACTURE}
                                                onChange={this.handleChange}
                                            />
                                        </td>
                                        <th className="center">제조국</th>
                                        <td className="center">
                                            <OwpTextField
                                                style={{ width: '100%' }}
                                                id="FACILITY_MANUF_COUNTRY"
                                                name="FACILITY_MANUF_COUNTRY "
                                                value={this.state.FACILITY_MANUF_COUNTRY}
                                                onChange={this.handleChange}
                                            />
                                        </td>
                                    </tr>
                                    <tr>
                                        <th className="center">제작연도/월</th>
                                        <td className="center">
                                            <OwpSearchMonthPicker
                                                label={''}
                                                id="FACILITY_YEAR_MONTH"
                                                name="FACILITY_YEAR_MONTH"
                                                onChange={this.handleChange}
                                                value={this.state.FACILITY_YEAR_MONTH}
                                                initNow={true}
                                            />
                                            {/* <OwpSearchDateField
                                                type="month"
                                                name="FACILITY_YEAR_MONTH"
                                                id="FACILITY_YEAR_MONTH"
                                                value={this.state.FACILITY_YEAR_MONTH}
                                                onChange={this.handleChange}
                                                initNow={true}
                                            /> */}
                                        </td>
                                        <th className="center">모델명</th>
                                        <td className="center">
                                            <OwpTextField
                                                style={{ width: '100%' }}
                                                id="FACILITY_MODEL_NAME"
                                                name="FACILITY_MODEL_NAME"
                                                value={this.state.FACILITY_MODEL_NAME}
                                                setValue={this.setMonthPickerValue}
                                                onChange={this.handleChange}
                                            />
                                        </td>
                                        <th className="center">일련번호</th>
                                        <td className="center">
                                            <OwpTextField
                                                style={{ width: '100%' }}
                                                id="FACILITY_SERIAL_NO"
                                                name="FACILITY_SERIAL_NO"
                                                value={this.state.FACILITY_SERIAL_NO}
                                                onChange={this.handleChange}
                                            />
                                        </td>
                                    </tr>
                                    <tr>
                                        <th className="center">연락처</th>
                                        <td className="center">
                                            <OwpTextField
                                                style={{ width: '100%' }}
                                                id="FACILITY_CONTACT_POINT"
                                                name="FACILITY_CONTACT_POINT"
                                                value={this.state.FACILITY_CONTACT_POINT}
                                                onChange={this.handleChange}
                                            />
                                        </td>
                                        <th className="center">자산번호</th>
                                        <td className="center">
                                            <OwpTextField
                                                style={{ width: '100%' }}
                                                id="FACILITY_ASSET_NO"
                                                name="FACILITY_ASSET_NO"
                                                value={this.state.FACILITY_ASSET_NO}
                                                onChange={this.handleChange}
                                            />
                                        </td>
                                        <th className="center">취득가액</th>
                                        <td className="center">
                                            <OwpTextField
                                                style={{ width: '100%' }}
                                                id="FACILITY_PURCHASE_AMT"
                                                name="FACILITY_PURCHASE_AMT"
                                                value={this.state.FACILITY_PURCHASE_AMT}
                                                onChange={this.handleChange}
                                            />
                                        </td>
                                    </tr>
                                    <tr>
                                        <th className="center">취득일</th>
                                        <td className="center">
                                            <OwpSearchDateField
                                                style={{ width: '100%' }}
                                                id="FACILITY_PURCHASE_DATE"
                                                name="FACILITY_PURCHASE_DATE"
                                                inputProps={{ max: '9999-12-31' }}
                                                // value={this.state.FACILITY_PURCHASE_DATE}
                                                onChange={this.handleChange}
                                                initNow={true}
                                            />
                                        </td>
                                        <th className="center">비고</th>
                                        <td className="center">
                                            <OwpTextField
                                                style={{ width: '100%' }}
                                                name="FACILITY_COMMENT"
                                                id="FACILITY_COMMENT"
                                                value={this.state.FACILITY_COMMENT}
                                                onChange={this.handleChange}
                                            />
                                        </td>
                                        <th className="center">이미지</th>
                                        <td className="center">
                                            <ImageUpload
                                                imageValue={this.state.FACILITY_IMAGE}
                                                onUploadSuccess={this.onUploadImages}
                                                onDeleteImage={this.onDeleteImage}
                                                isImage={true}
                                            ></ImageUpload>
                                        </td>
                                    </tr>
                                    <tr>
                                        <th className="center">용량</th>
                                        <td className="center">
                                            <OwpTextField
                                                style={{ width: '100%' }}
                                                name="FACILITY_CAPACITY"
                                                id="FACILITY_CAPACITY"
                                                value={this.state.FACILITY_CAPACITY}
                                                onChange={this.handleChange}
                                            />
                                        </td>

                                        <th className="center">타입(형식)</th>
                                        <td className="center">
                                            <OwpTextField
                                                style={{ width: '100%' }}
                                                name="FACILITY_TYPE"
                                                id="FACILITY_TYPE"
                                                value={this.state.FACILITY_TYPE}
                                                onChange={this.handleChange}
                                            />
                                        </td>
                                        <th className="center">재질</th>
                                        <td className="center">
                                            <OwpTextField
                                                style={{ width: '100%' }}
                                                name="FACILITY_MATERIAL"
                                                id="FACILITY_MATERIAL"
                                                value={this.state.FACILITY_MATERIAL}
                                                onChange={this.handleChange}
                                            />
                                        </td>
                                    </tr>
                                    <tr>
                                        <th className="center">전원</th>
                                        <td className="center">
                                            <OwpTextField
                                                style={{ width: '100%' }}
                                                name="FACILITY_POWER"
                                                id="FACILITY_POWER"
                                                value={this.state.FACILITY_POWER}
                                                onChange={this.handleChange}
                                            />
                                        </td>

                                        <th className="center">용량</th>
                                        <td className="center">
                                            <OwpTextField
                                                style={{ width: '100%' }}
                                                name="FACILITY_CAPACITY_KW"
                                                id="FACILITY_CAPACITY_KW"
                                                value={this.state.FACILITY_CAPACITY_KW}
                                                onChange={this.handleChange}
                                            />
                                        </td>
                                        <th className="center">도면</th>
                                        <td className="center">
                                            <ImageUpload
                                                imageValue={this.state.FLOOR_PLAN_FILESEQ}
                                                onUploadSuccess={this.onUploadFloorPlan}
                                                onDeleteImage={this.onDeleteFloorPlan}
                                                isImage={false}
                                            ></ImageUpload>
                                        </td>
                                    </tr>

                                    <tr>
                                        <th className="center">설비등급</th>
                                        <td className="center">
                                            <OwpSelectField
                                                className="w-full"
                                                id="LEVEL_CD"
                                                name="LEVEL_CD"
                                                value={this.state.LEVEL_CD}
                                                groupId={'D801200'}
                                                onChange={this.handleChange}
                                                useAll={false}
                                            />
                                        </td>

                                        <th className="center">점검주기</th>
                                        <td className="center">
                                            <OwpSelectField
                                                className="w-full"
                                                id="FACILITY_CHECK_CYCLE"
                                                name="FACILITY_CHECK_CYCLE"
                                                value={this.state.FACILITY_CHECK_CYCLE}
                                                groupId={'D007000'}
                                                onChange={this.handleMultiSelectChange}
                                                useAll={false}
                                                isMulti={true}
                                            />
                                        </td>
                                        <th className="center">보전방식</th>
                                        <td className="center">
                                            <OwpTextField
                                                style={{ width: '100%' }}
                                                name="FACILITY_METHOD"
                                                id="FACILITY_METHOD"
                                                value={this.state.FACILITY_METHOD}
                                                onChange={this.handleChange}
                                            />
                                        </td>
                                    </tr>
                                </tbody>
                            </table>
                        </div>
                    </Formsy>
                </React.Fragment>
            </OwpDialog>
        );
    }
}

export default DialogReweighing;
