import { OwpButton, OwpDialog, OwpSelectField, OwpTextField } from 'owp/wrapper';
import React, { Component } from 'react';
import Formsy from 'formsy-react';
import { mutate } from 'owp/api';
import * as TGEvent from 'owp/TGEvent';
import translationHelper from 'main/content/utils/translationHelper';

class DialogReweighing extends Component {
    constructor(props) {
        super(props);
        this.owpDialog = React.createRef();
        this.clickCreate = this.clickCreate.bind(this);
        this.onSave = this.onSave.bind(this);
        this.state = {
            FLAG: 'Y',
        };
    }

    handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
    };

    // 등록 화면 열기
    createDialogOpen = () => {
        this.setState({ createDialogOpen: true });
    };

    // 등록 화면 닫기
    createDialogClose = () => {
        this.setState({ createDialogOpen: false });
        this.owpDialog.handleClose();
    };

    // 등록 확인 화면 열기
    createCheckOpen = () => {
        this.setState({ createCheckOpen: true });
        this.setState({ popMessage: translationHelper('C802001.message.do_you_want_to_save') });
    };

    // 등록 확인 화면 닫기
    createCheckClose = () => {
        this.setState({ createCheckOpen: false });
    };

    onSave = async (type) => {
        const data = {
            cudtype: 'CREATE',
            // 'IPX_COMMONCODE.WUSERSEQ': OwpSession('USERSEQ') + '', // : 로그인사용자SEQ
            'IPX_COMMONCODE.WUSERSEQ': '0', // : 로그인사용자SEQ
            'IPX_COMMONCODE.CODEID': this.props.CODEID,
            'IPX_COMMONCODE.CODENM': document.getElementById('CODENM').value,
            'IPX_COMMONCODE.FLAG': this.state.FLAG,
            'IPX_COMMONCODE.REMARK': document.getElementById('REMARK').value,
        };

        const result = await mutate({
            // url: '/createPermissionGroup',
            url: '/createPermission',
            data,
        })
            .then((res) => {
                const G = TGEvent.getGridByID('C802001');
                G.ReloadBody();
                this.createDialogClose();
                this.createCheckClose();
            })
            .catch((error) => {
                console.error(error);
                this.createCheckClose();
            });
    };

    clickCreate() {
        this.onSave('START');
    }

    render() {
        return (
            <OwpDialog
                title={translationHelper('C802001.title.register_dialog_title')}
                dialogButton="등록"
                callbackFunction={this.createDialogOpen}
                dialogActions={
                    <React.Fragment>
                        <div className="w-full center">
                            <OwpButton color="secondary" variant="contained" onClick={this.createCheckOpen}>
                                {translationHelper('C802001.buttons.dialog_save_button')}
                            </OwpButton>
                            <OwpButton color="primary" variant="contained" onClick={this.createDialogClose}>
                                {translationHelper('C802001.buttons.dialog_cancel_button')}
                            </OwpButton>
                        </div>
                    </React.Fragment>
                }
                innerRef={(ref) => (this.owpDialog = ref)}
                width="auto"
                height="auto"
            >
                <React.Fragment>
                    <OwpDialog // 저장버튼을 누르면 나오는 다이얼로그.
                        onOpen={this.state.createCheckOpen}
                        dialogActions={
                            <React.Fragment>
                                <OwpButton color="secondary" variant="contained" onClick={this.clickCreate}>
                                    {translationHelper('C802001.buttons.dialog_ok_button')}
                                </OwpButton>
                                <OwpButton color="primary" variant="contained" onClick={this.createCheckClose}>
                                    {translationHelper('C802001.buttons.dialog_cancel_button')}
                                </OwpButton>
                            </React.Fragment>
                        }
                        width="211px"
                        height="100%"
                    >
                        <React.Fragment>{this.state.popMessage}</React.Fragment>
                    </OwpDialog>

                    <Formsy>
                        <table className="table table-bordered table-advance table-hover tlw7">
                            <tbody>
                            <tr>
                                <th className="left">
                                    {translationHelper('C802001.fields.authority_group')}<span className="c04">*</span>
                                </th>
                                <td colSpan={3}>
                                    <OwpTextField
                                        style={{ width: '440px' }}
                                        id="CODENM"
                                        name="CODENM"
                                    />
                                </td>
                                {/* <th className="left">
                                    {translationHelper('C802001.fields.visibility')}<span className="c04">*</span>
                                </th>
                                <td>
                                    <OwpSelectField
                                        className="w-160 left"
                                        id="FLAG"
                                        name="FLAG"
                                        onChange={this.handleChange}
                                        options={[
                                            { label: translationHelper('C802001.fields.use'), value: 'Y' },
                                            { label: translationHelper('C802001.fields.not_use'), value: 'N' },
                                        ]}
                                        value={'Y'}
                                        useAll={false}
                                    />
                                </td> */}
                            </tr>
                            <tr>
                                <th className="left">{translationHelper('C802001.fields.remark')}</th>
                                <td colSpan={3}>
                                    <OwpTextField
                                        style={{ width: '440px' }}
                                        id="REMARK"
                                        name="REMARK"
                                    />
                                </td>
                            </tr>
                            </tbody>
                        </table>
                    </Formsy>
                </React.Fragment>
            </OwpDialog>
        );
    }
}

export default DialogReweighing;
