import React, { Component } from 'react';
import {
    OwpButton,
    OwpSearchDateField,
    OwpSearchSelectField,
    OwpSearchTextField,
    OwpTextField,
    OwpTreeGrid,
} from 'owp/wrapper';
import Formsy from 'formsy-react';
import * as TGEvent from 'owp/TGEvent';
import moment from 'moment';
import { query, mutate } from 'owp/api';
import Pop_C0202002_01 from './Pop_C0202002_01';
import { setGridData, setRowAttribute, restoreGridSelect, setRowAttributes } from 'main/GridUtill';

const GRID_ID = 'C0202009';
class Page extends Component {
    constructor(props) {
        super(props);
        this.state = {};
    }
    /**
     * Owp에서 사용하는 인풋에 대해 모두 사용할 수 있는 change 이벤트 리시버
     * @param {Event} event 발생한 이벤트 객체
     * @param {Function} callback 콜백 함수. 인풋의 name, value를 인수로 전달함.
     */
    componentDidMount() {
        const _showApprovalDialog = this.showApprovalDialog;
        this.loadGridData();
    }
    //날짜 디폴드값 설정 함수

    testCallRef = () => {
        console.log();
    };

    loadGridData = () => {
        console.log('in----->', this.props.PROCESSSEQ);
        query({
            url: 'listOwpMbrProcessSopByUnitProcess',
            params: {
                jsondata: JSON.stringify({
                    'OWP_MBR_PROCESS_SOP.PROCESSSEQ': this.props.PROCESSSEQ + '',
                }),
            },
        })
            .then((res) => {
                setGridData(GRID_ID, res);
            })
            .catch((error) => {
                console.error(error);
            });
    };

    clickDelete = () => {
        this.onDelete('START');
    };

    onDelete = async () => {
        const G = TGEvent.getGridByID(GRID_ID);
        if (G) {
            const _rows = G.GetSelRows();
            if (_rows.length > 0) {
                _rows
                    .filter((item) => item.Kind === 'Data')
                    .map((row) => {
                        let PROCESSSOP_SEQ = row['OWP_MBR_PROCESS_SOP.PROCESSSOP_SEQ'];
                        const data = {
                            cudtype: 'delete',
                            'OWP_MBR_PROCESS_SOP.PROCESSSOP_SEQ': PROCESSSOP_SEQ,
                        };

                        mutate({
                            url: '/deleteOwpMbrProcessSop/' + PROCESSSOP_SEQ + '',
                            data,
                        })
                            .then((res) => {
                                this.loadGridData();
                                G.ReloadBody();
                            })
                            .catch((error) => {
                                console.error(error);
                            });
                    });
            }
        }
    };

    render() {
        return (
            <React.Fragment>
                <div style={{ width: '100%' }}>
                    <div className="row">
                        <div className="col-md-6 b">작업표준관리(SOP) 설정</div>
                        <div
                            className="col-md-6"
                            style={{ textAlign: 'right', paddingRight: '15' }}
                        >
                            <div
                                style={{ position: 'absolute', right: '0', margin: '1px 95px 0 0' }}
                            >
                                <Pop_C0202002_01 />
                            </div>
                            <OwpButton
                                color={'secondary'}
                                variant={'outlined'}
                                onClick={this.clickDelete}
                            >
                                삭제
                            </OwpButton>
                        </div>
                        <div className="col-md-12">
                            <OwpTreeGrid
                                id="C0202009"
                                LayoutUrl="assets/data/C0202009Def.xml"
                                width="100%"
                                height="40vh"
                            />
                        </div>
                    </div>
                </div>
            </React.Fragment>
        );
    }
}

export default Page;
