import React, { Component } from 'react';
import {
    OwpSearchDateTimePickerMulti,
    OwpPageCarded,
    OwpSearchHeader,
    OwpSearchSelectField,
    OwpSearchTextField,
    OwpToolbar,
    OwpTreeGrid,
} from 'owp/wrapper';
import * as CustomTGEvent from '../../../CustomTGEvent';
import { query } from 'owp/api';

class Page extends Component {
    constructor(props) {
        super(props);
        this.state = {
            interfaceData: '',
        };
    }

    componentDidMount() {
        CustomTGEvent.OWPTGRowClick('C805001', this.onGridClick);
        CustomTGEvent.hideRadioColumn('C805001');
    }

    onGridClick = (is, row) => {
        let interfaceSEQ = row['IPX_INTERFACESTATUS.INTERFACESTATUS_SEQ'];
        if (is && interfaceSEQ) {
            this.loadData(interfaceSEQ);
        }
    };

    loadData = (interfaceSEQ) => {
        query({
            url: '/loadIpxInterfacestatusDATA/' + interfaceSEQ,
        })
            .then((res) => {
                let content = res['IPX_INTERFACESTATUS.CONTENT'];
                this.setState((state) => {
                    return { interfaceData: content };
                });
            })
            .catch((error) => console.error('error...', error));
    };

    clearData = () => {
        this.setState((state) => {
            return { interfaceData: '' };
        });
    };

    render() {
        return (
            <React.Fragment>
                <OwpPageCarded
                    header={
                        <OwpSearchHeader 
                            url="listIpxInterfacestatusInC1005000"
                            gridIndex={0}
                            callfunc={this.clearData}
                        >
                            <OwpSearchSelectField
                                className="w-160"
                                name={'IPX_INTERFACESTATUS.INTERFACE_AVAILABILITY'}
                                label="작업종류"
                                useAll
                                items={[
                                    { label: 'Y', value: 'Y' },
                                    { label: 'N', value: 'N' },
                                ]}
                            />

                            <OwpSearchTextField
                                className="w-160"
                                name={'IPX_INTERFACESTATUS.INTERFACE_NAME'}
                                label={'인터페이스명'}
                                
                            />
                            <OwpSearchSelectField
                                className="w-160"
                                name={'IPX_INTERFACESTATUS.WORK_TYPE'}
                                label="인터페이스여부"
                                useAll
                                items={[
                                    { label: 'Update', value: 'update' },
                                    { label: 'Registration', value: 'registration' },
                                    { label: 'Delete', value: 'delete' },
                                ]}
                            />
                            <OwpSearchDateTimePickerMulti
                                name="IPX_INTERFACESTATUS.INTERFACE_DATE"
                                initNow={true} // TODO: true 인 경우 현재 시로 init (* default = false)
                                labels={{ start: ' 인터페이스시작', end: '종료일' }}
                                inputProps={{ start: { style: { width: 140 } }, end: { style: { width: 140 } } }}
                                required={true}
                            />
                        </OwpSearchHeader>
                    }

                    content={
                        <React.Fragment>
                            <div className="pt-16">
                                <div className="col-lg-5">
                                    <div style={{margin:'0 -16px'}}>
                                        <OwpToolbar title="조회결과" style={{padding:'0'}}>
                                            
                                        </OwpToolbar>
                                    </div>
                                    <OwpTreeGrid
                                        id="C805001"
                                        LayoutUrl="/assets/data/C805001Def.xml"
                                        height="72vh"
                                    />
                                </div>

                                <div className="col-lg-7">
                                    <div style={{margin:'0 -16px'}}>
                                        <OwpToolbar title="DATA">
                                            
                                        </OwpToolbar>
                                    </div>
                                    <div>
                                    <textarea
                                        className="t_textarea"
                                        style={{height:'65vh'}}
                                        value={this.state.interfaceData}
                                    />
                                    </div>
                                </div>
                            </div>
                        </React.Fragment>
                    }
                    innerScroll
                />
            </React.Fragment>
        );
    }
}

export default Page;
