import * as TGEvent from 'owp/TGEvent';

let isOnSelect = false;
export const OWPTGRowClick = function (gridId, callBackFunction) {
    window.TGSetEvent('OnClick', gridId, (G, row, col) => {
        if (!isOnSelect) {
            isOnSelect = true;
            if (col !== 'Panel') {
                G.SelectRow(row, 1, 0);
            }
            setTimeout(function () {
                callBackFunction(true, row);
                isOnSelect = false;
            }, 300);
        }
    });
};

export const hideRadioColumn = function (gridId) {
    window.TGSetEvent('OnRenderPageStart', gridId, (G) => {
        // G.HideCol('Panel');
    });
};
