import React, { Component } from "react";
import { OwpDialog, OwpButton, OwpSearchDateField, OwpSearchSelectField, OwpSearchTextField, OwpTextField, OwpTreeGrid } from 'owp/wrapper';
import Formsy from "formsy-react";
import * as TGEvent from 'owp/TGEvent';
import moment from "moment";
import { query } from 'owp/api';
import Tabs from "@material-ui/core/Tabs"
import Tab from "@material-ui/core/Tab"
import { Typography, withStyles, Collapse } from "@material-ui/core"
import EquipAttrButtonItems from './EquipAttrButtonItems';
import { setGridData } from "main/GridUtill";

class Page extends Component {
    constructor(props) {
        super(props);
        this.state = {
            MBRSEQ: this.props.MBRSEQ,
            productName: this.props.productName,
            functionName: this.props.functionName,
            groupNamesProcessTree: this.props.groupNamesProcessTree,
            stateDialogInDevelopment: false
        };
    }
    /**
     * Owp에서 사용하는 인풋에 대해 모두 사용할 수 있는 change 이벤트 리시버
     * @param {Event} event 발생한 이벤트 객체
     * @param {Function} callback 콜백 함수. 인풋의 name, value를 인수로 전달함.
     */
    componentDidMount() {
        const _showApprovalDialog = this.showApprovalDialog;
    }
    getProcessAndLowerTask = async (params) => {
       
     
        try {
            query({
                url: 'listOwpMbrEchecklist',
                params: {
                    jsondata: JSON.stringify(params),
                },
            }).then((res) => {
                setGridData('C0202013', res);
            });
        } catch (error) {
            console.error(error);
        }
    };
    

    openDialogInDevelopment = () => {
        this.setState({ stateDialogInDevelopment: true })
    }

    closeDialogInDevelopment = () => {
        this.setState({ stateDialogInDevelopment: false })
    }
    //날짜 디폴드값 설정 함수

    render() {
        return (
            <React.Fragment>
                <OwpDialog // 저장버튼을 누르면 나오는 다이얼로그.
                    onOpen={this.state.stateDialogInDevelopment}
                    dialogActions={
                        <React.Fragment>
                            <OwpButton color="secondary" variant="contained" onClick={this.closeDialogInDevelopment}>OK</OwpButton>
                        </React.Fragment>
                    }
                    width="211px"
                    height="100%"
                >
                    <React.Fragment>{"In Development"}</React.Fragment>
                </OwpDialog>
                <div style={{ width: '100%', marginTop: '12px' }}>
                    <div className="row">
                        <div className="col-md-2">
                            <Collapse unmountOnExit in={true}>
                                <EquipAttrButtonItems
                                    data={this.state.groupNamesProcessTree}
                                    MBRSEQ={this.state.MBRSEQ}
                                    handleChangeVisibleState={
                                        this.getProcessAndLowerTask
                                    }
                                    functionName={this.state.functionName}
                                />
                            </Collapse>
                        </div>
                        <div className="col-md-10" style={{ paddingRight: '0' }}>
                            <div className="row">
                                <div className="col-md-6 b" style={{ paddingTop: '13px' }}>공정 및 하위 작업(세부)</div>
                                <div className="col-md-6" style={{ textAlign: 'right' }}>
                                    <OwpButton color={'secondary'} variant={'outlined'} onClick={this.openDialogInDevelopment}>Paper 보기</OwpButton>
                                    <OwpButton color={'secondary'} variant={'outlined'} onClick={this.openDialogInDevelopment}>POP 화면 보기</OwpButton>
                                </div>
                                <div className="col-md-12">
                                    <OwpTreeGrid
                                        id="C0202013"
                                        LayoutUrl="assets/data/C0202013Def.xml"
                                        width="100%"
                                        height="24vh"
                                    />
                                </div>
                                <div className="col-md-12" style={{ marginTop: '-30px' }}>
                                    <div style={{ width: '100%' }}>
                                        <div className="row">
                                            <div className="col-md-6 b" style={{ paddingTop: '13px' }}>
                                                Log book 및 Record table 설정
                                                                                            </div>
                                            <div className="col-md-6" style={{ textAlign: 'right' }}>
                                                <OwpButton color={'secondary'} variant={'contained'}>추가</OwpButton>
                                                <OwpButton color={'secondary'} variant={'contained'}>수정</OwpButton>
                                                <OwpButton color={'secondary'} variant={'outlined'}>삭제</OwpButton>
                                            </div>
                                            <div className="col-md-12">
                                                <OwpTreeGrid
                                                    id="C0202016"
                                                    LayoutUrl="assets/data/C0202016Def.xml"
                                                    // DataUrl={'/assets/data/C0202000Data.xml'}
                                                    width="100%"
                                                    height="23vh"
                                                />
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>

                        </div>
                    </div>
                </div>
            </React.Fragment>
        );
    }
}

export default Page;
