import React, { Component } from 'react';
import {
    OwpDialog,
    OwpButton,
    OwpSearchDateField,
    OwpSearchSelectField,
    OwpSearchTextField,
    OwpTextField,
    OwpTreeGrid,
} from 'owp/wrapper';
import Formsy from 'formsy-react';
import * as TGEvent from 'owp/TGEvent';
import moment from 'moment';
import { query } from 'owp/api';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import { Typography, withStyles, Collapse } from '@material-ui/core';
import C0202002_tab07_tab01_tab01 from './C0202002_tab07_tab01_tab01';
import C0202002_tab07_tab01_tab02 from './C0202002_tab07_tab01_tab02';
import C0202002_tab07_tab01_tab03 from './C0202002_tab07_tab01_tab03';
import EquipAttrButtonItems from './EquipAttrButtonItems';
import { setGridData, setRowAttribute, restoreGridSelect, setRowAttributes } from 'main/GridUtill';
const UNIT_PROCESS_GRID_ID = 'C0202008';
function TabContainer(props) {
    return (
        <Typography component="div" style={{ padding: '0' }}>
            {props.children}
        </Typography>
    );
}
const styles = (theme) => ({
    tabsRoot: {
        borderBottom: '1px solid #e8e8e8',
    },
    tabsIndicator: {
        backgroundColor: '#1890ff',
    },
    tabRoot: {
        textTransform: 'initial',
        fontSize: 14,
        minWidth: 72,
        fontWeight: theme.typography.fontWeightRegular,
        marginRight: theme.spacing.unit * 4,
        fontFamily: [
            'NotoSans',
            '-apple-system',
            'BlinkMacSystemFont',
            '"Segoe UI"',
            'Roboto',
            '"Helvetica Neue"',
            'Arial',
            'sans-serif',
            '"Apple Color Emoji"',
            '"Segoe UI Emoji"',
            '"Segoe UI Symbol"',
        ].join(','),
        '&:hover': {
            color: '#40a9ff',
            opacity: 1,
        },
        '&$tabSelected': {
            borderBottom: '3px solid #e8e8e8',
            color: '#1890ff',
            fontWeight: theme.typography.fontWeightMedium,
        },
        '&:focus': {
            color: '#40a9ff',
        },
    },
    tabSelected: {},
});
class Page extends Component {
    constructor(props) {
        super(props);
        this.C0202002_tab07_tab01_tab01 = React.createRef();
        this.C0202002_tab07_tab01_tab02 = React.createRef();
        this.C0202002_tab07_tab01_tab03 = React.createRef();
        this.state = {
            tabValue: 0,
            groupNames: [],
            MBRSEQ: this.props.MBRSEQ,
            productName: this.props.productName,
            functionName: this.props.functionName,
            groupNamesProcessTree: this.props.groupNamesProcessTree,
            stateDialogInDevelopment: false,
            PROCESSSEQ: 0,
        };
    }
    /**
     * Owp에서 사용하는 인풋에 대해 모두 사용할 수 있는 change 이벤트 리시버
     * @param {Event} event 발생한 이벤트 객체
     * @param {Function} callback 콜백 함수. 인풋의 name, value를 인수로 전달함.
     */
    componentDidMount() {
        this.getProcessAndLowerTask({
            'OWP_MBR_UNIT_PROCESS.MBRSEQ': this.state.MBRSEQ + '',
            'OWP_MBR_UNIT_PROCESS.MAINPROCESS': null + '',
            'OWP_MBR_UNIT_PROCESS.SUBPROCESS': null + '',
            VER: 2,
        });
        this.setOnGridSelectEvent();
    }

    setOnGridSelectEvent = () => {
        TGEvent.OWPTGSelect(UNIT_PROCESS_GRID_ID, (is, row) => {
            if (is) {
                if (row['PROCESSSEQ']) {
                    this.setState((state) => {
                        return {
                            ...state,
                            PROCESSSEQ: row['PROCESSSEQ'],
                        };
                    });
                    if (
                        this.C0202002_tab07_tab01_tab01 &&
                        this.C0202002_tab07_tab01_tab01.current
                    ) {
                        this.C0202002_tab07_tab01_tab01.current.loadGridData();
                    }
                    if (
                        this.C0202002_tab07_tab01_tab02 &&
                        this.C0202002_tab07_tab01_tab02.current
                    ) {
                        console.log(this.C0202002_tab07_tab01_tab02);
                        this.C0202002_tab07_tab01_tab02.current.loadStdMainGrid();
                    }
                }
            }
        });
    };

    getProcessAndLowerTask = async (params) => {
        try {
            query({
                url: 'listOWP_UnitProcess',
                params: {
                    jsondata: JSON.stringify(params),
                },
            }).then((res) => {
                setGridData(UNIT_PROCESS_GRID_ID, res);
            });
        } catch (error) {
            console.error(error);
        }
    };
    handleChangeTabs = (event, tabValue) => {
        //탭을 변환할때마다 조회하기 위한 변수를 담아줌
        this.setState({ tabValue });
    };
    //날짜 디폴드값 설정 함수
    openDialogInDevelopment = () => {
        this.setState({ stateDialogInDevelopment: true });
    };

    closeDialogInDevelopment = () => {
        this.setState({ stateDialogInDevelopment: false });
    };

    render() {
        const { classes } = this.props;
        const { tabValue } = this.state;
        return (
            <React.Fragment>
                <OwpDialog // 저장버튼을 누르면 나오는 다이얼로그.
                    onOpen={this.state.stateDialogInDevelopment}
                    dialogActions={
                        <React.Fragment>
                            <OwpButton
                                color="secondary"
                                variant="contained"
                                onClick={this.closeDialogInDevelopment}
                            >
                                OK
                            </OwpButton>
                        </React.Fragment>
                    }
                    width="211px"
                    height="100%"
                >
                    <React.Fragment>{'In Development'}</React.Fragment>
                </OwpDialog>
                <div style={{ width: '100%', marginTop: '12px' }}>
                    <div className="row">
                        <div className="col-md-2">
                            <Collapse unmountOnExit in={true}>
                                <EquipAttrButtonItems
                                    data={this.state.groupNamesProcessTree}
                                    handleChangeVisibleState={this.getProcessAndLowerTask}
                                    MBRSEQ={this.state.MBRSEQ}
                                    functionName={this.state.functionName}
                                />
                            </Collapse>
                        </div>
                        <div className="col-md-10" style={{ paddingRight: '0' }}>
                            <div className="row">
                                <div className="col-md-6 b">공정 및 하위 작업(세부)</div>
                                <div className="col-md-6" style={{ textAlign: 'right' }}>
                                    <OwpButton
                                        color={'secondary'}
                                        variant={'outlined'}
                                        onClick={this.openDialogInDevelopment}
                                    >
                                        Paper 보기
                                    </OwpButton>
                                    <OwpButton
                                        color={'secondary'}
                                        variant={'outlined'}
                                        onClick={this.openDialogInDevelopment}
                                    >
                                        POP 화면 보기
                                    </OwpButton>
                                </div>
                                <div className="col-md-12">
                                    <OwpTreeGrid
                                        id="C0202008"
                                        LayoutUrl="assets/data/C0202008Def.xml"
                                        DataUrl={'/assets/data/C0202000Data.xml'}
                                        width="100%"
                                        height="40vh"
                                    />
                                </div>
                                <div className="col-md-12">
                                    <div style={{ border: '1px solid #efefef' }}>
                                        <div className="row mg0 pd0">
                                            {/*TAB*/}
                                            <Tabs
                                                style={{ border: '0px solid' }}
                                                value={tabValue}
                                                onChange={this.handleChangeTabs}
                                                classes={{
                                                    root: classes.tabsRoot,
                                                    indicator: classes.tabsIndicator,
                                                }}
                                            >
                                                <Tab
                                                    disableRipple
                                                    classes={{
                                                        root: classes.tabRoot,
                                                        selected: classes.tabSelected,
                                                    }}
                                                    label="SOP 설정"
                                                />
                                                <Tab
                                                    disableRipple
                                                    classes={{
                                                        root: classes.tabRoot,
                                                        selected: classes.tabSelected,
                                                    }}
                                                    label="Std 대시보드 설정"
                                                />
                                                <Tab
                                                    disableRipple
                                                    classes={{
                                                        root: classes.tabRoot,
                                                        selected: classes.tabSelected,
                                                    }}
                                                    label="Main 대시보드 설정"
                                                />
                                            </Tabs>
                                            {tabValue === 0 && (
                                                <TabContainer>
                                                    {/*일단위 등록 현황*/}
                                                    <C0202002_tab07_tab01_tab01
                                                        PROCESSSEQ={this.state.PROCESSSEQ}
                                                        ref={this.C0202002_tab07_tab01_tab01}
                                                    />
                                                </TabContainer>
                                            )}
                                            {tabValue === 1 && (
                                                <TabContainer>
                                                    {/*주단위 등록 현황*/}
                                                    <C0202002_tab07_tab01_tab02
                                                        PROCESSSEQ={this.state.PROCESSSEQ}
                                                        ref={this.C0202002_tab07_tab01_tab02}
                                                    />
                                                </TabContainer>
                                            )}
                                            {tabValue === 2 && (
                                                <TabContainer>
                                                    {/*주단위 등록 현황*/}
                                                    <C0202002_tab07_tab01_tab03 />
                                                </TabContainer>
                                            )}
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </React.Fragment>
        );
    }
}

export default withStyles(styles, { withTheme: true })(Page);
