import React, { Component } from 'react';
import {
    OwpButton,
    OwpSearchDateTimePickerMulti,
    OwpPageCarded,
    OwpSearchHeader,
    OwpSearchSelectField,
    OwpSearchTextField,
    OwpToolbar,
    OwpTreeGrid,
    OwpSearchAutoComplete,
} from 'owp/wrapper';
import * as TGEvent from 'owp/TGEvent';
import Pop_C0202002 from '../../include/Pop_C0202002';
import Pop_C0202005 from '../../include/Pop_C0202005';
import Pop_C0202006 from '../../include/Pop_C0202006';

class Page extends Component {
    constructor(props) {
        super(props);
        this.state = {};
    }
    componentDidMount() {
        TGEvent.OWPTGSelect('C0202000', (is, row) => {
            //TODO question here
            if (is) {
                this.setState((state) => {
                    return { MBRSEQ: row['OWP_MBR.MBRSEQ'] };
                });
            } else {
                this.setState((state) => {
                    return { MBRSEQ: null };
                });
            }
        });

    }



    render() {
        return (
            <React.Fragment>
                <OwpPageCarded
                    header={
                        <OwpSearchHeader url="listOwpMbr"  autoSubmit={true}>
                            <OwpSearchTextField
                                className="w-160"
                                id={'OWP_MBR.PRODUCTNAME'}
                                name={'OWP_MBR.PRODUCTNAME'}
                                label={'제품명'}
                            />

                            <OwpSearchTextField
                                className="w-160"
                                id={'OWP_MBR.PRODUCTID'}
                                name={'OWP_MBR.PRODUCTID'}
                                label={'제품ID번호'}
                            />

                            <OwpSearchSelectField
                                className="w-160"
                                id={'sm05'}
                                name={'sm05'}
                                label="진행현황"
                                useAll
                            />
                            <OwpSearchTextField
                                className="w-160"
                                id={'OWP_MBR.MBRNO'}
                                name={'OWP_MBR.MBRNO'}
                                label={'MBR 번호'}

                            />

                            <OwpSearchTextField
                                className="w-160"
                                id={'OWP_MBR.MBRVER'}
                                name={'OWP_MBR.MBRVER'}
                                label={'버전번호'}

                            />
                            <OwpSearchDateTimePickerMulti
                                name="sm02"
                                initNow={true} // TODO: true 인 경우 현재 시로 init (* default = false)
                                labels={{ start: '배치등록시작일', end: '배치등록종료일' }}
                                inputProps={{ start: { style: { width: 120 } }, end: { style: { width: 120 } } }}
                                required={true}
                            />
                        </OwpSearchHeader>
                    }
                    contentToolbar={
                        <OwpToolbar title="Master 제조지시 조회">
                            <OwpButton color={'secondary'} variant={'outlined'}>
                                엑셀양식
                            </OwpButton>
                            <OwpButton color={'secondary'} variant={'outlined'}>
                                엑셀저장
                            </OwpButton>
                            <Pop_C0202002/>
                            <OwpButton color={'secondary'} variant={'contained'}>
                                COPY
                            </OwpButton>
                            <OwpButton color={'secondary'} variant={'contained'}>
                                수정
                            </OwpButton>
                            <OwpButton color={'secondary'} variant={'contained'}>
                                삭제
                            </OwpButton>
                            {/* <Pop_C0202005/> */}
                            <Pop_C0202006/>
                        </OwpToolbar>
                    }
                    content={
                        <React.Fragment>
                            <OwpTreeGrid
                                id="OWP_C0202000"
                                LayoutUrl={'/assets/data/C0202000Def.xml'}
                            />
                        </React.Fragment>
                    }
                    innerScroll
                />
            </React.Fragment>
        );
    }
}

export default Page;
