import {
    OwpTextField,
    OwpButton,
    OwpSelectField,
    OwpDialog,
    OwpMessage,
    OwpTreeGrid,
    OwpSearchDateTimePickerMulti,
    OwpDateTimePicker,
} from 'owp/wrapper';
import React, { Component } from 'react';
import Formsy from 'formsy-react';
import { mutate } from 'owp/api';
import * as TGEvent from 'owp/TGEvent';

class DialogReweighing extends Component {
    constructor(props) {
        super(props);
        this.owpDialog = React.createRef();
        this.clickCreate = this.clickCreate.bind(this);
        this.onSave = this.onSave.bind(this);
        this.state = {
            FLAG: 'Y',
        };
    }

    handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
    };

    

    // 등록 화면 닫기
    createDialogClose = () => {
        this.setState({ createDialogOpen: false });
        this.owpDialog.handleClose();
    };

    // 등록 확인 화면 열기
    createCheckOpen = () => {

        this.setState({ createCheckOpen: true });
        this.setState({ popMessage: '저장하시겠습니까?' });
    };

    // 등록 확인 화면 닫기
    createCheckClose = () => {
        this.setState({ createCheckOpen: false });
    };

    onSave = async (type) => {
        const data = {
            cudtype: 'create',
            
        };

        const result = await mutate({
            url: '/createIpxCommoncode',
            data,
        })
            
    };

    clickCreate() {
        this.onSave('START');
    }

    render() {
        return (
            <OwpDialog
                // title="수불 대장"
                title={
                    <div className="pt-0 mt-0 pb-0 mb-0 w-full flex justify-between items-center">
                        <div>
                        재입고 처리
                        </div>
                        <div style={{right: '130px', position:'absolute'}}>
                            <OwpTextField
                                style={{ width: '100%' }}
                                id={''}
                                name={''}
                                value="A0000001"
                                withTable={false}
                                disabled={false}
                                className="mg0"
                            />
                        </div>
                        <div>

                            <OwpButton 
                                color="secondary" 
                                variant="contained"
                            >
                                확인
                            </OwpButton>
                        </div>
                    </div>
                }

                callbackFunction={this.createDialogOpen}
                dialogButton="재입고"
                dialogActions={
                    <React.Fragment>
                        <div className="w-full center">
                            <OwpButton color="secondary" variant="contained" onClick={this.createCheckOpen}>
                                확인
                            </OwpButton>
                            <OwpButton color="secondary" variant="outlined" onClick={this.createDialogClose}>
                                취소
                            </OwpButton>
                        </div>
                    </React.Fragment>
                }
                innerRef={(ref) => (this.owpDialog = ref)}
                width="900px"
                height="auto"
            >
                <React.Fragment>
                <div style={{width:'100%'}}>
                        <div>
                            <table className="table table-bordered table-advance tlw5">
                                <tbody>
                                <tr>
                                    <th className="left b" width="150">제품번호</th>
                                    <td width="250">xxxxxxxx</td>
                                    <th className="left b" width="150">재입고 요청량(kg)</th>
                                    <td colspan="2">xxxxxxxx</td>
                                </tr>
                                <tr>
                                    <th className="left b">인수번호</th>
                                    <td>xxxxxxxx</td>
                                    <th className="left b">실제 입고량(kg)</th>
                                    <td>
                                        <OwpSelectField
                                            className="left w-full"
                                            id="PROD_TYPE"
                                            name="PROD_TYPE"
                                            options={[
                                                { label: '실제 칭량 값', value: '1' },
                                                { label: '전산 기입 값', value: '2' },
                                            ]}
                                            value={'1'}
                                        />
                                    </td>
                                    <td>
                                        <OwpTextField
                                            style={{ width: '100%' }}
                                            id={''}
                                            name={''}
                                            value="999"
                                            withTable={false}
                                            disabled={false}
                                            className="mg0"
                                        />
                                    </td>
                                </tr>
                                <tr>
                                    <th className="left b">원료 ID</th>
                                    <td>xxxxxxxx</td>
                                    <th className="left b">용기별 재입고량(kg)</th>
                                    <td colspan="2">xxxxxxxx</td>
                                </tr>
                                <tr>
                                    <th className="left b">원료명</th>
                                    <td>xxxxxxxx</td>
                                    <th className="left b">포장용기 및라벨 <br />부착상태 확인</th>
                                    <td colspan="2">xxxxxxxx</td>
                                </tr>
                                </tbody>
                            </table>
                        </div>
                        
                    </div>
                </React.Fragment>
            </OwpDialog>
        );
    }
}

export default DialogReweighing;
