import {
    OwpTextField,
    OwpButton,
    OwpSelectField,
    OwpDialog,
    OwpMessage,
    OwpSearchMonthPicker,
    OwpSearchDateField,
} from 'owp/wrapper';
import React, { Component } from 'react';
import Formsy from 'formsy-react';
import { mutate } from 'owp/api';
import * as TGEvent from 'owp/TGEvent';
import moment from 'moment';
import ImageUpload from '../../custom-component/ImageUpload';
import { isMissingParam } from '../../CommonUtils';

class DialogReweighing extends Component {
    constructor(props) {
        super(props);
        this.owpDialog = React.createRef();
        this.clickCreate = this.clickCreate.bind(this);
        this.onSave = this.onSave.bind(this);
        this.state = {
            isDialogOpen: false,
            isDialogCheckOpen: false,
            popMessage: '저장하시겠습니까',
            FACILITY_IMAGE: [],
            FLOOR_PLAN_FILESEQ: [],
        };
    }

    handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
    };

    handleMultiSelectChange = (event) => {
        let sortedCode = this.sortCommonCode(event.target.value);
        this.setState({ [event.target.name]: sortedCode });
    };

    sortCommonCode = (commonCodes) => {
        if (commonCodes) {
            let arrCode = commonCodes.split(',');
            arrCode.sort();
            return arrCode.join(',');
        }
        return commonCodes;
    };

    closeDialog = () => {
        this.resetStates();
        this.setState({ isDialogOpen: false });
        this.owpDialog.handleClose();
    };

    resetStates = () => {
        this.setState((state) => {
            return {
                FACILITY_ID: '',
                FACILITY_NAME: '',
                LEVEL_CD: '',
                LOCATION_CD: '',
                FACILITY_COMMENT: '',
                FACILITY_MT_FREQUENCY: '',

                FACILITY_METHOD: '',
                FACILITY_SIZE: '',
                FACILITY_MANUFACTURE: '',
                FACILITY_MANUF_COUNTRY: '',
                FACILITY_YEAR_MONTH: '',
                FACILITY_MODEL_NAME: '',
                FACILITY_SERIAL_NO: '',
                FACILITY_CONTACT_POINT: '',
                FACILITY_ASSET_NO: '',
                FACILITY_PURCHASE_AMT: '',
                FACILITY_PURCHASE_DATE: '',
                FACILITY_CAPACITY: '',
                FACILITY_TYPE: '',
                FACILITY_MATERIAL: '',
                FACILITY_POWER: '',
                FACILITY_CAPACITY_KW: '',
                FACILITY_CHECK_CYCLE: '',
                FACILITY_IMAGE: [],
                FLOOR_PLAN_FILESEQ: [],
            };
        });
    };

    // 등록 화면 닫기
    createDialogClose = () => {
        this.setState({ isDialogCheckOpen: false });
        this.owpDialog.handleClose();
    };

    openDialogCheck = () => {
        if (this.isMissingMandatoryField()) {
            OwpMessage({
                message: '양식을 작성하십시오',
                variant: 'warning',
            });
        } else {
            this.setState({ isDialogCheckOpen: true });
            this.setState({ popMessage: '저장하시겠습니까?' });
        }
    };

    isMissingMandatoryField = () => {
        if (isMissingParam(this.state.FACILITY_NAME) || isMissingParam(this.state.FACILITY_ID)) {
            return true;
        }
        return false;
    };

    onUploadImages = (image) => {
        this.setState((state) => {
            return { FACILITY_IMAGE: [...state.FACILITY_IMAGE, image] };
        });
    };

    onDeleteImage = (image) => {
        if (image) {
            this.setState((state) => {
                return {
                    ...state,
                    FACILITY_IMAGE: state.FACILITY_IMAGE.filter(
                        (item) => item.fileSeq != image.fileSeq
                    ),
                };
            });
        }
    };

    closeDialogCheck = () => {
        this.setState({ isDialogCheckOpen: false });
    };

    clickCreate() {
        this.onSave('START');
    }

    onSave = async (type) => {};

    render() {
        return (
            <OwpDialog
                title={'추가'}
                callbackFunction={this.createDialogOpen}
                dialogButton="등록"
                dialogActions={
                    <React.Fragment>
                        <div className="w-full center">
                            <OwpButton
                                color="secondary"
                                variant="contained"
                                onClick={this.openDialogCheck}
                            >
                                저장
                            </OwpButton>
                            <OwpButton
                                color="secondary"
                                variant="outlined"
                                onClick={this.closeDialog}
                            >
                                취소
                            </OwpButton>
                        </div>
                    </React.Fragment>
                }
                innerRef={(ref) => (this.owpDialog = ref)}
                width="1100px"
                height="auto"
            >
                <React.Fragment>
                    <OwpDialog
                        onOpen={this.state.isDialogCheckOpen}
                        dialogActions={
                            <React.Fragment>
                                <div className="w-full center">
                                    <OwpButton
                                        color="secondary"
                                        variant="contained"
                                        onClick={this.clickCreate}
                                    >
                                        저장
                                    </OwpButton>
                                    <OwpButton
                                        color="secondary"
                                        variant="outlined"
                                        onClick={this.closeDialogCheck}
                                    >
                                        취소
                                    </OwpButton>
                                </div>
                            </React.Fragment>
                        }
                    >
                        <React.Fragment>{this.state.popMessage}</React.Fragment>
                    </OwpDialog>

                    <Formsy>
                        <div style={{ width: '100%' }}>
                            <table className="table table-bordered table-advance tlw5">
                                <tbody>
                                    <tr>
                                        <th className="center" width="35">
                                            설치장소
                                        </th>
                                        <td className="center">
                                            <OwpSelectField
                                                className="w-full"
                                                value={this.state.LOCATION_CD}
                                                groupId={'D027000'}
                                                onChange={this.handleChange}
                                                useAll={false}
                                            />
                                        </td>
                                        <th className="center" width="35">
                                            설치장소
                                        </th>
                                        <td className="center">
                                            <OwpSelectField
                                                className="w-full"
                                                value={this.state.LOCATION_CD}
                                                groupId={'D027000'}
                                                onChange={this.handleChange}
                                                useAll={false}
                                            />
                                        </td>
                                        <th className="center" width="35">
                                            설치장소
                                        </th>
                                        <td className="center">
                                            <OwpSelectField
                                                className="w-full"
                                                value={this.state.LOCATION_CD}
                                                groupId={'D027000'}
                                                onChange={this.handleChange}
                                                useAll={false}
                                            />
                                        </td>
                                        <th className="center" width="35">
                                            설치장소
                                        </th>
                                        <td className="center">
                                            <OwpSelectField
                                                className="w-full"
                                                value={this.state.LOCATION_CD}
                                                groupId={'D027000'}
                                                onChange={this.handleChange}
                                                useAll={false}
                                            />
                                        </td>
                                        <th className="center" width="35">
                                            설치장소
                                        </th>
                                        <td className="center">
                                            <OwpSelectField
                                                className="w-full"
                                                value={this.state.LOCATION_CD}
                                                groupId={'D027000'}
                                                onChange={this.handleChange}
                                                useAll={false}
                                            />
                                        </td>
                                        <th className="center" width="35">
                                            설치장소
                                        </th>
                                        <td className="center">
                                            <OwpSelectField
                                                className="w-full"
                                                value={this.state.LOCATION_CD}
                                                groupId={'D027000'}
                                                onChange={this.handleChange}
                                                useAll={false}
                                            />
                                        </td>
                                    </tr>
                                </tbody>
                            </table>
                        </div>
                    </Formsy>
                </React.Fragment>
            </OwpDialog>
        );
    }
}

export default DialogReweighing;
