import {
    OwpTextField,
    OwpButton,
    OwpDialog,
    OwpMessage,
} from 'owp/wrapper';
import React, { Component } from 'react';
import Formsy from 'formsy-react';
import { mutate, query } from 'owp/api';
import * as TGEvent from 'owp/TGEvent';

class DialogReweighing extends Component {
    constructor(props) {
        super(props);
        this.owpDialog = React.createRef();
        this.clickCreate = this.clickCreate.bind(this);
        this.onSave = this.onSave.bind(this);
        this.state = {};
    }

    openDialogReweighing = () => {
        console.log(this.props.SESSIONEXPIREMINUTE);
        if(!Boolean(this.props.isGridSelected)){
            OwpMessage({
                message:"작업지시를 선택해 주세요.",
                variant: "warning"
            })
            this.closeDialogReweighing();
        }else{
            query({
                url: "loadIpxSystemmanage" ,
              }).then(res => {
                this.setState({
                    updateDialogOpen: true,
                    MAXLOAGINFAILCOUNT : res['IPX_SYSTEMMANAGE.MAXLOAGINFAILCOUNT'],
                    NOTICEBEFOREPASSWORDEXPIRE : res['IPX_SYSTEMMANAGE.NOTICEBEFOREPASSWORDEXPIRE'],
                    PASSWORDEXPIREPERIOD : res['IPX_SYSTEMMANAGE.PASSWORDEXPIREPERIOD'],
                    SESSIONEXPIREMINUTE : res['IPX_SYSTEMMANAGE.SESSIONEXPIREMINUTE']
          
                  });
            })
            .catch(error =>{
               console.error(error);
            })

            // this.setState({stateDialogReweihing: true})
        }
    }

    closeDialogReweighing = () => {
    
        this.setState({ stateDialog: false})
        this.props.callback()
        this.owpDialog.handleClose();
    }

    openDialogReweighing2 = () => {
        this.setState({ stateDialogReweighing2: true })
    }

    closeDialogReweighing2 = () => {
    this.setState({ stateDialogReweighing2: false })
    }


    handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
    };

    
    onSave = async (type) => {
        const data = {
            cudtype: 'update',
           "IPX_SYSTEMMANAGE.SESSIONEXPIREMINUTE": document.getElementById("SESSIONEXPIREMINUTE").value ,
            "IPX_SYSTEMMANAGE.PASSWORDEXPIREPERIOD":document.getElementById("PASSWORDEXPIREPERIOD").value,
            "IPX_SYSTEMMANAGE.MAXLOAGINFAILCOUNT": document.getElementById("MAXLOAGINFAILCOUNT").value ,
            "IPX_SYSTEMMANAGE.NOTICEBEFOREPASSWORDEXPIRE" :document.getElementById("NOTICEBEFOREPASSWORDEXPIRE").value    
        };

        const result = await mutate({
            url: '/updateIpxSystemmanage',
            data
        })
        .then(res => {
            const G = TGEvent.getGridByID("C804001")
            G.ReloadBody()
            this.closeDialogReweighing()
            this.closeDialogReweighing2()
        })
        .catch(error =>{
           console.error(error);
        })
            
    };

    clickCreate() {
        this.onSave('START');
    }

    render() {
        return (
            <OwpDialog
                title="시스템 설정관리 수정"
                callbackFunction={this.openDialogReweighing}
                dialogButton="수정"
                onOpen ={this.state.stateDialog}
                dialogActions={
                    <React.Fragment>
                        <div className="w-full center">
                            <OwpButton color="secondary" variant="contained" onClick={this.openDialogReweighing2}>
                                수정
                            </OwpButton>
                            <OwpButton color="secondary" variant="outlined" onClick={this.closeDialogReweighing}>
                                닫기
                            </OwpButton>
                        </div>
                    </React.Fragment>
                }
                innerRef={(ref) => (this.owpDialog = ref)}
                width="1100px"
                height="auto"
            >
                <React.Fragment>
                    <OwpDialog // 저장버튼을 누르면 나오는 다이얼로그.
                    onOpen={this.state.stateDialogReweighing2}
                    dialogActions={
                    <React.Fragment>
                        <OwpButton color="primary" variant="contained" onClick={this.clickCreate}>
                        예
                        </OwpButton>
                        <OwpButton
                        color="primary"
                        variant="contained"
                        onClick={this.closeDialogReweighing2}
                        >
                        아니요
                        </OwpButton>
                    </React.Fragment>
                    }
                    width="300px"
                    height="100%"
                    >
                    <React.Fragment>수정하시겠습니까?</React.Fragment>
                </OwpDialog>
                    <Formsy>
                        <div style={{width:'100%'}}>
                            <table className="table table-bordered table-advance tlw5">
                                <tbody>
                                <tr>
                                    <th className="center" width="150">세션시간(분)</th>
                                    <td className="center">
                                        <OwpTextField
                                                style={{width:'100%'}}
                                                id = "SESSIONEXPIREMINUTE"
                                                name="SESSIONEXPIREMINUTE"
                                                value ={this.state.SESSIONEXPIREMINUTE}
                                        />
                                    </td>
                                    <th className="center" width="200">패스워드연속실패횟수</th>
                                    <td className="center">
                                        <OwpTextField
                                            style={{width:'100%'}}
                                            id = "MAXLOAGINFAILCOUNT"
                                            name="MAXLOAGINFAILCOUNT"
                                            value ={this.state.MAXLOAGINFAILCOUNT}
                                        />
                                    </td>
                                   
                                </tr>
                                <tr>
                                    <th className="center">패스워드유효기간(일)</th>
                                    <td className="center">
                                        <OwpTextField
                                            style={{width:'100%'}}
                                            id = "PASSWORDEXPIREPERIOD"
                                            name="PASSWORDEXPIREPERIOD"
                                            value ={this.state.PASSWORDEXPIREPERIOD}
                                        />
                                    </td>
                                    <th className="center">페스워드만료일전알림시간(일)</th>
                                    <td className="center">
                                        <OwpTextField
                                            style={{width:'100%'}}
                                            id = "NOTICEBEFOREPASSWORDEXPIRE"
                                            name="NOTICEBEFOREPASSWORDEXPIRE"
                                            value ={this.state.NOTICEBEFOREPASSWORDEXPIRE}
                                        />
                                    </td>
                                  
                                </tr>
                           
                                </tbody>
                            </table>
                        </div>
                    </Formsy>
                </React.Fragment>
            </OwpDialog>
        );
    }
}

export default DialogReweighing;
