import React, { Component } from "react"
import { OwpDialog, OwpButton, OwpSession, OwpSearchTextField, OwpSearchSelectField, OwpMessage, OwpSelectField } from 'owp/wrapper';
import Formsy from "formsy-react"
import { mutate } from 'owp/api';
import * as TGEvent from 'owp/TGEvent';
import translationHelper from 'main/content/utils/translationHelper';
const pageID = 'C803000';

class DialogReweighing extends Component {
  constructor(props) {
    super(props)
    this.owpDialog = React.createRef()
    this.clickCreate = this.clickCreate.bind(this)
    this.onSave = this.onSave.bind(this)

    this.state = {
      stateDialogReweighing: false
    }
  }

  openDialogReweighing = () => {
    this.setState({ stateDialogReweighing: true })
    if (!Boolean(this.props.CHECKLISTSEQ)) {
      OwpMessage({
        message: "Please select an E-checklist",
        variant: "warning"
      })
      this.closeDialogReweighing()
    } else {
      this.setState({ stateDialogReweighing: true })
    }
  }

  closeDialogReweighing = () => {
    this.setState({ stateDialogReweighing: false })
    this.owpDialog.handleClose()
  }

  openDialogReweighing2 = () => {
    this.setState({ stateDialogReweighing2: true })
    this.setState("Do you want to save?");
  }

  closeDialogReweighing2 = () => {
    this.setState({ stateDialogReweighing2: false })
  }
  handleChange = event => {
    this.setState({ [event.target.name]: event.target.value })
  }
  onSave = async type => {
    const data = {
      cudtype: "CREATE",
      "OWP_MBR_LOG_BOOK.CHECKLISTSEQ": this.props.CHECKLISTSEQ + '',
      "OWP_MBR_LOG_BOOK.NAROUND": document.getElementById("NAROUND").value,
      "OWP_MBR_LOG_BOOK.WUSERSEQ": "3",// : 로그인 사용자SEQ
      // "OWP_MBR_LOG_BOOK.WUSERSEQ": OwpSession("USERSEQ"),// : 로그인 사용자SEQ
      "OWP_MBR_LOG_BOOK.PROCESS_CONDITION": document.getElementById("PROCESS_CONDITION").value,
      "OWP_MBR_LOG_BOOK.DETAIL_TEXT": document.getElementById("DETAIL_TEXT").value,
      "OWP_MBR_LOG_BOOK.OPERATION1": document.getElementById("OPERATION1").value,
      "OWP_MBR_LOG_BOOK.OPERATION2": document.getElementById("OPERATION2").value,
    }

    const result = await mutate({
      url: "/createOwpMbrLogBook",
      data
    })
      .then(res => {
        const G = TGEvent.getGridByID("C0202016")
        G.ReloadBody()
        this.closeDialogReweighing()
        this.closeDialogReweighing2()
      })
      .catch(error => {
        console.error(error)
        this.closeDialogReweighing2()
      })
  }

  clickCreate() {
    this.onSave("START")
  }

  render() {
    console.log(this.state.CHECKLISTSEQ);
    return (
      <OwpDialog
        title={"Add Log book"} //Upper Code Registration
        dialogButton={"Add"} //Registration
        callbackFunction={this.openDialogReweighing}
        onOpen={this.state.stateDialogReweighing}
        dialogActions={
          <React.Fragment>
            <OwpButton color="secondary" variant="contained" onClick={this.clickCreate}>
              {"Save"} {/*Save*/}
            </OwpButton>
            <OwpButton color="secondary" variant="outlined" onClick={this.closeDialogReweighing}>
              {"Close"} {/*Close*/}
            </OwpButton>
          </React.Fragment>
        }
        innerRef={ref => (this.owpDialog = ref)}
        width="700px"
        height="auto"
      >
        <React.Fragment>
          <Formsy>
            <div style={{ width: '100%' }}>
              <table className="table table-bordered table-advance tlw5">
                <tbody>
                  <tr>
                    <th className="left" width="120">{"N/A 회차"}<span className="c04">*</span></th>
                    <td className="left">
                      <OwpSearchTextField
                        className="w-full left"
                        id="NAROUND"
                        name="NAROUND"
                        value={this.state["NAROUND"]}
                      />
                    </td>
                    <th className="left">{"공정조건"}<span className="c04">*</span></th>
                    <td>
                      <OwpSelectField
                        className="w-full left"
                        id="PROCESS_CONDITION"
                        name="PROCESS_CONDITION"
                        groupId={"D025000"}
                        isall={false}
                        value={this.state['PROCESS_CONDITION']}
                      />
                    </td>
                  </tr>
                  <tr>
                    <th className="left" width="120">{"Unit"}<span className="c04">*</span></th>
                    <td className="left">
                      <OwpSearchTextField
                        className="w-full left"
                        id="UNIT"
                        name="UNIT"
                        value={this.state["UNIT"]}
                      />
                    </td>
                    <th className="left" width="120">{"Detail Text"}<span className="c04">*</span></th>
                    <td className="left">
                      <OwpSearchTextField
                        className="w-full left"
                        id="DETAIL_TEXT"
                        name="DETAIL_TEXT"
                        value={this.state["DETAIL_TEXT"]}
                      />
                    </td>
                  </tr>
                  <tr>
                    <th className="left">{"Operation Details 1"}<span className="c04">*</span></th>
                    <td>
                      <OwpSelectField
                        className="w-full left"
                        id="OPERATION1"
                        name="OPERATION1"
                        groupId={"D026000"}
                        isall={false}
                        value={this.state['OPERATION1']}
                      />
                    </td>
                    <th className="left">{"Operation Details 2"}<span className="c04">*</span></th>
                    <td>
                      <OwpSelectField
                        className="w-full left"
                        id="OPERATION2"
                        name="OPERATION2"
                        groupId={"D026000"}
                        isall={false}
                        value={this.state['OPERATION2']}
                      />
                    </td>
                  </tr>
                </tbody>
              </table>
            </div>
          </Formsy>
        </React.Fragment>
      </OwpDialog>
    )
  }
}

export default DialogReweighing
