import {
    OwpTextField,
    OwpButton,
    OwpSelectField,
    OwpDialog,
    OwpMessage,
    OwpTreeGrid,
    OwpSearchDateTimePickerMulti,
    OwpSearchSelectField,
    OwpSearchTextField,
    OwpDateTimePickerMulti,
} from 'owp/wrapper';
import Icon from '@material-ui/core/Icon';
import TextField from '@material-ui/core/TextField';
import InputAdornment from '@material-ui/core/InputAdornment';
import React, { Component } from 'react';
import Formsy from 'formsy-react';
import { mutate } from 'owp/api';
import * as TGEvent from 'owp/TGEvent';
import { setGridData } from 'main/GridUtill';

const PASS_GRID_ID = 'C0202016';
const FAIL_GRID_ID = 'C0202017';

class DialogReweighing extends Component {
    constructor(props) {
        super(props);
        this.owpDialog = React.createRef();
        this.clickCreate = this.clickCreate.bind(this);
        this.onSave = this.onSave.bind(this);
        this.state = {
            FLAG: 'Y',
            gridLength: 0,
        };
    }

    handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
    };

    

    // 등록 화면 닫기
    createDialogClose = () => {
        this.setState({ createDialogOpen: false });
        this.owpDialog.handleClose();
    };

    // 등록 확인 화면 열기
    createCheckOpen = () => {

        this.setState({ createCheckOpen: true });
        this.setState({ popMessage: '저장하시겠습니까?' });
    };

    // 등록 확인 화면 닫기
    createCheckClose = () => {
        this.setState({ createCheckOpen: false });
    };

    onSave = async (type) => {
        const data = {
            cudtype: 'create',
            
        };

        const result = await mutate({
            url: '/createIpxCommoncode',
            data,
        })
            
    };

    clickCreate() {
        this.onSave('START');
    }
    handleDeleteFailRow = () => {
        this.fillGridData(true);
    };

    handleAddFailRow = () => {
            const G = TGEvent.getGridByID(FAIL_GRID_ID);
            G.ActionAcceptEdit();
            TGEvent.OWPTGAddRow(FAIL_GRID_ID, (row) => {
                this.setState((state) => {
                    return {
                        ...state,
                        gridLength: state.gridLength + 1,
                    };
                });
            });
    };
    fillGridData = (isDelete) => {
        const G = TGEvent.getGridByID(FAIL_GRID_ID);
        if (G) {
            const _rows = Object.values(G['Rows']).filter((item) => item.Kind == 'Data');
            let arrData = [];
            _rows.some((row) => {
                if (isDelete && row['Selected'] === 1) {
                    return false;
                } else {
                    arrData.push({
                        CONDITION: row['CONDITION'],
                        STEP: row['STEP']
                    });
                }
            });
            setGridData(FAIL_GRID_ID, arrData);
            this.setState((state) => {
                return { ...state, gridLength: arrData.length };
            });
        }
    };
    
    handleUpdate = () => {
        this.fillGridData();
        let arrData = [];
        const GPass = TGEvent.getGridByID(PASS_GRID_ID);
        if (GPass) {
            const _rows = Object.values(GPass['Rows']).filter((item) => item.Kind == 'Data');
            _rows.some((row) => {
                    arrData.push({
                        CONDITION: row['CONDITION'],
                        STEP: row['STEP'],
                        CONFIGTYPE: 'PASS'
                    });
            });
        }
        const GFail = TGEvent.getGridByID(FAIL_GRID_ID);
        if (GFail) {
            const _rows = Object.values(GFail['Rows']).filter((item) => item.Kind == 'Data');
            _rows.some((row) => {
                    arrData.push({
                        CONDITION: row['CONDITION'],
                        STEP: row['STEP'],
                        CONFIGTYPE: 'FAIL'
                    });
            });
        }
        this.props.hanldeGetOptPRConfig(arrData);
        this.setState({ createDialogOpen: false });
        this.owpDialog.handleClose();
    };

    render() {
        return (
            <OwpDialog
                title="Opt.PR 설정"
                callbackFunction={this.createDialogOpen}
                dialogButton="설정"
                dialogActions={
                    <React.Fragment>
                        <div className="w-full center">
                            <OwpButton color="secondary" variant="contained" onClick={this.handleUpdate}>
                                등록
                            </OwpButton>
                            <OwpButton color="secondary" variant="outlined" onClick={this.createDialogClose}>
                                취소
                            </OwpButton>
                        </div>
                    </React.Fragment>
                }
                innerRef={(ref) => (this.owpDialog = ref)}
                width="600px"
                height="auto"
            >
                <React.Fragment>
                    <Formsy>
                        <div style={{width:'100%'}}>

                            <div className="row">
                                <div className="col-lg-12 pd0">
                                    
                                    <div className="portlet light mgb0" style={{paddingTop:'0'}}>
                                        <div className="portlet-title" style={{minHeight:'0'}}>
                                            <div className="caption">
                                                <Icon className="ico_m01 c04">insert_chart</Icon>
                                                <span className="font-dark bold uppercase"> Pass 설정 </span>
                                                <span className="caption-helper"> </span>
                                            </div>
                                            <div className="flex justify-end">
                                            </div>
                                        </div>
                                        <OwpTreeGrid
                                            id={PASS_GRID_ID}
                                            LayoutUrl={'/assets/data/C0202015Def.xml'}
                                            DataUrl={'/assets/data/C0202015Data.xml'}
                                            width="100%"
                                            height="20vh"
                                        />
                                        
                                    </div>
                                </div>
                            </div>

                            <div className="row">
                                <div className="col-lg-12 pd0">
                                    <div className="portlet light mgb0" style={{paddingTop:'0'}}>
                                        <div className="portlet-title" style={{minHeight:'0'}}>
                                            <div className="caption">
                                                <Icon className="ico_m01 c04">insert_chart</Icon>
                                                <span className="font-dark bold uppercase"> Fail 설정 </span>
                                                <span className="caption-helper"> </span>
                                            </div>
                                            <div className="flex justify-end">
                                                <OwpButton color="secondary" variant="contained" onClick={() => this.handleAddFailRow()}>
                                                    추가
                                                </OwpButton>
                                                <OwpButton color="secondary" variant="outlined" onClick={() => this.handleDeleteFailRow()}>
                                                    삭제
                                                </OwpButton>
                                            </div>
                                        </div>
                                        <OwpTreeGrid
                                            id={FAIL_GRID_ID}
                                            LayoutUrl={'/assets/data/C0202015Def.xml'}
                                            DataUrl={'/assets/data/C0202015Data.xml'}
                                            width="100%"
                                            height="20vh"
                                        />
                                    </div>
                                </div>
                            </div>
                            
                        </div>
                    </Formsy>
                </React.Fragment>
            </OwpDialog>
        );
    }
}

export default DialogReweighing;
