import React, { Component } from 'react';
import {
    OwpMessage,
    OwpSearchDateTimePickerMulti,
    OwpPageCarded,
    OwpSearchHeader,
    OwpSearchSelectField,
    OwpSearchTextField,
    OwpToolbar,
    OwpTreeGrid,
    OwpSearchAutoComplete,
} from 'owp/wrapper';
import * as TGEvent from 'owp/TGEvent';
import Pop_C404001 from '../../include/Pop_C404001';
import Pop_C404002 from '../../include/Pop_C404002';
import { restoreGridSelect, saveGridSelect } from 'main/GridUtill';
import Dialog_C404003 from '../../include/Dialog_C404003';
import Dialog_C404004 from '../../include/Dialog_C404004';
import Dialog_C404005 from '../../include/Dialog_C404005';
class Page extends Component {
    
    constructor(props) {
       
        super(props);
        this.Dialog_C404005 = React.createRef();
        this.state = {
            dialogData: ""
        };
       
    }
  
    componentDidMount() {
        
        TGEvent.OWPTGSelect("C404000", (is, row) => {
              saveGridSelect("C404000","OWP_FACILITIESHIS.FAC_HIS_SEQ")
          if (is) {
              this.setState({
                FAC_HIS_SEQ: row["OWP_FACILITIESHIS.FAC_HIS_SEQ"]
              });
              document.getElementById("hideButtonModify").style.display = "block"
              document.getElementById("hideButtonDelete").style.display = "block"
          } else {
              this.setState({
                FAC_HIS_SEQ: null
              });
              document.getElementById("hideButtonModify").style.display = "none"
              document.getElementById("hideButtonDelete").style.display = "none"
          }
        });  

        const _show = this.show;
        window.TGSetEvent("OnClick", "C404000", function(grid, row, col, val, bool, event) {
          event.preventDefault();
          if (col === "상세보기" && row.Kind === "Data") {
            _show(row);
          }
        });

      }
     
    show = (row) => {
        console.log("RowData",row);
        if (!Boolean(row["OWP_FACILITIESHIS.FAC_HIS_SEQ"])) {
          OwpMessage({
            message: "Data empty",
            variant: "warning",
          });
          return false;
        } else {
          this.setState({
            dialogData: row,
            });
            this.Dialog_C404005.openDialog_customer();
        }
      };  

    callback = () => {
        restoreGridSelect("C404000");
    }
    callbackHidenButton = () => {
        console.log("ccc")
        document.getElementById("hideButtonModify").style.display = "none"
        document.getElementById("hideButtonDelete").style.display = "none"
    }
    
    render() {
        return (
            <React.Fragment>
                <OwpPageCarded
                    header={
                        <OwpSearchHeader url="listOwpFacilitieshis" className="pd0">
                            <OwpSearchTextField
                                className="w-160"
                                id="OWP_FACILITIESHIS.FAC_HIS_ID"
                                name="OWP_FACILITIESHIS.FAC_HIS_ID"
                                label={'설비번호'}
                                
                            />
                        
                            <OwpSearchTextField
                                className="w-160"
                                id="OWP_FACILITIESHIS.FAC_HIS_NM"
                                name="OWP_FACILITIESHIS.FAC_HIS_NM"
                                label={'설비명'}
                                
                            />
                            <OwpSearchSelectField
                                className="w-160"
                                id="OWP_FACILITIESHIS.FAC_HIS_CLASSIFICATION"
                                name="OWP_FACILITIESHIS.FAC_HIS_CLASSIFICATION"
                                label="이력구분"
                                groupId ={"D404000"}
                                value={this.state.FAC_HIS_CLASSIFICATION}
                                useAll
                            />
                            <OwpSearchTextField
                                className="w-160"
                                id="OWP_FACILITIESHIS.FAC_HIS_CONTENTS"
                                name="OWP_FACILITIESHIS.FAC_HIS_CONTENTS"
                                label={'이력내역'}
                                
                            />

                            <OwpSearchDateTimePickerMulti
                                name="OWP_FACILITIESHIS.RDATE"
                                id = "OWP_FACILITIESHIS.RDATE"
                                initNow={true} // TODO: true 인 경우 현재 시로 init (* default = false)
                                labels={{ start: ' 등록시작일', end: '종료일' }}
                                inputProps={{ start: { style: { width: 140 } }, end: { style: { width: 140 } } }}
                                required={true}
                            />

                        
                        </OwpSearchHeader>
                    }
                    contentToolbar={
                        <OwpToolbar title="설비/장비 이력관리 리스트">
                            <Dialog_C404004/>
                            <Pop_C404001/>
                            <div id="hideButtonModify" style={{ display: "none" }}>
                                <Pop_C404002
                                    FAC_HIS_SEQ = {this.state.FAC_HIS_SEQ}
                                    callback = {this.callback}
                                />
                            </div>
                        
                            <div id="hideButtonDelete" style={{ display: "none" }}>
                                <Dialog_C404003
                                    FAC_HIS_SEQ = {this.state.FAC_HIS_SEQ}
                                    callbackHidenButton = {this.callbackHidenButton}
                                />
                            </div>
                        </OwpToolbar>
                    }
                    content={
                        
                        <React.Fragment>
                            <Dialog_C404005
                                dialogData={this.state.dialogData}
                                innerRef={(ref) => (this.Dialog_C404005 = ref)}
                            />
                            <OwpTreeGrid
                                id="C404000"
                                LayoutUrl={'/assets/data/C404000Def.xml'}
                                // DataUrl={'/assets/data/C0202000Data.xml'}
                            />
                        </React.Fragment>
                    }
                    innerScroll
                />
            </React.Fragment>
        );
    }
}

export default Page;
