import React, { Component } from 'react';
import {
    OwpButton,
    OwpSearchDateField,
    OwpSearchSelectField,
    OwpSearchTextField,
    OwpTextField,
    OwpTreeGrid,
    OwpMessage,
} from 'owp/wrapper';
import Formsy from 'formsy-react';
import * as TGEvent from 'owp/TGEvent';
import moment from 'moment';
import { query } from 'owp/api';
import { setGridData, setRowAttribute, restoreGridSelect, setRowAttributes } from 'main/GridUtill';

const MAX_ROW = 6;
const FACILITY = 'FACILITY';
const MATERIAL = 'MATERIAL';
class Page extends Component {
    constructor(props) {
        super(props);
        this.state = {
            gridLength: 0,
        };
    }
    /**
     * Owp에서 사용하는 인풋에 대해 모두 사용할 수 있는 change 이벤트 리시버
     * @param {Event} event 발생한 이벤트 객체
     * @param {Function} callback 콜백 함수. 인풋의 name, value를 인수로 전달함.
     */
    componentDidMount() {
        const _showApprovalDialog = this.showApprovalDialog;
        this.loadStdMainConfig();
    }
    //날짜 디폴드값 설정 함수
    //load master data and first rowData
    loadStdMainConfig = () => {
        let mock = `[{"FACILITY":"28","MATERIAL":"Three","CONFIG_TYPE":"Three","TOTAL_INPUT":"Three","INPUT_SPEED":"Three","MESASURING_EQUIPMENT":"Three"},{"FACILITY":"29","MATERIAL":"Three","CONFIG_TYPE":"Three","TOTAL_INPUT":"Three","INPUT_SPEED":"Three","MESASURING_EQUIPMENT":"Three"}]`;

        query({
            url: 'loadOwpMbrUnitProcessMainConfig/' + 139,
        }).then((res) => {
            if (res['OWP_MBR_UNIT_PROCESS.MAIN_CONFIG']) {
                let gridData = JSON.parse(mock);
                console.log(gridData);
                setGridData('C0202011', gridData);
                window.setTimeout(() => {
                    this.setGridDynamicColumnType();
                }, 100);
            }
        });
    };

    handleAddRow = () => {
        const G = TGEvent.getGridByID('C0202011');
        const _rows = Object.values(G['Rows']).filter((item) => item.Kind == 'Data');
        const checkLength = _rows.length;
        if (checkLength >= MAX_ROW) {
            OwpMessage({
                message: '6 미만',
                variant: 'error',
            });
        } else {
            G.ActionAcceptEdit();
            TGEvent.OWPTGAddRow('C0202011', (row) => {
                //TODO:do something here
            });
        }
    };

    handleUpdate = () => {
        const G = TGEvent.getGridByID('C0202011');
        //TODO:remove this
        this.setGridDynamicColumnType();
        this.fillGridData(G);
    };

    handleDeleteRow = () => {
        const G = TGEvent.getGridByID('C0202011');
        this.fillGridData(G, true);
    };

    setGridDynamicColumnType = () => {
        let gridId = 'C0202011';
        const G = TGEvent.getGridByID(gridId);
        const _rows = Object.values(G['Rows']).filter((item) => item.Kind == 'Data');
        const facilityColumn = 'FACILITY';
        const facilityEnum = this.loadFacilityEnum();
        _rows.forEach((row) => {
            setRowAttributes(gridId, row, facilityColumn, facilityEnum);
        });
    };

    fillGridData = (G, isDelete) => {
        if (G) {
            const _rows = Object.values(G['Rows']).filter((item) => item.Kind == 'Data');
            let arrData = [];
            _rows.some((row) => {
                if (isDelete && row['Selected'] === 1) {
                    return false;
                } else {
                    arrData.push({
                        FACILITY: row['FACILITY'],
                        MATERIAL: row['MATERIAL'],
                        CONFIG_TYPE: row['CONFIG_TYPE'],
                        TOTAL_INPUT: row['TOTAL_INPUT'],
                        INPUT_SPEED: row['INPUT_SPEED'],
                        MESASURING_EQUIPMENT: row['MESASURING_EQUIPMENT'],
                    });
                }
            });
            setGridData('C0202011', arrData);
            restoreGridSelect('C0202011');
            G.ReloadBody();
        }
    };

    loadFacilityEnum = () => {
        //TODO:load from server
        //TODO: write new service to get facility from parent of unit process id=
        let facilityDummy = [
            { 'OWP_FACILITIES.FACILITY_NAME': 'R-1117', 'OWP_FACILITIES.FACILITY_SEQ': '28' },
            { 'OWP_FACILITIES.FACILITY_NAME': 'AC-1105', 'OWP_FACILITIES.FACILITY_SEQ': '29' },
        ];

        let res = facilityDummy;
        if (res) {
            let enums = '';
            let enumKeys = '';
            res.forEach((item) => {
                enums = enums + '|' + item['OWP_FACILITIES.FACILITY_NAME'];
                enumKeys = enumKeys + '|' + item['OWP_FACILITIES.FACILITY_SEQ'];
            });

            return {
                Type: 'Enum',
                Enum: enums,
                EnumKeys: enumKeys,
            };
        }
        return null;
    };

    render() {
        console.log('tap 2');
        return (
            <React.Fragment>
                <div style={{ width: '100%' }}>
                    <div className="row">
                        <div className="col-md-6 b" style={{ padding: '13px 0 0 0' }}>
                            Std 대시보드 설정
                        </div>
                        <div className="col-md-6" style={{ textAlign: 'right', paddingRight: '0' }}>
                            <OwpButton
                                color={'secondary'}
                                variant={'contained'}
                                onClick={() => this.handleAddRow()}
                            >
                                추가
                            </OwpButton>
                            <OwpButton
                                color={'secondary'}
                                variant={'contained'}
                                onClick={() => this.handleUpdate()}
                            >
                                수정
                            </OwpButton>
                            <OwpButton
                                color={'secondary'}
                                variant={'outlined'}
                                onClick={() => this.handleDeleteRow()}
                            >
                                삭제
                            </OwpButton>
                        </div>
                        <div className="col-md-12" style={{ padding: '0' }}>
                            <OwpTreeGrid
                                id="C0202011"
                                LayoutUrl="assets/data/C0202011Def.xml"
                                // DataUrl={'/assets/data/C0202000Data.xml'}
                                width="100%"
                                // height="17vh"
                            />
                        </div>
                    </div>
                </div>
            </React.Fragment>
        );
    }
}

export default Page;
