import React, { Component } from "react";
import { OwpDialog, OwpButton, OwpSearchDateField, OwpSearchSelectField, OwpSearchTextField, OwpTextField, OwpTreeGrid, OwpCheckBox, OwpSelectField, OwpSession } from 'owp/wrapper';
import Formsy from "formsy-react";
import * as TGEvent from 'owp/TGEvent';
import moment from "moment";
import { mutate, query } from 'owp/api';
import Tabs from "@material-ui/core/Tabs"
import Tab from "@material-ui/core/Tab"
import { Typography, withStyles, Collapse } from "@material-ui/core"
import Pop_C0202002_02 from './Pop_C0202002_02';
import Pop_C0202002_03 from './Pop_C0202002_03';
import EquipAttrButtonItems from './EquipAttrButtonItems';
import { setGridData } from "main/GridUtill";
import { isEmpty } from "lodash";

class Page extends Component {
    constructor(props) {
        super(props);
        this.state = {
            MBRSEQ: this.props.MBRSEQ,
            productName: this.props.productName,
            functionName: this.props.functionName,
            groupNamesProcessTree: this.props.groupNamesProcessTree,
            stateDialogInDevelopment: false
        };
    }
    /**
     * Owp에서 사용하는 인풋에 대해 모두 사용할 수 있는 change 이벤트 리시버
     * @param {Event} event 발생한 이벤트 객체
     * @param {Function} callback 콜백 함수. 인풋의 name, value를 인수로 전달함.
     */
    componentDidMount() {
        const _showApprovalDialog = this.showApprovalDialog;
    }
    //날짜 디폴드값 설정 함수

    getListEChecklist = async (params) => {
        console.log(params);
        if (params.hasOwnProperty('OWP_MBR_UNIT_PROCESS.SUBPROCESS')) {
            console.log("ok");
            document.getElementById("ECHECKLIST_ADD").style.display = "inline-block";
            document.getElementById("ECHECKLIST_MODIFY").style.display = "inline-block";
            document.getElementById("ECHECKLIST_DELETE").style.display = "inline-block";
            document.getElementById("addEChecklist").style.display = "block";
            this.setState({ MAINPROCESS: params["OWP_MBR_UNIT_PROCESS.MAINPROCESS"], SUBPROCESS: params["OWP_MBR_UNIT_PROCESS.SUBPROCESS"] })
        } else {
            document.getElementById("ECHECKLIST_ADD").style.display = "none"
            document.getElementById("ECHECKLIST_MODIFY").style.display = "none"
            document.getElementById("ECHECKLIST_DELETE").style.display = "none"
            document.getElementById("addEChecklist").style.display = "none"
        }
        try {
            query({
                url: 'listOwpMbrEchecklist',
                params: {
                    jsondata: JSON.stringify(params),
                },
            }).then((res) => {
                setGridData('C0202013', res);
            });
        } catch (error) {
            console.error(error);
        }
    };
    openDialogInDevelopment = () => {
        this.setState({ stateDialogInDevelopment: true })
    }

    closeDialogInDevelopment = () => {
        this.setState({ stateDialogInDevelopment: false })
    }
    hanldeGetLinkConfig = (linkConfig) => {
        console.log(linkConfig);
        this.setState({ stateLinkConfig: false })
    }
    addEChecklist = async () => {
        const data = {
            cudtype: "CREATE",
            "OWP_MBR_ECHECKLIST.PRODUCTIONORDER": document.getElementById("PRODUCTIONORDER").value,
            "OWP_MBR_ECHECKLIST.PRODUCTIONORDERSEQ": document.getElementById("PRODUCTIONORDERSEQ").value,
            // "OWP_MBR_ECHECKLIST.NOTIFICATIONTYPE": document.getElementsByName("NOTIFICATIONTYPE"),
            "OWP_MBR_ECHECKLIST.RECORDTYPE": document.getElementById("RECORDTYPE").value,
            "OWP_MBR_ECHECKLIST.STANDARDUNIT": document.getElementById("STANDARDUNIT").value,
            "OWP_MBR_ECHECKLIST.RECORDSPEC": document.getElementById("RECORDSPEC").value,
            "OWP_MBR_ECHECKLIST.PROCEDURENAME": document.getElementById("PROCEDURENAME").value,
            "OWP_MBR_ECHECKLIST.WUSERSEQ": 3 + '',// : 로그인 사용자SEQ
            "OWP_MBR_ECHECKLIST.FLAG": 'Y'// : 로그인 사용자SEQ
            // "OWP_MBR_ECHECKLIST.WUSERSEQ": OwpSession("USERSEQ"),// : 로그인 사용자SEQ
        }
        const mdProcessData = {
            "OWP_MBR_MD_PROCESS.MBRSEQ": this.state.MBRSEQ + '',
            "OWP_MBR_MD_PROCESS.MAINPROCESS": this.state.MAINPROCESS,
            "OWP_MBR_MD_PROCESS.SUBPROCESS": this.state.SUBPROCESS,
        }
        console.log(data);
        const mdProcessSEQ = await query({
            url: "/listOwpMbrMdProcess",
            params: {
                jsondata: JSON.stringify(mdProcessData),
            },
        })
            .then(res => {
                console.log(res);
                if (!isEmpty(res)) {
                    data["OWP_MBR_ECHECKLIST.MDPROCESSSEQ"] = res[0]["OWP_MBR_MD_PROCESS.MDPROCESSSEQ"] + '';
                    const result = mutate({
                        url: "/createOwpMbrEchecklist",
                        data
                    })
                        .then(res => {
                            console.log("createOwpMbrEchecklist success ");
                            const G = TGEvent.getGridByID("C0202013");
                            G.ReloadBody();
                        })
                        .catch(error => {
                            console.error(error)
                        })
                }

            })
            .catch(error => {
                console.error(error)
            })

    }
    render() {
        console.log(this.state.groupNamesProcessTree);
        return (
            <React.Fragment>
                <OwpDialog // 저장버튼을 누르면 나오는 다이얼로그.
                    onOpen={this.state.stateDialogInDevelopment}
                    dialogActions={
                        <React.Fragment>
                            <OwpButton color="secondary" variant="contained" onClick={this.closeDialogInDevelopment}>OK</OwpButton>
                        </React.Fragment>
                    }
                    width="211px"
                    height="100%"
                >
                    <React.Fragment>{"In Development"}</React.Fragment>
                </OwpDialog>
                <div style={{ width: '100%', marginTop: '12px' }}>
                    <div className="row">
                        <div className="col-md-2">
                            <Collapse unmountOnExit in={true}>
                                <EquipAttrButtonItems
                                    data={this.state.groupNamesProcessTree}
                                    handleChangeVisibleState={
                                        this.getListEChecklist
                                    }
                                    MBRSEQ={this.state.MBRSEQ}
                                    functionName={this.state.functionName}
                                />
                            </Collapse>
                        </div>
                        <div className="col-md-10" style={{ paddingRight: '0' }}>
                            <div className="row">
                                <div className="col-md-6 b" style={{ paddingTop: '13px' }}>
                                    공정 및 하위 작업(세부)
                                </div>
                                <div className="col-md-6" style={{ textAlign: 'right' }}>
                                    <div style={{ display: "inline-block" }}>
                                        <OwpButton color={'secondary'} variant={'outlined'} onClick={this.openDialogInDevelopment}>
                                            Paper 보기
                                            </OwpButton>
                                    </div>
                                    <div style={{ display: "inline-block" }}>
                                        <OwpButton color={'secondary'} variant={'outlined'} onClick={this.openDialogInDevelopment}>
                                            POP 화면 보기
                                            </OwpButton>
                                    </div>
                                    <div id="ECHECKLIST_ADD" style={{ display: "none" }}>
                                        <OwpButton color={'secondary'} variant={'outlined'} onClick={this.addEChecklist}>Add</OwpButton>
                                    </div>
                                    <div id="ECHECKLIST_MODIFY" style={{ display: "none" }}>
                                        <OwpButton color={'secondary'} variant={'outlined'}>Modify</OwpButton>
                                    </div>
                                    <div id="ECHECKLIST_DELETE" style={{ display: "none" }}>
                                        <OwpButton color={'secondary'} variant={'outlined'}>Delete</OwpButton>
                                    </div>
                                </div>
                                <div className="col-md-12">
                                    <OwpTreeGrid
                                        id="C0202013"
                                        LayoutUrl="assets/data/C0202013Def.xml"
                                        DataUrl={'/assets/data/C0202013Data.xml'}
                                        width="100%"
                                        height="24vh"
                                    />
                                </div>
                                <div id="addEChecklist" className="col-md-12" style={{ marginTop: '-30px', display: "none"  }}>
                                    <div style={{ width: '100%' }}>
                                        <table className="table table-bordered table-advance tlw5">
                                            <tbody>
                                                <tr>
                                                    <th className="left" width="150">Production Order</th>
                                                    <td className="left" width="200">
                                                        <div>
                                                            <OwpTextField
                                                                style={{ width: '100%' }}
                                                                id="PRODUCTIONORDER"
                                                                name="PRODUCTIONORDER"
                                                                value={this.state['OWP_MBR_ECHECKLIST.PRODUCTIONORDER']}
                                                            />
                                                        </div>
                                                    </td>
                                                    <th className="left" width="150">Sequence</th>
                                                    <td className="left" width="200">
                                                        <div>
                                                            <OwpTextField
                                                                style={{ width: '100%' }}
                                                                id="PRODUCTIONORDERSEQ"
                                                                name="PRODUCTIONORDERSEQ"
                                                                value={this.state['OWP_MBR_ECHECKLIST.PRODUCTIONORDER']}
                                                            />
                                                        </div>
                                                    </td>

                                                    <td className="left" colSpan="4">
                                                        <div style={{ margin: '-20px 0' }}>
                                                            <OwpCheckBox
                                                                required
                                                                options={[
                                                                    { label: '알람 메시지', value: 'alarm_message' },
                                                                    { label: '알람 이메일', value: 'alarm_email' },
                                                                    { label: 'ERP 실적등록', value: 'erp' },
                                                                    { label: 'Rolling bar', value: 'rolling_bar' },
                                                                ]}
                                                                id="NOTIFICATIONTYPE"
                                                                name="NOTIFICATIONTYPE"
                                                                defaultValue={['alarm_message', 'alarm_email']}
                                                            />
                                                        </div>

                                                    </td>
                                                </tr>
                                                <tr>
                                                    <th className="left">Record Type</th>
                                                    <td className="left" colSpan="3">
                                                        <OwpSelectField
                                                            className="w-full"
                                                            id="b"
                                                            name="b"
                                                            options={[
                                                                { label: 'Check box', value: 'Check box' },
                                                                { label: '문서입력', value: '문서입력' },
                                                                { label: '수치입력', value: '수치입력' },
                                                                { label: 'Std 대시보드', value: 'Std 대시보드' },
                                                                { label: '메인 대시보드', value: '메인 대시보드5' },
                                                                { label: 'Opt.PR', value: 'Opt.PR' },
                                                                { label: '병렬', value: '병렬' },
                                                                { label: 'Link 설정', value: 'Link 설정' },
                                                            ]}
                                                            value={'1'}
                                                            id="RECORDTYPE"
                                                            name="RECORDTYPE"
                                                        />
                                                    </td>
                                                    <th className="left" width="80">Standard Unit</th>
                                                    <td className="left">
                                                        <OwpSelectField
                                                            className="w-full"
                                                            id="b"
                                                            name="b"
                                                            options={[
                                                                { label: 'kg', value: 'kg' },
                                                                { label: 'mg', value: 'mg' },
                                                                { label: '℃', value: '℃' },
                                                            ]}
                                                            value={'1'}
                                                            id="STANDARDUNIT"
                                                            name="STANDARDUNIT"
                                                        />
                                                    </td>
                                                    <th className="left">Link</th>
                                                    <td className="left">
                                                        <Pop_C0202002_02 hanldeGetLinkConfig={this.hanldeGetLinkConfig}/>
                                                    </td>
                                                </tr>
                                                <tr>
                                                    <th className="left" width="80">Record Spec</th>
                                                    <td className="left" colSpan="3">
                                                        <OwpSelectField
                                                            className="w-full"
                                                            id="b"
                                                            name="b"
                                                            options={[
                                                                { label: 'Standard Dashboard 기준 값', value: 'Standard Dashboard 기준 값' },
                                                                { label: 'Main Dashboard 기준 값', value: 'Main Dashboard 기준 값' },
                                                                { label: '병렬 스탭번호', value: '병렬 스탭번호' },
                                                            ]}
                                                            value={'1'}
                                                            id="RECORDSPEC"
                                                            name="RECORDSPEC"
                                                        />
                                                    </td>
                                                    <th className="left" width="80">Opt,PR</th>
                                                    <td className="left" colSpan="3">
                                                        <Pop_C0202002_03 />
                                                    </td>
                                                </tr>
                                                <tr>
                                                    <th className="left">Procedure Name</th>
                                                    <td className="left" colSpan="7">
                                                        <textarea
                                                            className="t_textarea"
                                                            style={{ maxHeight: '200px', maxWidth: '100%' }}
                                                            id="PROCEDURENAME"
                                                            name="PROCEDURENAME"
                                                        />
                                                    </td>
                                                </tr>
                                            </tbody>
                                        </table>
                                    </div>
                                </div>
                            </div>

                        </div>
                    </div>
                </div>
            </React.Fragment>
        );
    }
}

export default Page;
