import {
    OwpTextField,
    OwpButton,
    OwpSelectField,
    OwpDialog,
    OwpMessage,
    OwpTreeGrid,
    OwpSearchDateTimePickerMulti,
    OwpSearchSelectField,
    OwpSearchTextField,
    OwpDateTimePickerMulti,
} from 'owp/wrapper';
import Icon from '@material-ui/core/Icon';
import TextField from '@material-ui/core/TextField';
import InputAdornment from '@material-ui/core/InputAdornment';
import React, { Component } from 'react';
import Formsy from 'formsy-react';
import { mutate } from 'owp/api';
import * as TGEvent from 'owp/TGEvent';
import { setGridData, setRowAttribute, restoreGridSelect, setRowAttributes } from 'main/GridUtill';


const GRID_ID = 'C0202015';
class DialogReweighing extends Component {
    constructor(props) {
        super(props);
        this.owpDialog = React.createRef();
        this.clickCreate = this.clickCreate.bind(this);
        this.onSave = this.onSave.bind(this);
        this.state = {
            FLAG: 'Y',
            gridLength: 0,
        };
    }

    handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
    };

    

    // 등록 화면 닫기
    createDialogClose = () => {
        this.setState({ createDialogOpen: false });
        this.owpDialog.handleClose();
    };

    // 등록 확인 화면 열기
    createCheckOpen = () => {

        this.setState({ createCheckOpen: true });
        this.setState({ popMessage: '저장하시겠습니까?' });
    };

    // 등록 확인 화면 닫기
    createCheckClose = () => {
        this.setState({ createCheckOpen: false });
    };

    onSave = async (type) => {
        const data = {
            cudtype: 'create',
            
        };

        const result = await mutate({
            url: '/createIpxCommoncode',
            data,
        })
            
    };

    clickCreate() {
        this.onSave('START');
    }

    handleDeleteRow = () => {
        this.fillGridData(true);
    };

    handleAddRow = () => {
        console.log("kec");
            const G = TGEvent.getGridByID(GRID_ID);
            G.ActionAcceptEdit();
            TGEvent.OWPTGAddRow(GRID_ID, (row) => {
                this.setState((state) => {
                    return {
                        ...state,
                        gridLength: state.gridLength + 1,
                    };
                });
            });
    };
    fillGridData = (isDelete) => {
        const G = TGEvent.getGridByID(GRID_ID);
        if (G) {
            const _rows = Object.values(G['Rows']).filter((item) => item.Kind == 'Data');
            let arrData = [];
            _rows.some((row) => {
                if (isDelete && row['Selected'] === 1) {
                    return false;
                } else {
                    arrData.push({
                        CONDITION: row['CONDITION'],
                        STEP: row['STEP']
                    });
                }
            });
            setGridData(GRID_ID, arrData);
            this.setState((state) => {
                return { ...state, gridLength: arrData.length };
            });
        }
    };
    
    handleUpdate = () => {
        this.fillGridData();
        const G = TGEvent.getGridByID(GRID_ID);
        if (G) {
            const _rows = Object.values(G['Rows']).filter((item) => item.Kind == 'Data');
            let arrData = [];
            _rows.some((row) => {
                    arrData.push({
                        CONDITION: row['CONDITION'],
                        STEP: row['STEP']
                    });
            });
            this.props.hanldeGetLinkConfig(arrData);
        }
        this.setState({ createDialogOpen: false });
        this.owpDialog.handleClose();
    };

    render() {
        return (
            <OwpDialog
                title="Link Config"
                callbackFunction={this.createDialogOpen}
                dialogButton="설정"
                dialogActions={
                    <React.Fragment>
                        <div className="w-full center">
                            <OwpButton color="secondary" variant="contained" onClick={this.handleUpdate}>
                                등록
                            </OwpButton>
                            <OwpButton color="secondary" variant="outlined" onClick={this.createDialogClose}>
                                취소
                            </OwpButton>
                        </div>
                    </React.Fragment>
                }
                innerRef={(ref) => (this.owpDialog = ref)}
                width="600px"
                height="auto"
            >
                <React.Fragment>
                    <Formsy>
                        <div style={{width:'100%'}}>
                            <div className="row">
                                <div className="col-lg-12 pd0">
                                    <div className="portlet light mgb0" style={{paddingTop:'0'}}>
                                        <div className="portlet-title" style={{minHeight:'0'}}>
                                            <div className="flex justify-end">
                                                <OwpButton color="secondary" variant="contained" onClick={() => this.handleAddRow()}>
                                                    추가
                                                </OwpButton>
                                                <OwpButton color="secondary" variant="outlined" onClick={() => this.handleDeleteRow()}>
                                                    삭제
                                                </OwpButton>
                                            </div>
                                        </div>
                                        <OwpTreeGrid
                                            id="C0202015"
                                            LayoutUrl={'/assets/data/C0202015Def.xml'}
                                            DataUrl={'/assets/data/C0202015Data.xml'}
                                            width="100%"
                                            height="20vh"
                                        />
                                    </div>
                                </div>
                            </div>
                            
                        </div>
                    </Formsy>
                </React.Fragment>
            </OwpDialog>
        );
    }
}

export default DialogReweighing;
