import { Collapse, Icon, IconButton, ListItem, ListItemText } from '@material-ui/core';
import { withStyles } from '@material-ui/core/styles/index';
import classNames from 'classnames';
import PropTypes from 'prop-types';
import React, { Component } from 'react';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import FuseNavBadge from './../FuseNavBadge';
import FuseNavVerticalGroup from './FuseNavVerticalGroup';
import FuseNavVerticalItem from './FuseNavVerticalItem';

const propTypes = {
    item: PropTypes.shape({
        id: PropTypes.string.isRequired,
        title: PropTypes.string,
        icon: PropTypes.string,
        children: PropTypes.array,
    }),
};

const defaultProps = {};

const styles = (theme) => ({
    root: {
        padding: 0,
        '&.open': {
            backgroundColor: 'rgba(0,0,0,.08)',
        },
    },
    item: {
        height: 40,
        width: 'calc(100% - 16px)',
        borderRadius: '0 20px 20px 0',
        paddingRight: 12,
        '&.square': {
            width: '100%',
            borderRadius: '0',
        },
    },
});

function needsToBeOpened(props) {
    return props.location && isUrlInChildren(props.item, props.location.pathname);
}

function isUrlInChildren(parent, url) {
    if (!parent.children) {
        return false;
    }

    for (let i = 0; i < parent.children.length; i++) {
        if (parent.children[i].children) {
            if (isUrlInChildren(parent.children[i], url)) {
                return true;
            }
        }

        if (parent.children[i].url === url || url.includes(parent.children[i].url)) {
            return true;
        }
    }

    return false;
}

class FuseNavVerticalCollapse extends Component {
    constructor(props) {
        super(props);

        this.state = { open: needsToBeOpened(this.props) };
    }

    componentDidUpdate(prevProps, prevState, snapshot) {
        if (needsToBeOpened(this.props)) {
            !prevState.open && this.setState({ open: true });
        } else {
            prevState.open && this.setState({ open: false });
        }
    }

    handleClick = () => {
        this.setState({ open: !this.state.open });
    };

    render() {
        const { item, nestedLevel, classes, userRole, active } = this.props;
        if (
            item.auth &&
            (!item.auth.includes(userRole) ||
                (userRole !== 'guest' && item.auth.length === 1 && item.auth.includes('guest')))
        ) {
            return null;
        }
        let paddingValue = 40 + nestedLevel * 16;
        const listItemPadding =
            nestedLevel > 0 ? 'pl-' + (paddingValue > 80 ? 80 : paddingValue) : 'pl-24';
        return (
            <ul className={classNames(classes.root, this.state.open && 'open')}>
                <ListItem
                    button
                    className={classNames(classes.item, listItemPadding, 'list-item', active)}
                    onClick={this.handleClick}
                >
                    {!item.iconUrl && item.icon && (
                        <Icon color="action" className="text-16 flex-no-shrink">
                            {item.icon}
                        </Icon>
                    )}
                    {item.iconUrl && (
                        <img
                            src={item.iconUrl}
                            alt=""
                            style={{ color: '#0000008A', width: 16, height: 16 }}
                            className="text-16 flex-no-shrink"
                        />
                    )}
                    <ListItemText
                        className="list-item-text"
                        primary={item.title}
                        classes={{ primary: 'text-14' }}
                    />
                    {item.badge && <FuseNavBadge className="mr-4" badge={item.badge} />}
                    <IconButton disableRipple className="w-16 h-16 p-0">
                        <Icon className="text-16 arrow-icon" color="inherit">
                            {this.state.open ? 'expand_less' : 'expand_more'}
                        </Icon>
                    </IconButton>
                </ListItem>

                {item.children && (
                    <Collapse in={this.state.open} className="collapse-children">
                        {item.children.map((item, index) => (
                            <React.Fragment key={`${item.id}-${index}`}>
                                {item.type === 'group' && (
                                    <FuseNavVerticalGroup
                                        item={item}
                                        nestedLevel={nestedLevel + 1}
                                        active={active}
                                    />
                                )}

                                {item.type === 'collapse' && (
                                    <NavVerticalCollapse
                                        item={item}
                                        nestedLevel={nestedLevel + 1}
                                        active={active}
                                    />
                                )}

                                {item.type === 'item' && (
                                    <FuseNavVerticalItem
                                        item={item}
                                        nestedLevel={nestedLevel + 1}
                                        active={active}
                                    />
                                )}
                            </React.Fragment>
                        ))}
                    </Collapse>
                )}
            </ul>
        );
    }
}

function mapStateToProps({ auth }) {
    return {
        userRole: auth.user.role,
    };
}

FuseNavVerticalCollapse.propTypes = propTypes;
FuseNavVerticalCollapse.defaultProps = defaultProps;

const NavVerticalCollapse = withStyles(styles, { withTheme: true })(
    withRouter(connect(mapStateToProps)(FuseNavVerticalCollapse))
);

export default NavVerticalCollapse;
